    --gif-in-TG-top-offset: 31px;       /* GIFåœ¨æ¸¸æˆæœºå†…çš„ä¸Šä¸‹åç§» */
    --gif-in-TG-left-offset: 98px;      /* GIFåœ¨æ¸¸æˆæœºå†…çš„å·¦å³åç§» */
}

/* --- 4.2 æ‹“éº»æ­Œå­æ ¸å¿ƒä»£ç  --- */
.mes:has(.mesAvatarWrapper)::after {
    content: '';
