    --player-id-left: 107px;           /* â€œç©å®¶IDâ€å·¦å³ä½ç½® */
    --mood-value-top: 143px;           /* â€œå¿ƒæƒ…å€¼â€ä¸Šä¸‹ä½ç½® */
