    --game-icon-char: 'â™¥';            /* å·¦ä¾§çš„æµ®åŠ¨å›¾æ ‡ */
    --game-icon-color: #5E97D1;       /* å›¾æ ‡çš„é¢œè‰² */
    --game-icon-glow-color: #FFFFFF;  /* å›¾æ ‡çš„å…‰æ™•é¢œè‰² */
}

/* --- 7.2 æ ‡é¢˜æ æ ¸å¿ƒä»£ç  --- */
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
    position: relative;
    z-index: 1;
    background-color: var(--game-bg-color) !important;
    color: var(--game-text-color) !important;
    border: 2px solid var(--game-border-color) !important;
    box-shadow: 0 4px 10px var(--game-shadow-color) !important;
    border-radius: 8px !important;
    padding: 10px 20px 10px 40px !important; 
