    position: absolute;
    left: 15px;
    top: 50%;
    font-size: 1.1em;
    color: var(--game-icon-color);
    text-shadow: 0 0 5px var(--game-icon-glow-color);
    animation: float 2.5s ease-in-out infinite;
}
.inline-drawer-header::after,
#extensions_settings .inline-drawer-toggle.inline-drawer-header::after,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header::after,
#user-settings-block h4::after,
