@import url("https://fontsapi.zeoseven.com/161/main/result.css");

body {
    font-family: "Sarasa Gothic SC";
    font-weight: normal;
}

/* 自定义顶栏文字 */
#sheld::before {
  content: 'Opus 4.5' !important; /* 改这里 */
