}

:root {
  --背景色: #FAF9F5;
  --用户气泡: #F0EEE6;
  --文字色: #141413;
  --思考框背景: #F5F3ED;
  --思考框边框: #D4A574;
  --输入框背景: #FFFFFF;
  --输入框边框: #E8E6E0;
  --侧边栏背景: #FAF9F5;
  --控件背景: #F0EEE6;
  --强调色: #C96342;
  --侧边栏文字: #7D7C76;
  --描边色: #D0D0D0;
}

body,
#sheld {
  background-color: var(--背景色) !important;
}

/* =============== 聊天区域 =============== */

.mes {
  background: transparent !important;
  box-shadow: none !important;
  border: none !important;
}

.mes[is_user="true"] .mes_block {
  background: var(--用户气泡) !important;
  color: var(--文字色) !important;
  border-radius: 24px 24px 8px 24px !important;
  padding: 14px 16px !important;
  width: fit-content !important;
  max-width: 85% !important;
  margin-left: auto !important;
  margin-right: 4px !important;
  display: flex !important;
  align-items: center !important;
}

.mes[is_user="true"] .mes_block .mes_text {
  padding: 0 !important;
  margin: 0 !important;
}

.mes[is_user="true"] .mes_text p {
  margin: 0 !important;
  padding: 0 !important;
}

.mes[is_user="false"] .mes_block {
  background: transparent !important;
  color: var(--文字色) !important;
  padding: 8px 0px !important;
}

.mes[is_user="false"] .mes_text {
  padding-left: 6px !important;
  padding-right: 6px !important;
}

.mes {
  margin-bottom: 30px !important;
}

.mes[is_user="false"] {
  margin-top: -5px !important;
  padding-top: 0 !important;
}

.mes[is_user="false"] .mes_block {
  margin-top: 0 !important;
  padding-top: 0 !important;
}

.mes[is_user="true"] {
  margin-bottom: -10px !important;
  padding-bottom: 0 !important;
}

.mes .ch_name .name_text,
.mes .ch_name .timestamp {
  font-size: 0 !important;
  color: transparent !important;
}

.mes .mesAvatarWrapper {
  position: static !important;
  width: 0 !important;
  height: 0 !important;
  overflow: visible !important;
}

.mes .mesAvatarWrapper .avatar {
  display: none !important;
}

.mes[is_user="true"] .mesIDDisplay,
.mes[is_user="true"] .mes_timer,
.mes[is_user="true"] .tokenCounterDisplay {
  display: none !important;
}

.mes[is_user="false"] .mesIDDisplay,
.mes[is_user="false"] .mes_timer,
.mes[is_user="false"] .tokenCounterDisplay {
  position: absolute !important;
  z-index: 20 !important;
  background: transparent !important;
  border: none !important;
  padding: 0 !important;
  font-size: 0.65rem !important;
  color: #807E78 !important;
  opacity: 0;
  transition: all 0.3s ease;
  box-shadow: none !important;
  bottom: -16px !important;
}

.mes[is_user="false"] .mesIDDisplay {
  right: 20px !important;
  left: auto !important;
}

.mes[is_user="false"] .tokenCounterDisplay {
  right: 70px !important;
  left: auto !important;
}

.mes[is_user="false"] .mes_timer {
  right: 120px !important;
  left: auto !important;
}

.mes[is_user="false"]:hover .mesIDDisplay,
.mes[is_user="false"]:hover .mes_timer,
.mes[is_user="false"]:hover .tokenCounterDisplay {
