  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  background-image: url('https://img.heliar.top/file/1769163122006_user1.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

.mes[is_user="true"] .mes_button.mes_edit {
  position: relative !important;
  width: 18px !important;
  height: 18px !important;
}

.mes[is_user="true"] .mes_button.mes_edit i,
.mes[is_user="true"] .mes_button.mes_edit::before {
  display: none !important;
}

.mes[is_user="true"] .mes_button.mes_edit::after {
  content: '' !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  background-image: url('https://img.heliar.top/file/1769165188505_user2.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

.mes[is_user="false"] .mes_button.extraMesButtonsHint {
  position: relative !important;
  width: 18px !important;
  height: 18px !important;
}

.mes[is_user="false"] .mes_button.extraMesButtonsHint i,
.mes[is_user="false"] .mes_button.extraMesButtonsHint::before {
  display: none !important;
}

.mes[is_user="false"] .mes_button.extraMesButtonsHint::after {
  content: '' !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  background-image: url('https://img.heliar.top/file/1769167018026_AI1.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

.mes[is_user="false"] .mes_button.mes_edit {
  position: relative !important;
  width: 18px !important;
  height: 18px !important;
}

.mes[is_user="false"] .mes_button.mes_edit i,
.mes[is_user="false"] .mes_button.mes_edit::before {
  display: none !important;
}

.mes[is_user="false"] .mes_button.mes_edit::after {
  content: '' !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  background-image: url('https://img.heliar.top/file/1769170026455_AI2.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

.mes_button.extraMesButtonsHint {
  filter: none !important;
  -webkit-filter: none !important;
  box-shadow: none !important;
  -webkit-box-shadow: none !important;
  text-shadow: none !important;
}

.mes_button.extraMesButtonsHint i,
.mes_button.extraMesButtonsHint svg {
  filter: none !important;
  -webkit-filter: none !important;
  text-shadow: none !important;
}

.mes_button.extraMesButtonsHint {
  opacity: 1 !important;
  color: #666 !important;
}

.mes_button.extraMesButtonsHint i,
.mes_button.extraMesButtonsHint svg {
  opacity: 1 !important;
