}

.mes_button.mes_edit {
  filter: none !important;
  -webkit-filter: none !important;
  box-shadow: none !important;
  -webkit-box-shadow: none !important;
  text-shadow: none !important;
}

.mes_button.mes_edit i,
.mes_button.mes_edit {
  filter: none !important;
  -webkit-filter: none !important;
  text-shadow: none !important;
}

.mes_button.mes_edit {
  opacity: 1 !important;
  color: #666 !important;
}

.mes_button.mes_edit i,
.mes_button.mes_edit svg {
  opacity: 1 !important;
  color: #666 !important;
}

.mes[is_user="true"] .mes_edit_buttons {
  position: absolute !important;
  bottom: -40px !important;
  right: 4px !important;
  left: auto !important;
  width: auto !important;
  background: #FFFFFF !important;
  border: 1px solid #E8E6E0 !important;
  border-radius: 12px !important;
  padding: 8px 12px !important;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06) !important;
  z-index: 100 !important;
}

.mes[is_user="true"] .mes_block:has(.mes_edit_buttons[style*="display: flex"]),
.mes[is_user="true"] .mes_block:has(.mes_edit_buttons:not([style*="none"])):has(textarea) {
