    background-color: transparent !important;
    border: none !important;
    border-radius: 0 !important;
    filter: none !important;
    animation: logoSpin 1.5s ease-in-out infinite;
}

#load-spinner::before {
    display: none !important;
}

@keyframes logoSpin {
    0% { transform: translate(-50%, -50%) rotate(0deg); }
    100% { transform: translate(-50%, -50%) rotate(360deg); }
}

.popup-content:has(#load-spinner),
.sliding-popup:has(#load-spinner),
.modal-content:has(#load-spinner),
.dialog-content:has(#load-spinner),
[class*="popup"]:has(#load-spinner),
[class*="modal"]:has(#load-spinner),
[class*="dialog"]:has(#load-spinner) {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
    backdrop-filter: none !important;
    width: auto !important;
    height: auto !important;
    padding: 0 !important;
}

* {
  text-shadow: none !important;
  box-shadow: none !important;
  filter: none !important;
}

textarea,
textarea.text_pole,
textarea.textarea_compact,
textarea[id*="instruct"],
textarea[id*="prefix"],
textarea[id*="suffix"] {
  background: var(--控件背景) !important;
  background-color: #F0EEE6 !important;
  color: var(--侧边栏文字) !important;
  color: #7D7C76 !important;
  border: 1px solid rgba(240, 238, 230, 0.8) !important;
}

#show_more_messages {
  background: #FAF9F5 !important;
  border: none !important;
  border-radius: 0 !important;
  padding: 15px 0 !important;
  margin: 0 !important;
  width: 100vw !important;
  margin-left: calc(-50vw + 50%) !important;
