custom_css:
#sheld {
  background-image: url('https://image.uglycat.cc/4rf95b.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
}

@font-face {
  font-family: 'Font';
  src: url('https://files.catbox.moe/o8iey3.ttc') format('truetype');
  font-display: swap;
}

* {
  box-shadow: none !important;
  text-shadow: none !important;
  filter: none !important;
}

:not([class*="fa-"]):not(.fas):not(.far):not(.fal):not(.fab):not(.fa),
#send_textarea,
.menu_button_icon.fa-repeat,
.menu_button_icon.fa-bolt {
  font-family: 'Font', system-ui;
  color: #3A5F3A !important;
}

:root {
  --border-color: #C1E1C1;
  --background-color: #E8F5E8;
  --text-color: #3A5F3A;
  --accent-color: #5D9C5D;
  --light-accent: #7AB07A;
  --inline-code-bg: #D4EDDA;
  --inline-code-border: #A3D9A5;
}

::-webkit-scrollbar {
  display: none;
}

::selection {
  background-color: var(--background-color);
  color: #2C4A2C !important;
  text-decoration: underline;
  text-decoration-color: var(--accent-color);
  text-decoration-style: dashed;
  text-decoration-thickness: 1.2px;
  text-underline-offset: 2px;
}

select {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  background-image: none !important;
}

select::-ms-expand {
  display: none;
}

input[type="checkbox"] {
  background-color: var(--SmartThemeChatTintColor);
  border: 1px solid var(--border-color);
}

input[type="checkbox"]:checked {
  background-color: var(--accent-color) !important;
  border: none !important;
  outline: none !important;
}

input[type="checkbox"]:checked::before {
  transform: scale(0.9);
}

input[type="range"] {
  background: transparent;
}

input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  width: 13px;
  height: 13px;
  border-color: var(--border-color);
  transform: scaleY(2.5);
  outline: none;
}

input[type="range"]::-webkit-slider-runnable-track {
  background: color-mix(in srgb, var(--accent-color) 30%, white) !important;
  border-radius: 6px 6px 0 0;
  transform: scaleY(0.4);
  border: none !important;
}

input:not([type="checkbox"]):not([type="radio"]):not([type="range"]):not([type="search"]) {
  background-color: var(--white100) !important;
  border-radius: 4px !important;
  box-shadow: 0 0.5px 1px var(--background-color) !important;
  color: var(--text-color) !important;
}

input[type="radio"]:checked {
  accent-color: var(--accent-color);
}

select {
  background-color: color-mix(in srgb, var(--background-color) 60%, white) !important;
  padding: 4px 6px !important;
  box-shadow: 0 0 2px var(--border-color) !important;
  border: none !important;
  color: var(--text-color) !important;
}

#top-settings-holder {
  border-bottom: 2px dashed var(--border-color);
  position: relative;
}

#top-settings-holder::before {
  content: "ğŸŒ¿";
  position: absolute;
  bottom: -18px;
  left: 0px;
  color: var(--light-accent);
  font-size: 24px;
}

#top-settings-holder::after {
  content: "ğŸƒ";
  position: absolute;
  bottom: -18px;
  right: -10px;
  color: var(--light-accent);
  font-size: 24px;
}

#top-settings-holder::before,
#top-settings-holder::after {
  pointer-events: none;
}

#chat {
  align-items: center;
  border: none;
}

#chat::-webkit-scrollbar {
  display: block;
  width: 6px;
}

#chat::-webkit-scrollbar-thumb {
  border: none;
  box-shadow: none;
  background-color: var(--accent-color);
}

#left-nav-panel {
  padding-right: 0;
}

#nonQRFormItems {
  position: relative !important;
}

#nonQRFormItems::after {
  content: "ğŸŒ± Please enter text...";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 10px;
  font-style: italic;
  pointer-events: none;
  white-space: nowrap;
  color: var(--light-accent) !important;
}

#send_textarea::placeholder {
  color: transparent;
  opacity: 0;
}

#send_textarea:not(:placeholder-shown) ~ * #nonQRFormItems::after,
#nonQRFormItems:has(#send_textarea:not(:placeholder-shown))::after {
  display: none;
}

#nonQRFormItems .fa-solid {
  transform: scale(0.7);
  color: var(--accent-color) !important;
}

input[type="search"],
#script-search-input.text_pole {
  background-color: var(--background-color);
  color: var(--text-color) !important;
  border-radius: 12px !important;
  box-shadow: 0 0 1px 1px var(--background-color) !important;
