    src: url("https://ziti.zeabur.app/fonts/%E8%80%81%E4%BA%AC%E5%8D%8E%E5%AE%8B%E4%BD%93.ttf") format("truetype");
}

body,
#nonQRFormItems textarea,
.mes_text pre code,
.mes_reasoning pre code,
input,
select,
button,
.options-content,
.options-content a,
.options-content span,
#extensionsMenu,
#extensionsMenu span {
    font-family: "CustomFont", -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
