}


/* â•å…¨å±€åŸºç¡€â• */

body {
    background-color: #f5f5f5 !important;
}

#sheld {
    background: linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(232,232,232,0.9) 50%, rgba(240,240,240,0.9) 100%) !important;
}

* {
    scrollbar-width: thin;
    scrollbar-color: rgba(0, 0, 0, 0.2) rgba(0, 0, 0, 0.03);
    box-shadow: none !important;
    text-shadow: none !important;
}


/* â•æ»šåŠ¨æ¡â• */

::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}

::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.03);
}

::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, 0.35);
}


/* â•æ¶ˆæ¯æ°”æ³¡â• */

body.bubblechat .mes,
body.bubblechat .mes[is_user="true"],
.mes {
    padding: 18px 18px 35px;
    border-radius: 18px;
    border: 0px solid rgba(0, 0, 0, 0.08) !important;
    max-width: 380px;
    margin: 210px auto 20px;
    position: relative;
    background: rgba(255, 255, 255, 0.95);
}

.mes:not([is_user="true"]).last_mes {
    padding-bottom: 45px !important;
}

.mes::after {
    content: "â”€â”€â”€â”€â”€â”€â”€â”€â”€ âœ¦ â”€â”€â”€â”€â”€â”€â”€â”€â”€";
    position: absolute;
    bottom: 8px;
    left: 50%;
    transform: translateX(-50%);
    font-size: 0.7em;
    color: rgba(100, 100, 100, 0.5);
    z-index: 1;
}

.mes_block {
    padding-left: 0;
    transform: none !important;
}

/* å¤´åƒå®¹å™¨ */
.mes .mesAvatarWrapper {
    width: 380px;
    height: 240px;
    position: absolute;
    left: 50%;
    transform: translateX(-50%) !important;
    top: -190px;
    border-radius: 19px 19px 0 0;
    overflow: hidden;
}

.mes .mesAvatarWrapper .avatar img {
    width: 380px;
    height: 240px;
    object-fit: cover;
    filter: grayscale(30%);
    border-radius: 19px 19px 0 0;
}

.mes:not([is_user="true"]) .mesAvatarWrapper .avatar img {
    object-position: center 20%;
}

.mes[is_user="true"] .mesAvatarWrapper .avatar img {
    object-position: center center;
}

/* å¤´åƒæ¸å˜é®ç½© */
.mes .mesAvatarWrapper::before {
    content: "";
    position: absolute;
    bottom: 0;
    left: 0;
    width: 380px;
    height: 160px;
    background: linear-gradient(to top,
        rgba(255, 255, 255, 1) 0%,
        rgba(255, 255, 255, 0.97) 8%,
        rgba(255, 255, 255, 0.92) 16%,
        rgba(255, 255, 255, 0.84) 24%,
        rgba(255, 255, 255, 0.74) 32%,
        rgba(255, 255, 255, 0.62) 42%,
        rgba(255, 255, 255, 0.48) 52%,
        rgba(255, 255, 255, 0.34) 62%,
        rgba(255, 255, 255, 0.21) 72%,
        rgba(255, 255, 255, 0.11) 82%,
        rgba(255, 255, 255, 0.04) 92%,
        rgba(255, 255, 255, 0) 100%);
    z-index: 2;
    pointer-events: none;
}

.mes .mesAvatarWrapper::after {
    display: none !important;
}

/* å¤´åƒç‚¹å‡»ç¦ç”¨ */
.mes .mesAvatarWrapper,
.mes .mesAvatarWrapper .avatar,
.mes .mesAvatarWrapper .avatar img {
    pointer-events: none !important;
    cursor: default !important;
}

/* åç§°æ ·å¼ */
.mes .name_text {
    font-size: 1.1em;
    font-weight: 600;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: -15px;
    white-space: nowrap;
    z-index: 10;
    color: #1a1a1a;
}

.mes .name_text::before,
.mes .name_text::after {
    content: "â—‡";
    font-size: 0.8em;
    margin: 0 5px;
    color: #1a1a1a;
}

/* æ—¶é—´æˆ³ */
.mes .timestamp {
    font-size: 0.85em;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 5px;
    color: #1a1a1a;
    font-weight: 500;
}

/* æ¶ˆæ¯æ–‡æœ¬ */
.mes_text {
    font-size: 1em;
    line-height: 1.8;
    color: #1a1a1a;
    letter-spacing: 0.3px;
    padding: 0;
