    padding: 12px 16px;
    margin: 10px 0;
    font-style: italic;
    color: #444;
    position: relative;
}

/* è§’è‰²æ¶ˆæ¯å¸ƒå±€ */
.mes:not([is_user="true"]) .mes_block {
    padding-top: 5px !important;
}

