  --frost-ai-frame: url('https://s41.ax1x.com/2026/02/04/pZIMm24.png');
  --frost-user-frame: url('https://s41.ax1x.com/2026/02/04/pZIMm24.png');
  --frost-snow-cap: url('https://i.postimg.cc/t4zsVRt3/yi-yi-chu-bei-jing-de-jie-ping-2026-02-06-12-44-25.png');
