  --frost-floor-prefix: 'â„ï¸ å†¬æ—¥æœ­è®° ';
  --frost-timer-prefix: 'â„ï¸ è€—æ—¶ ';
  --frost-timer-suffix: ' â„ï¸';
  --frost-token-prefix: 'ðŸŽ æ•°æ® ';
