body {
  background-color: var(--bg-mist) !important;
  background-image: url('https://s41.ax1x.com/2026/02/05/pZoSQje.jpg') !important;
  background-size: 100vw 100vh !important;
  background-position: center top !important;
  background-attachment: fixed !important;
  background-repeat: no-repeat !important;
  background-blend-mode: soft-light !important;
  color: var(--ink-main) !important;
  font-family: 'ChillKai', 'PingFang SC', 'Microsoft YaHei', sans-serif !important;
  font-weight: normal !important;
  overflow-x: hidden !important;
}

#chat {
  background: transparent !important;
  padding-top: 60px !important;
  padding-bottom: 0 !important;
  counter-reset: ID-counter -1;
  align-items: center !important;
}
#sheld {
  background: transparent !important;
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  border: none !important;
  box-shadow: none !important;
  top: var(--ui-nav-h) !important;
  height: calc(100vh - var(--ui-nav-h) - 1px) !important;
  height: calc(100dvh - var(--ui-nav-h) - 1px) !important;
  max-height: calc(100dvh - var(--ui-nav-h) - 1px) !important;
}

.mes_text {
  width: 100% !important;
  max-width: 100% !important;
  color: var(--ink-main) !important;
  font-weight: 400 !important;
  line-height: 1.6 !important;
  padding: 0 14px 14px 14px !important;
  box-sizing: border-box !important;
  font-family: 'ChillKai', 'PingFang SC', 'Microsoft YaHei', serif !important;
  font-size: var(--mainFontSize) !important;
}
.mes_text q {
  background: linear-gradient(135deg, rgba(225, 245, 255, 0.65) 0%, rgba(255, 236, 246, 0.55) 100%) !important;
  color: rgba(30, 50, 80, 0.92) !important;
  padding: 2px 8px !important;
  border-radius: 8px !important;
  border-left: 3px solid rgba(145, 195, 245, 0.7) !important;
  box-decoration-break: clone !important;
  display: inline !important;
  box-shadow:
    0 2px 10px rgba(30, 50, 80, 0.1),
    inset 0 1px 0 rgba(255, 255, 255, 0.7) !important;
  font-family: 'YRDZST-Regular', 'WenYuan Serif SC VF', 'PingFang SC', 'Microsoft YaHei', serif !important;
}
.mes_text blockquote {
  background: linear-gradient(135deg, rgba(235, 248, 255, 0.55) 0%, rgba(255, 238, 246, 0.4) 100%) !important;
  color: rgba(30, 50, 80, 0.92) !important;
  border-left: 3px solid rgba(145, 195, 245, 0.55) !important;
  border-radius: 10px !important;
  padding: 10px 12px !important;
  margin: 8px 0 !important;
  box-shadow: 0 6px 18px rgba(30, 50, 80, 0.1) !important;
  font-family: 'YRDZST-Regular', 'WenYuan Serif SC VF', 'PingFang SC', 'Microsoft YaHei', serif !important;
}
.mes_text blockquote p {
  margin: 0 0 10px 0 !important;
}
.mes_text em,
.mes_text i {
  color: rgba(74, 123, 176, 0.95) !important;
  font-style: italic !important;
  font-weight: 500 !important;
  background: linear-gradient(135deg, rgba(207, 232, 255, 0.22) 0%, rgba(255, 226, 238, 0.18) 100%) !important;
  padding: 0 5px !important;
  border-radius: 8px !important;
  text-shadow: 0 0 6px rgba(145, 195, 245, 0.25) !important;
  font-family: 'YRDZST-Regular', 'WenYuan Serif SC VF', 'PingFang SC', 'Microsoft YaHei', serif !important;
}
.mes_text strong {
  color: rgba(30, 60, 95, 0.96) !important;
  text-shadow: 0 0 6px rgba(190, 225, 255, 0.35) !important;
}
.mes_text span[style*='color'] {
