custom_css:
/*顶栏*/
#top-settings-holder {
    background-image: url('https://iili.io/f0fRuOF.png'); 
    background-size: 100% 100%;
}


/*图标*/
#top-settings-holder .drawer-icon{
  opacity: 0.8; 
}

/* 平滑过渡 */
#top-settings-holder .drawer-icon::before {
  vertical-align: middle;
  transition: transform 0.3s ease-in-out, color 0.2s ease-in-out;
  display: inline-block;
  font-size: 24px; 
}

/* 导航关闭状态 */
#top-settings-holder .drawer-icon.closedIcon::before {
  content: '' !important; 
  transform: rotate(0deg);
}

/* 悬停效果 */
#top-settings-holder .drawer-icon:hover::before {
    transform: scale(1.2) rotate(15deg);
    transition: transform 0.2s ease-in-out;
}

/* 导航展开状态 */
#top-settings-holder .drawer-icon.openIcon::before {
  content: '' !important;  
  transform: rotate(180deg);
}

#top-settings-holder .drawer-icon{
   opacity: 1;
}

/* 基本设置 */
#top-settings-holder .drawer-icon::before {
  content: '' !important;
  display: inline-block;
  width: 20px;  
  height: 20px; 
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  transition: transform 0.3s ease-in-out, color 0.2s ease-in-out;
}

/* 关闭状态 */
#top-settings-holder .drawer-icon.closedIcon::before {
  background-image: url('') !important; 
  transform: scale(1);
}

/* 展开状态 */
#top-settings-holder .drawer-icon.openIcon::before {
  background-image: url('https://iili.io/f0fxeUX.png') !important; 
  transform: scale(1.5) rotate(0deg);
}


/* 隐藏滚动条 */
::-webkit-scrollbar {
    display: none;
}


/* =========================================
   1. 聊天气泡整体布局与间距
   ========================================= */
.mes {
    position: relative !important;
    overflow: visible !important; 
    margin-bottom: 50px !important;
    margin-top: 220px !important;
}

/* =========================================
   2. 头像 (Avatar) - 放大、旋转、独立
   ========================================= */
.mesAvatarWrapper {
    position: absolute !important;
    /* 你可以在这里调整头像整体的位置 */
    top: -120px !important;   /*上下*/
    left: 240px !important; /* 左右 */

    width: auto !important;
    height: auto !important;
    padding: 0 !important;
    margin: 0 !important;
    order: unset !important; 
    z-index: 10 !important; 
}

/* 头像图片的具体特效 */
.mesAvatarWrapper .avatar {
    /* 放大300% (scale 3)，旋转15度 */
    transform: scale(3) rotate(15deg) !important;
    transform-origin: center center !important;
    max-width: unset !important;
    max-height: unset !important;
}

/* =========================================
   3. 解绑并移动 "三元素" (楼层、计时、词符)
   ========================================= */

/* 楼层数 (Floor ID) */
.mesIDDisplay {
    position: absolute !important;
    z-index: 1;
    top: 130px !important;    /* 上下 */
    right: 130px !important;    /* 左右 */
    width: auto !important;
    margin: 0 !important;
}

/* 回复计时 (Timer) */
.mes_timer {
    position: absolute !important;
    z-index: 1;
    bottom: -95px !important;    /* 上下 */
    left: -130px !important;    /* 左右 */
    width: auto !important;
    margin: 0 !important;
}

/* 词符数 (Token Count) */
.tokenCounterDisplay {
    position: absolute !important;
    z-index: 1;
    bottom: -95px !important;    /* 上下 */
    right: 200px !important; /* 左右 */

    width: auto !important;
    margin: 0 !important;
}

/* =========================================
   4. 解绑并移动 名字 和 时间
   ========================================= */
/* 名字 (Name) */
.name_text {
    position: absolute !important;
    z-index: 1;
    transform: scale(2)  !important;
    top: -50px !important;    /* 上下 */
    left: 50px !important;    /* 左右 */
    width: auto !important;
    text-align: left !important;
}

/* 时间戳 (Timestamp) */
.timestamp {
    position: absolute !important;
    z-index: 1;
    top: -20px !important;    /* 上下 */
    right: 260px !important;    /* 左右 */
    font-size: 0.8em !important; /* 稍微调小字体 */
}

/* =========================================
   5. 操作按钮 (小铅笔等)
   ========================================= */
.mes_buttons {
    position: absolute !important;
    z-index: 1;
    top: 0px !important;    /* 上下 */
    right: 20px !important;    /* 左右 */
    color:#ffffff;
    opacity: 0.8; 
    flex-direction: row !important; 
}

.mes_edit_buttons {
    z-index: 1;
    opacity: 1 !important;
    visibility: visible !important;
}

.mes:hover .mes_edit_buttons {
    opacity: 1 !important;
}

/* =========================================
   6. 文本框主体 (Text)
   ========================================= */
.mes_text {
    z-index: 1;
    margin-left: 10px !important;     /* 左右 */
    margin-top: 30px !important;    /* 上下 */
    width: 100% !important;
}


/*气泡*/
.mes {
  background-image: url('https://iili.io/f0f1HuV.png'); 
  background-size: cover; 
  background-position: center;  
  background-repeat: repeat; 
}

/*头像框*/
.mes .avatar {
  position: relative; 
  overflow: visible !important; 
}

.mes .avatar::after {
  content: '';
  position: absolute;
  top: 5px;
  left: -5px;
  right: -5%;
  bottom: -5%;
  transform: scale(1.7) rotate(-15deg) !important;
  background-image: url('https://iili.io/f0fEIJj.png');
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  pointer-events: none;
}



/*装饰层*/
.mes .mes_block {
  position: relative;
  overflow: visible;
}


.mes .mes_block {
  position: relative;
}


.mes .mes_block::after {
  content: '';
  position: absolute;
  top: -200px;    /* 上下 */
  right: -15px;  /* 左右 */
  width: 430px;  /* 设置宽度 */
  height: 430px;  /* 设置高度 */
  background-image: url('https://iili.io/f0fahpS.png');
  background-size: contain;
  background-repeat: no-repeat;
  z-index: 0; 
  pointer-events: none; 
}



/*下层装饰层*/
.mes .mes_block {
    position: relative; 
}


.mes .mes_block::before {
    content: "";
    display: block;
    position: absolute;
    top: 99%; 
    left: 50%; 
    transform: translateX(-50%);

    /* 图片设置 */
    background-image: url('https://iili.io/f0f7ZDF.png');
    background-repeat: no-repeat;
    background-size: contain; 
    background-position: center top;
    width: 430px;  /* 设置宽度 */
    height: 100px; /* 设置高度 */
    margin-top: 0px; /* 图片与气泡框底部的间距 */
    z-index: 0; 
}


/*底栏*/
#send_form {
  background-image: url('https://iili.io/f0fwYlI.png'); 
  background-repeat: repeat-x; 
  background-size: auto 100%; 
  background-position: center bottom; 
  border-radius: 0px !important; /* 圆角数值 */
  border: 0.1px solid #fff !important; /* 边框，分别为“边框粗细数值”和“颜色” */
  padding: 5px; /* 内边距 */
}


/*底栏图标*/
#send_form [class*="fa-"]::before {
  content: '' !important;
  display: inline-block;
  width: 24px; /* 图片宽度 */
  height: 24px; /* 图片高度 */
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  vertical-align: middle;
}

/* 菜单 */
#options_button::before {
  background-image: url('https://iili.io/f0fxeUX.png') !important;
}

/* 扩展 */
#extensionsMenuButton::before {
  background-image: url('https://iili.io/f0fxeUX.png') !important;
}

/* QR */
#quick-reply-rocket-button i::before {
  background-image: url('https://iili.io/f0fxeUX.png') !important;
}

/* 发送 */
#send_but::before {
  background-image: url('https://iili.io/f0fM99f.png') !important;
}

/* 停止 */
#mes_stop i::before {
  background-image: url('https://iili.io/f0fxeUX.png') !important;
}


/*世界书界面*/
.wi-card-entry {
  border-radius: 0px !important; /* （可选)圆角数值 */
  padding: 20px;
}

.fillRight, .fillLeft, #WorldInfo, #floatingPrompt{
border-radius: 0px !important; /* （可选)圆角数值 */
}


/*主题界面*/
.drawer-content.openDrawer{
border-radius: 0px !important; /* （可选)圆角数值 */
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader{
border-radius: 0px !important; /* （可选)圆角数值 */
background-image: linear-gradient(#BDBDBD);
    margin-bottom: 5px;
    border-radius: 10px;
    padding: 2px 5px;
    border: 1px solid var(--SmartThemeBorderColor);
    transition: all var(--animation-duration-2x);
}

/*引用文本*/
q {
  text-decoration: underline solid rgba(255, 255, 255, 0.3) 0px;
  text-underline-offset: 3px;
}

/*隐藏输入框原身文字*/
#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important; /* 移除可能存在的文字阴影 */
}

/*输入框自定义文案*/
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "You are the only rose in my barren world."; /* ""内自定义文案 */
    color: #BDBDBD; 
    position: absolute; 
    left: 55%;
    top: 50%;
    transform: translate(-50%, -50%); 
    font-style: oblique; 
    font-size: 0.5em;  
    pointer-events: none; 
    z-index: 10; 
    white-space: nowrap; 
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px); 
}

/*正则*/
#saved_regex_scripts,
#saved_scoped_scripts,
#saved_preset_scripts,
#global-script-list,
#character-script-list {
  margin-right: 80px !important;
}



/*思维链*/
.mes_reasoning_summary {
  list-style: none; 
  cursor: pointer;
  display: flex !important;
  align-items: center;
}

.mes_reasoning_header {
  background: linear-gradient(45deg, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0.2)); 
  border: 1px solid rgba(255, 255, 255, 0); /* 边框 */
  border-radius: 0px; /* 圆角 */
  padding: 0px 10px; /* 内边距 */
  top:12px;
  left:50px;
  z-index: 1;
  transition: all 0.2s ease-in-out;
}

.mes_reasoning_summary:hover .mes_reasoning_header {
  transform: scale(1.02); 
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3); /* 辉光效果 */
}

/* 自定义思维链文案 */
.mes_reasoning_header_title {
  font-size: 0 !important;
}

.mes_reasoning_header_title::before {
  content: 'Rose ｀s you.'; /* 自定义文案 */
  font-size: 10px; /* 字体大小 */
  color: #fff; /* 字体颜色 */
  font-weight: bold; /* 粗体 */
}


.mes_reasoning_arrow {
  font-size: 0 !important;
}
.mes_reasoning_arrow::before {
  content: ''; /* 去除箭头 */
  font-size: 12px; 
  color: rgba(255, 255, 255, 0.5);
  display: inline-block; 
  transition: transform 0.3s ease;
}
