/* ==========================================================================
   1. 基础消息容器与布局 (Message Container)
   ========================================================================== */
.mes {
    width: 96%;
    max-width: 560px;
    margin: 120px auto;
    border-radius: 15px !important;background: url('https://iili.io/fO9AhX9.png') repeat-y;
    background-size: 100% auto;
    background-attachment: local;
    transition: transform 0.2s ease;
    transform-origin: top center;
    border: 3px solid #DDA856 !important;
    outline: 1.5px dashed #D08008;
    outline-offset: 3px;
    padding:120px 0px 10px 0px !important;
    position: relative;
}

.mes_text {
    margin-left: 1px;
    margin-right: 5px;
    padding-left: 0;
    padding-right: 0;
}

/* 消息引用/引用块样式 */
.mes_text q {
    background: linear-gradient(to bottom, 
    rgba(255, 255, 255, 0) 50%, 
    #FFF9E0 50%) !important;  
    border-bottom: 1.5px solid #D08008 !important;
}
.mes_block {
  transform: none !important;
}

body * {
  box-shadow: none !important;
  filter: none !important;
}
/* ==========================================================================
   2. 装饰性伪元素 (Decorations)
   ========================================================================== */

.mes_block {
  border-radius: 10px !important;border: 1.5px solid rgba(208,128,8,0.1) !important;background-color:#fff;
  width: 97%;
  margin: 5px auto;
  background-image: 
        linear-gradient(rgba(208,217,217,0) 0.4px, transparent 1px),
        linear-gradient(90deg, rgba(208,128,8,0.1) 0px, transparent 1px);
    background-size: 15px 1px;
    background-position: -1px -1px;
    box-shadow: 0 0 4px rgba(230, 200, 85, 0.5) !important;
  z-index: 10;

}
.mesAvatarWrapper {
transform: none !important;
}
/* 头像样式 */
.mes .avatar {
    cursor: pointer;
    position: absolute;
    top: -70px;
    left: 124px;
    z-index: 10;
}
.mesAvatarWrapper::after {
  content: "“小鸡小鸡小鸡小鸡小鸡咯咯哒”";
  position: absolute;
  top: 44px;
  left: 75px;
  white-space: nowrap;
}
.mes[is_user="false"] .avatar::before {
    content: "";
    position: absolute;
    background: url('https://iili.io/fO2CyZX.gif') no-repeat;
    background-size: contain;
    width: 100px;
    height: 100px;
    top: -56px;
    left: -46px;
    z-index: 1;
}
.mes[is_user="false"] .avatar::after {
    content: "";
    position: absolute;
    background: url('https://iili.io/fO2Fxnf.gif') no-repeat;
    background-size: contain;
    width: 110px;
    height: 110px;
    top: 16px;
    left: 50px;
    z-index: 1;
}
.mes[is_user="true"] .avatar::before {
    content: "";
    position: absolute;
    background: url('https://iili.io/fOdi0N9.gif') no-repeat;
    background-size: contain;
    width: 100px;
    height: 100px;
    top: -57px;
    left: 50px;
    z-index: 1;
}
.mes[is_user="true"] .avatar::after {
    content: "";
    position: absolute;
    background: url('https://iili.io/fOdQy0J.gif') no-repeat;
    background-size: contain;
    width: 110px;
    height: 110px;
    top: 8px;
    left: -55px;
    z-index: 1;
}
.mes .avatar img {
    position: absolute;
    width: 98px !important;
    height: 93px !important;
    border: 3px solid #DDA856 !important;
}

/* ==========================================================================
   3. UI 状态信息显示 (ID, Timer, Token, etc.)
   ========================================================================== */
.mesIDDisplay {
    position: absolute !important;
    color: #000000;
    top: 117px !important;
    right: 15px;background: #ffd;border-radius: 20px !important;
    border: 1.5px solid #000000 !important;padding: 0px 6px !important;
    z-index: 20;
}

.mes_timer {
    position: absolute !important;
    top: 117px !important;
    right: 150px;
    background: #ffd;border-radius: 20px !important;
    border: 1.5px solid #000000 !important;padding: 0px 6px !important;
    color: #000000;
    z-index: 111;
}

.mes .tokenCounterDisplay {
    position: absolute !important;
    top: 117px;
    right: 290px;
   background: #ffd;border-radius: 20px !important;
    border: 1.5px solid #000000 !important;
    color: #000000;
    padding: 0px 6px !important;
    z-index: 10000;
}

.name_text {
    position: absolute !important;
    top: 66px !important;
    left: 95px;
    color: #000000;
    text-shadow: 0 2px 1px rgba(230, 200, 85, 0.5) !important;
    font-size: 1rem;
    z-index: 10000;
}
.mes .name_text::before {
  content: "可爱小鸡「";
color:rgba(208,128,8,0.6);
}
.mes .name_text::after {
  content: "」一位";
color:rgba(208,128,8,0.6);
}
.timestamp {
    position: absolute !important;
    top: 90px !important;
    right: 100px;
    color: #000;
    text-shadow: 0 2px 1px rgba(230, 200, 85, 0.7) !important;
    z-index: 13;
    font-size: 0.9rem;
}
.mes .timestamp::before {
  content: "-";
  margin-right: 8px;
}

.mes .timestamp::after {
  content: "-";
  margin-left: 8px;
}

.swipes-counter {
    margin-left: -330px !important;
}

/* ==========================================================================
   4. 通用区块样式 (Input Boxes, Menus, Selects)
   ========================================================================== */
.reasoning_edit_textarea,
.edit_textarea,
.options-content,
#char-management-dropdown,
.buttons_block .menu_button,
#tagInput,
.menu_button,
#form_create textarea,
#persona_search_bar,
.textarea_compact,
.drawer-content select,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
.text_pole {
    background:rgba(230, 228, 185, 0.2);   
    border: 1px solid #D08008 !important;
    border-radius: 12px;
    box-shadow: 0 0 3px rgba(218, 120, 8, 0.5) !important;
    position: relative;
}


/* 标题样式 */
#user-settings-block h4,
.standoutHeader {
    background: #fff;
    padding: 3px 5px;
    border: 1px solid #DDA856 !important;
    border-radius: 12px;
}

/* ==========================================================================
   5. 思维链/推理区块样式 (Reasoning Header)
   ========================================================================== */
.mes_reasoning_header {
   background-color:rgba(208,128,8,0);
    border:none !important;
    position: relative !important;
    left:40px;
    z-index: 20
}

/* 隐藏原生标题文本 */
.mes_reasoning_header_title {
  font-size: 0 !important;
}

/* 显示自定义内容 */
.mes_reasoning_header_title::before {
  content: " ₊⁺ ꒰১┈┈┈┈┈┈ ♡ ┈┈┈┈┈┈໒꒱ ⁺₊"; 
  font-family: "Maruko Gothic CJKsc" !important;
  font-size: 13px;
  text-shadow: 0 1px 0 rgba(255, 255, 255, 0.4);
  line-height: 1.4;
  position: absolute;
  top: 0;
  left: 0;
  white-space: nowrap;
}
.reasoning_edit_textarea, 
.edit_textarea {

  /* 确保在最上层 */
  z-index: 9999 !important;
  position: relative;

  /* 过渡动画 */
  transition: all 0.3s ease !important;
}


/* ==========================================================================
   6. 底栏与输入区域 (Send Form)
   ========================================================================== */
#send_form,
.userSettingsInnerExpandable {
    background-color:#FFF;
    border: 1px solid #DDA856 !important;
    border-radius: 12px;
    position: relative;
    overflow: hidden;
    padding: 3px 10px;
}

#send_form {
    margin-top: -10px;
    margin-bottom: 5px;
    width: calc(100% - 20px);
    margin-left: 10px;
    margin-right: 10px;
}

.mes_edit_buttons {
    position: relative;
    z-index: 9999;
}



/* ==========================================================================
   7. 顶栏与移动端适配 (Top Bar & Media Queries)
   ========================================================================== */
@media screen and (max-width: 1000px) {
    *::-webkit-scrollbar {
        display: none;
    }

    #top-bar {    
         box-shadow: none !important;
    }


/* ==========================================================================
   8. 图标替换与 FontAwesome 样式 (Icons)
   ========================================================================== */
/* 隐藏原有图标 */
.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }

/* 侧边栏图标 */
.drawer-icon.fa-fw {
    width: 30px;
    height: 30px;
    margin-top: 15px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: transform 0.3s ease;letter-spacing: 0.1px!important; 
}
.drawer-icon.fa-fw:hover { transform: scale(1.1); }

/* 具体图标分配 */
#ai-config-button .drawer-icon, 
#advanced-formatting-button .drawer-icon, 
#user-settings-button .drawer-icon, 
#extensions-settings-button .drawer-icon, 
#persona-management-button .drawer-icon { 
    background-image: url('https://iili.io/fO2Fxnf.gif'); 
}

#sys-settings-button .drawer-icon, 
#WI-SP-button .drawer-icon, 
#backgrounds-button .drawer-icon, 
#table_database_settings_drawer .drawer-icon, 
#rightNavHolder .drawer-icon { 
    background-image: url('https://iili.io/fOdi0N9.gif'); 
}

/* 底栏按钮图标 */
#options_button::before, #extensionsMenuButton::before, #send_but::before, #quick-reply-rocket-button > i {
    display: none !important;
}

#options_button, #extensionsMenuButton, #send_but, #quick-reply-rocket-button {
    width: 35px;
    height: 32px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: transform 0.2s ease;
    font-size: 0;
    border: none;
    background-color: transparent;
}
#options_button { background-image: url('https://iili.io/fO2CyZX.gif'); }
#extensionsMenuButton { background-image: url('https://iili.io/fO2CyZX.gif'); }
#send_but, #quick-reply-rocket-button { background-image: url('https://iili.io/fO2CyZX.gif'); }

#mes_stop {
    font-size: 0 !important;
    width: 35px; height: 35px;
    background-image: url('https://iili.io/fOdQy0J.gif');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}

/* 翻页图标 */
.fa-chevron-left:before {
    content: "" !important;
    background-image: url('https://iili.io/fO9IVb1.png');
    width: 55px; height: 40px;
    background-size: contain; background-repeat: no-repeat; background-position: center;
    position: relative; top: 40px; right: 19px;
}
.fa-chevron-right:before {
    content: "" !important;
    background-image: url('https://iili.io/fO9IVb1.png');
    width: 55px; height: 40px;
    background-size: contain; background-repeat: no-repeat; background-position: center;
    position: relative; top: 40px;
}

/* 编辑图标 */
#chat.mes.fa-pencil-alt::before, .fa-pencil::before {
  display: none !important;
}
.fa-pencil-alt, .fa-pencil {
  background: url('https://iili.io/fNyo2kX.png') no-repeat center/contain !important;
  width: 30px !important;
  height: 30px !important;
  display: inline-block;
  vertical-align: middle;
  opacity: 1 !important; 
  transition: opacity 0.3s ease !important; 
  filter: none !important;
}

/* 省略号图标 */
.fa-ellipsis-h::before, .fa-ellipsis::before {
  display: none !important;
}
.fa-ellipsis-h, .fa-ellipsis {
  background: url('https://iili.io/fNyo3pn.png') no-repeat center/contain !important;
  width: 30px !important;
  height: 30px !important;
  display: inline-block;
  vertical-align: middle;
  opacity: 1 !important;
  transition: opacity 0.3s ease !important; 
  filter: none !important;
}


.fa-expand-arrows-alt:before, .fa-maximize:before, .fa-chevron-circle-up:before, .fa-circle-chevron-up:before, .fa-chevron-circle-down::before, .fa-circle-chevron-down::before {
    content: ">ᴗoಣ";
    font-size: 15px;filter: none !important;
}

/* ==========================================================================
   9. 代码块、滑块与复选框 (Components)
   ========================================================================== */
code,.mes pre code  {
    background-color:rgba(230, 228, 185, 0.2);
    border: 1px solid #DDA856 !important;
    color:#000;
    box-shadow: 
        0 0 2px 1px rgba(217,217,217,0.6), 
        inset 0 1px 2px rgba(255, 255, 255, 0.8), /* 顶部内高光 */
        inset 0 1px 0 rgba(139, 69, 19, 0.1); /* 底部内阴影 */
    border-radius: 8px;
}

#smooth_streaming_speed, .neo-range-slider {
    height: 2px !important;
    background: #D08008 !important;
    border-radius: 0px !important;
    background-image: none;
    outline: none;
}

.neo-range-input, .range-block-counter input {
    background: #fff;
    padding: 3px 5px;
    border: 1px solid #DDA856 !important;
    border-radius: 12px;margin-top: 5px;
}

/* 复选框 */
input[type='checkbox'] {
    appearance: none;
    width: 20px; height: 20px;
    border: 1px solid #DDA856;
    border-radius: 15px;
    cursor: pointer;
    position: relative;
    background: #ffffff;
    transition: all 0.2s ease;
}

/* 添加对勾标记 */
input[type='checkbox']:checked::before {
  content: '';
  position: absolute;
  top: 30%;
  left: 30%;
  width: 0px;
  height: 0px;
  border-width: 0 2px 2px 0;
  border-radius: 1px;
}

input[type='checkbox']:checked::after {
  content: '';
  background-image: url('https://iili.io/fNpVBvn.png');
    width: 20px; /* 调整图标宽度 */
    height: 60px; /* 调整图标高度 */
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;

    /* 向下移动 */
    position: relative;
    top: 2px; /* 向下偏移量 */right:0px;
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    background-color:rgba(255, 253, 249, 0.6);
    border: 1px dashed #D08008 !important;
    box-shadow: 0 4px 2px rgba(230, 200, 85, 0.5) !important;
border-radius: 20px;  
}
    .drawer-content {
        background-image: url('https://iili.io/fO9AhX9.png');
        background-size: cover;
        background-position: center;
        background-repeat: no-repeat;
        background-attachment: fixed;
    }
em,i{

    text-shadow: 0 2px 2px rgba(230, 200, 85, 0.7) !important;
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, 
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #55619C !important;

}
/* 自定义滑块 (Chrome/Safari) */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 85px;
    height: 85px;
    background: 
        url('https://iili.io/fO2As7s.gif') center/contain no-repeat,
        transparent !important; /* 强制透明背景 */
    border: none;
    cursor: ew-resize;
    margin-top: -16px;
    box-shadow: none !important; /* 禁用阴影 */
}
