/* ===== 气泡 ===== */

/* 气泡容器 */
.mes {
  display: flex !important;
  flex-direction: column;
  align-items: center;
  max-width: 98%;
  margin: 15% 0 0 10px !important;
  padding: 0 5px 15px !important;
  position: relative;
  background: transparent !important;
}
/* 气泡背景 - 改为网格背景 */
.mes_block {
  position: relative;
  background: 
    /* 网格背景图案 */
    linear-gradient(90deg, rgba(255, 255, 255, 0.1) 1px, transparent 1px) 0 0 / 20px 100%,
    linear-gradient(0deg, rgba(255, 255, 255, 0.1) 1px, transparent 1px) 0 0 / 100% 20px,
    /* 主背景色 */
    rgba(114, 2, 2, 0.8) !important;
  border-radius: 18px !important;
  border: 2px solid var(--color-bubble-border) !important;
  overflow: hidden;
}

/* 气泡高度补偿系统 - 确保头像装饰有足够空间 */
.mes_block {
  padding-top: 5px !important;
  margin-top: 0px !important;
}

/* 移动端适配 */
@media (max-width: 768px) {
  .mes_block {
    padding-top: 5px !important;
  }
}
/* ===== 头像与装饰层叠方案 ===== */

/* 头像容器 - 作为定位参考点 */
.mesAvatarWrapper {
    position: relative !important;
    display: inline-block !important;
    margin: 8% auto 0px auto !important;
width: auto !important; 
    height: auto !important;
    text-align: center !important;
    padding: 20px !important; /* ⬅️ 添加内边距扩大点击区域 */
    margin: 8% auto 0px auto !important;
}

/* 装饰作为底层背景 - 改为相对于头像图片尺寸 */
.mesAvatarWrapper::before {
    content: '';
    position: absolute;
    top:0% !important; /* ⬅️ 改为50%居中 */
    left: 48% !important;
    transform: translate(-50%, -50%) !important;
    width: 500%;
    height: 200%;     
    background-image: var(--url-bubble-decoration);
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;
    z-index: 1 !important;
    opacity: 1 !important;
    pointer-events: none !important;
}

/* 头像图片 - 确保填满容器 */
.mesAvatarWrapper .avatar {
    width: 120px !important;
    height: 120px !important;
position: relative !important;
top: -50% !important; 
left:40%;
    z-index: 2 !important;
    display: block !important;
    margin: 0 auto !important;

    /* 确保头像图片填满avatar元素 */
    object-fit: cover !important; /* 或 contain，根据需求 */
    object-position: center !important;
}

/* 确保头像图片的img元素也填满 */
.mesAvatarWrapper .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
    object-position: center !important;
    display: block !important;
}
/* ===== 小屏幕进一步适配 ===== */
@media (max-width: 480px) {
    .mesAvatarWrapper {
        margin: 20% auto 0px auto !important;        
        padding: 8px !important;                    
    }

    .mesAvatarWrapper .avatar {
        width: 65px !important;                     
        height: 65px !important;                    
    }

    .mes {
        margin: 6px 0 0 6px !important;            /* 8px → 6px */
    }
}

