}
/* =====气泡中按钮===== */
/* 按钮容器 */
.mes .mes_button.extraMesButtonsHint,
.mes .mes_button.mes_edit,
.mes .mes_button.extraMesButtonsHint:hover,
.mes .mes_button.extraMesButtonsHint:active,
.mes .mes_button.extraMesButtonsHint:focus,
.mes .mes_button.mes_edit:hover,
.mes .mes_button.mes_edit:active,
.mes .mes_button.mes_edit:focus {
display: inline-block;
width: 45px !important;
height: 80px !important;
background-repeat: no-repeat !important;
background-position: center !important;
background-size: contain !important;
border: 0 !important;
box-shadow: none !important;
outline: none !important;
filter: none !important;
opacity: 1 !important;
cursor: pointer !important;
}

/* 翻页按钮定位 - 调整位置，确保在气泡内 */
.swipe_left,
.swipe_right {
position: absolute !important;
bottom: 15px !important; /* 调整底部距离 */
width: 35px !important; /* 稍微减小尺寸 */
height: 35px !important;
font-size: 0 !important;
color: transparent !important;
opacity: 1 !important;
filter: none !important;
z-index: 1000 !important;
display: block !important;
visibility: visible !important;
}

/* 左滑按钮定位在左下角 - 调整位置 */
.swipe_left {
left: 15px !important; /* 调整左边距 */
}

/* 右滑按钮定位在右下角 - 调整位置 */
.swipe_right {
right: 15px !important; /* 调整右边距 */
}

/* 按钮内容 */
.swipe_left::before,
.swipe_right::before {
content: "";
display: block;
width: 25px; /* 减小图标尺寸 */
height: 25px;
background-repeat: no-repeat;
background-position: center;
background-size: contain;
background-color: transparent;
border: none;
position: absolute;
top: 50%;
left: 50%;
transform: translate(-50%, -50%);
}

/* 左滑按钮图标 */
.swipe_left::before {
background-image: var(--url-swipe-icon-left);
}

/* 右滑按钮图标 */
.swipe_right::before {
background-image: var(--url-swipe-icon-right);
transform: translate(-50%, -50%) rotate(0deg); /* 右滑按钮旋转180度 */
}

/* 移除调试样式 */
.swipe_left,
.swipe_right {
background-color: transparent !important;
border: none !important;
}

/* 回复菜单按钮 */
.mes .mes_button.extraMesButtonsHint {
background-image: var(--url-edit-icon) !important;
}

/* 回复编辑按钮 */
