.mes .mesIDDisplay {
    bottom: 40% !important;
    right: -40% !important;    /* 在mes_timer左侧 */
}

.mes .tokenCounterDisplay {
    bottom: 40% !important;
    right: -5% !important;    /* 最左侧 */
}

/* 手机适配 */
@media (max-width: 768px) {
    .mes .mes_timer {
        bottom: 40% !important;
        right: -60% !important;
    }

    .mes .mesIDDisplay {
        bottom: 40% !important;
        right: -30% !important;
    }

    .mes .tokenCounterDisplay {
        bottom: 40% !important;
        right: 0% !important;
    }
}

/* 小手机适配 */
@media (max-width: 480px) {
    .mes .mes_timer {
        bottom: 40% !important;
        right: -100% !important;
    }

    .mes .mesIDDisplay {
        bottom: 40% !important;
        right: -60% !important;
    }

    .mes .tokenCounterDisplay {
        bottom: 40% !important;
        right: -20% !important;
    }
}

/* ===== 头像框样式 ===== */
.mesAvatarWrapper .avatar::after {
    content: '';
    position: absolute !important;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%) !important;
    width: 140% !important;
    height: 140% !important;
    background-image: url('https://i.imgs.ovh/2025/12/13/CHzJap.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;
    z-index: 3 !important;
    opacity: 1 !important;
    pointer-events: none !important;
}

/* 思维链设计 - 相对于气泡左右边线居中 */
.mes_reasoning_header {
    border: none !important;
    border-radius: 0 !important;
    padding: 0 !important;
    margin: 0px 0 0px 0 !important;
    box-shadow: none !important;
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    position: relative !important;
    z-index: 1 !important;
    cursor: pointer;

    /* 根据图片比例调整尺寸 */
    /* 图片尺寸：1864×268，宽高比约 6.96:1 */
    width: 90% !important; /* 适当宽度 */
    max-width: 95% !important;
    min-height: 0 !important; /* 取消固定高度 */
    height: 100% !important; /* 自适应高度 */
    aspect-ratio: 6.96 / 1 !important; /* 根据图片宽高比设置 */

    background-image: var(--url-paper-texture) !important;
    background-size: contain !important; /* 改为contain完整显示图片 */
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-color: transparent !important;

    left: 0 !important;
    right: 0 !important;
    transform: none !important;
    margin-left: auto !important;
    margin-right: auto !important;
}
