custom_css:
/*
复古便签页 @Mayonnaise
非常感谢墨千尾老师的教程，以及楼内各位老师的经验分享！
*/

/* 变量与全局字体  */
@import url("https://fontsapi.zeoseven.com/309/main/result.css"); /* KingHwaOldSong */

:root {
    /* 尺寸 */
    --hole-sidebar-w: 38px;

    /* 纹理 */
    --noise-texture: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.4'/%3E%3C/svg%3E");
}

/*  基础布局与全局样式 */
body::before {
    content: "";
    position: fixed;
    top: 0; left: 0; width: 100vw; height: 100vh;
    background-image: var(--noise-texture);
    opacity: 0.08;
    pointer-events: none;
    z-index: 9999;
    mix-blend-mode: multiply;
}

#sheld {
    display: flex;
    flex-direction: column;
    height: 100vh;
}

#chat {
    flex: 1;
    overflow-y: auto;
    background-color: #F8F8F8;
    background-image: linear-gradient(90deg, transparent calc(var(--hole-sidebar-w) - 1px), #e0ddd0 calc(var(--hole-sidebar-w) - 1px), #e0ddd0 var(--hole-sidebar-w), transparent var(--hole-sidebar-w));
    background-size: 100% 100%;
    -webkit-mask: radial-gradient(circle at 12px 22px, transparent 5px, #000 5.5px) repeat-y 0 0/100% 50px;
    mask: radial-gradient(circle at 12px 22px, transparent 5px, #000 5.5px) repeat-y 0 0/100% 50px;
    box-shadow: inset -10px 0 20px -10px rgba(0,0,0,0.1);
}

::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}

::-webkit-scrollbar-thumb {
    background-color: #1A2A3B !important;
    border-radius: 0;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::selection {
    background: #AAAEB5;
    color: #fff;
}

/* 顶部栏与输入区域 */
#top-bar {
    background: #F8F8F8 !important;
    border: 1px solid #1A2A3B;
    box-shadow: none;
    border-radius: 2px;
}

#top-settings-holder .drawer-icon {
    font-size: 27px;
    color: #1A2A3B;
}

#form_sheld {
    background: #F8F8F8 !important;
    border: 1px solid #1a2a3b !important;
    border-radius: 5px !important;
    box-shadow: none !important;
    transition: transform 0.2s ease;
    padding: 7px !important;
    margin-bottom: 7px !important;
}

#form_sheld:hover {
    transform: translateY(-2px);
}

#form_sheld .fa-solid,
#form_sheld .fa-regular {
    color: #1a2a3b !important;
}

#send_textarea {
    color: #1a2a3b;
}

/* 聊天消息 - 布局 */
.mes {
    --avatar-size: 120px;
    position: relative;
    display: block !important;
    padding-left: calc(var(--hole-sidebar-w) + 15px) !important;
    padding-right: 15px;
    margin: 20px 0;
    overflow: visible !important;
}

.mesAvatarWrapper {
    position: absolute !important;
    left: calc(var(--hole-sidebar-w) + 20px) !important;
    width: var(--avatar-size) !important;
    height: var(--avatar-size) !important;
    margin: 0 !important;
    padding: 0 !important;
    z-index: 10;
}

.mes_block {
    display: block !important;
    width: 100% !important;
    max-width: 100% !important;
    margin: 0 !important;
}

/* 聊天消息 - 头像与元数据 */
.mes .avatar img {
    width: var(--avatar-size) !important;
    height: var(--avatar-size) !important;
    border: 2px solid #fff;
    object-fit: cover;
    box-shadow: 2px 2px 5px -2px rgba(0, 0, 0, 0.18);
    transform: rotate(-1.5deg);
    transition: transform 0.3s ease;
}

.mes .avatar img:hover {
    transform: rotate(0deg);
}

.mes .ch_name {
    display: flex !important;
    flex-direction: column;
    justify-content: flex-end;
    position: relative;
    margin-left: calc(var(--avatar-size) + 15px) !important;
    min-height: var(--avatar-size);
    padding-bottom: 70px;
    border-bottom: 1px dashed #1a2a3b;
    margin-bottom: 10px;
}

.mes .name_text {
    font-family: 'Times New Roman', serif !important;
    font-size: 24px !important;
    font-weight: 900 !important;
    text-transform: uppercase;
    color: #1a2a3b;
    letter-spacing: -0.5px;
    margin-bottom: 5px;
}

.mesIDDisplay, .mes_timer, .tokenCounterDisplay {
    position: absolute;
    z-index: 5;
    font-size: 11px;
    color: #5d6e7d;
    left: calc(var(--avatar-size) + 5px);
    padding-left: 70px;
    min-width: 130px;
    min-height: 14px;
}
.mesIDDisplay { top: 52px; }
.mes_timer { top: 72px; }
.tokenCounterDisplay { top: 92px; }

.mesIDDisplay::before,
.mes_timer::before,
.tokenCounterDisplay::before {
    position: absolute;
    left: 15px;
    top: 0;
    display: inline-block;
    width: 50px;
    text-align: left;
}
.mesIDDisplay::before { content: 'ID:'; }
.mes_timer::before { content: 'TIME:'; }
.tokenCounterDisplay::before { content: 'TOKEN:'; }

.mesIDDisplay::after,
.mes_timer::after,
.tokenCounterDisplay::after {
    content: '';
    position: absolute;
    left: 70px;
    bottom: -2px;
    width: 60px;
    height: 0.5px;
    background-color: #5d6e7d;
}

/* 聊天消息 - 文本内容 */
.mes_text {
    clear: both;
    display: block;
    width: 100%;
    font-family: "KingHwaOldSong", serif !important;
    font-weight: normal;
    font-size: 15px !important;
    line-height: 28px !important;
    color: #0d1e2e;
    background: repeating-linear-gradient(transparent, transparent 31px, #2c4a6314 32px) local;
    padding: 35px 5px !important;
}

.mes_text q {
    display: inline;
    font-style: normal !important;
    background: rgba(44, 74, 99, 0.08);
    border: 1px solid rgba(44, 74, 99, 0.3);
    color: #2c3e50;
    margin: 0;
    box-decoration-break: clone;
    -webkit-box-decoration-break: clone;
}

.mes_reasoning_header {
    background: #1a2a3b !important;
    padding: 4px 8px !important;
    margin: 15px 0 10px 0;
    border-bottom: 2px solid #1a2a3b;
    border-radius: 0;
}

.mes_reasoning_header_title {
    color: #F8F8F8 !important;
    font-family: 'Times New Roman', serif !important;
    font-weight: bold;
    font-size: 0 !important;
    text-transform: uppercase;
}

.mes_reasoning_header_title::before {
    content: 'THINKING';
    font-size: 14px;
}

.mes_reasoning_arrow {
    display: none;
}

/* 聊天消息 - 交互按钮  */
.mes_buttons {
    position: absolute;
    right: 0;
    bottom: 15px;
    opacity: 0.5;
    transition: opacity 0.2s;
}

.mes_edit_buttons {
    position: absolute;
    right: 0;
    top: 125px;
    transform: scale(0.8);
}

.mes:hover .mes_buttons {
    opacity: 1;
}

/* 通用UI组件 */
.text_pole,
select,
input[type="text"],
input[type="number"],
input[type="search"],
#settingsSearch,
.popup textarea,
#description_textarea,
#firstmessage_textarea,
.edit_textarea.mdHotkeys,
.reasoning_edit_textarea,
.select2-container--default .select2-selection--single,
.select2-container--default .select2-selection--multiple {
    background-color: #DDD !important;
    border: 1px solid #1A2A3B !important;
    padding: 1px !important;
    border-radius: 2px !important;
}

h4,
.standoutHeader,
#rm_extensions_block .inline-drawer-toggle.inline-drawer-header {
    background-color: #1a2a3b;
    color: #F8F8F8;
    font-family: 'Times New Roman', serif;
    border-radius: 0 !important;
}

.popup-button-ok,
#dialogue_popup_ok, 
#dialogue_del_mes_ok {
    background-color: #DDD !important;
}

dialog.popup, 
#select_chat_popup,
select, 
.list-group, 
.select2-dropdown, 
#options, 
#options-content, 
#extensionsMenu {
    box-shadow: none !important;
}

.drawer-content {
    border-radius: 2px !important;
}

.select2-container--default .select2-selection--single .select2-selection__rendered {
    color: #1a2a3b !important;
}

.select2-selection__choice__display,
.select2-selection__choice__remove {
    background: none !important;
}

#completion_prompt_manager_list {
    background: none !important;
    border: 0 !important;
}

.completion_prompt_manager_header {
    color: #1A2A3B !important;
}

.completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled.ui-sortable-handle {
    background-color: #DDD !important;
}

.completion_prompt_manager_prompt_disabled .prompt-manager-inspect-action {
    color: #999EA3 !important;
}

.avatar-container.interactable.selected {
    border: 1px solid #B1B0AE;
}

.fa-fw {
    color: #1A2A3B !important;
}

/* 字体与排版 */
code, pre {
    font-family: 'Times New Roman', serif;
    color: #1a2a3b;
    background-color: #DDD;
    border: 1px solid #1a2a3b;
    border-radius: 2px;
}

#send_textarea,
.edit_textarea.mdHotkeys,
.reasoning_edit_textarea {
    font-family: 'Times New Roman', serif;
}
