custom_css:
/* ============================================================
   油墨插图 @Mayonnaise
   非常感谢导航楼内各位老师的分享，所使用代码已在以下标注作者❤︎
   ============================================================ */

@import url("https://fontsapi.zeoseven.com/467/main/result.css");

:root {
    --custom: url('https://pub-222b3a4a8d2e47bc9c31cc9e1733ed9a.r2.dev/%E6%B2%B9%E5%A2%A8%E6%8F%92%E5%9B%BE/bdeeee04-2fad-4a5a-b611-fbd0268a2e29.png');/* 自定义图片 */
}

body {
    font-family: "NanoOldSong-A";
    font-weight: normal;
}

/* ============================================================
   2. 聊天主容器布局 (#chat, #top-bar)
   ============================================================ */
#chat {
    backdrop-filter: blur(15px);
    border: none !important;
    mask: linear-gradient(
        to bottom,
        transparent 0%,
        rgba(0, 0, 0, 0.1) 2%,
        rgba(0, 0, 0, 0.5) 5%,
        black 12%,
        black 91%,
        rgba(0, 0, 0, 0.5) 97%,
        rgba(0, 0, 0, 0.1) 99%,
        transparent 100%
    );
}

#top-bar {
    background: transparent;
    box-shadow: none;
}

/* 顶栏图标二改by墨千尾老师 */
#top-settings-holder .drawer-icon {
    color: var(--SmartThemeBotMesBlurTintColor);
    transition: all 0.3s ease-in-out;
    font-size: 20px;
    opacity: 1;
}

#top-settings-holder .drawer-icon:hover,
#top-settings-holder .drawer-icon:not(.closedIcon) {
    transform: scale(1.1);
}

/* ============================================================
   3. 输入区域与发送表单 (#send_form, #qr--bar)
   ============================================================ */
#send_form {
    position: absolute;
    bottom: 5px;
    background: transparent;
    border: none;
    transition: backdrop-filter 1s ease-in-out;
}

/* 底栏图标二改by墨千尾老师 */
#send_form [class*="fa-"]::before {
    color: var(--SmartThemeBotMesBlurTintColor);
    opacity: 1;
    font-size: 20px;
    vertical-align: middle;
}

#send_form:focus-within {
    transition: backdrop-filter 1s ease-in-out;
    backdrop-filter: blur(10px);
}

#send_textarea {
    color: var(--SmartThemeUserMesBlurTintColor);
    font-family: "NanoOldSong-A";
}

/* 输入框文字by hgu老师 */
#send_textarea::placeholder {
    color: transparent !important;
    opacity: 0 !important;
}

/* 隐藏QR栏二改by 12老师 */
#qr--bar {
    max-height: 0 !important;
    opacity: 0 !important;
    pointer-events: none;
    transition: all 1.5s !important;
    cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

#send_form:focus-within #qr--bar {
    max-height: 120px !important;
    opacity: 1 !important;
    pointer-events: auto;
}

/* ============================================================
   4. 通用消息样式 (背景、按钮、文本装饰)
   ============================================================ */
.mes {
    position: relative;
}

.mes.last_mes {
    margin-bottom: 50px !important;
}

.mes::after {
    content: "";
    position: absolute;
    background-image: url("https://www.transparenttextures.com/patterns/blizzard.png"); /* 使用变量 */
    background-repeat: repeat;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    pointer-events: none;
}

.mes_text {
    padding: 30px 25px 50px 25px;
}

.mes .name_text {
    margin-left: 25px;
}

/* 消息功能按钮样式 */
.mes_button, 
.extraMesButtons .mes_button {
    filter: none !important;
}

.mes_edit, 
.extraMesButtonsHint {
    filter: none;
    margin-right: 5px;
}

.mes_edit::before {
    content: '❞' !important;
}

.extraMesButtonsHint::before {
    content: '❝' !important;
}

.mes_edit_buttons {
    transform: scale(0.8);
    filter: none;
}

/* 代码与引用块 */
code, pre {
    font-family: "NanoOldSong-A";
    color: rgba(80, 80, 80, 1);
    background-color: #00000020;
    border: 0;
    border-radius: 0 !important;
}

::selection {
    background: var(--SmartThemeQuoteColor);
    color: #fff;
}

.mes_text blockquote {
    color: #666;
    background-color: color-mix(in srgb, var(--SmartThemeQuoteColor), transparent 70%) !important;
    border: 0;
    border-radius: 0 !important;
}

/* 翻页图标替换二改by 眠N勿扰老师 */
.swipe_left, .swipe_right {
    position: absolute;
    bottom: 5px;
}

.swipe_left::before {
    font-family: "NanoOldSong-A";
    content: 'Prev.' !important;
    font-size: calc(var(--mainFontSize) - .2rem);
    margin-left: 28px;
}

.swipe_right::before {
    font-family: "NanoOldSong-A";
    font-size: calc(var(--mainFontSize) - .2rem);
    content: 'To be continued...' !important;
    white-space: nowrap;
    margin-right: 80px;
}

.swipes-counter {
    font-family: "NanoOldSong-A";
    position: absolute;
    bottom: 20px;
    right: 15px;
    opacity: 1;
    font-size: calc(var(--mainFontSize) - .1rem);
}

/* ============================================================
   5. 角色消息适配 (.mes[is_user="false"])
   ============================================================ */
.mes[is_user="false"].mes {
    padding: 180px 0 0 0 !important;
}

.mes[is_user="false"].mes::before {
    content: "";
    position: absolute;
    width: 95%;
    height: 150px;
    top: 0;
    left: 50%;
    transform: translate(-50%);
    background-image: var(--custom); /* 使用变量 */
    background-size: cover;
    -webkit-mask-image: linear-gradient(to bottom, black 80%, transparent 100%);
    mask-image: linear-gradient(to bottom, black 75%, transparent 100%);
    background-position:auto;
    background-size:cover;
}

.mes[is_user="false"] .mesIDDisplay {
    position: absolute;
    top: 153px;
    left: 35px;
    font-size: 15px !important;
    opacity: 1 !important;
}

.mes[is_user="false"] .mes_buttons {
    position: absolute;
    top: 153px;
    right: 20px;
}

/* 消息元数据样式 */
.mesIDDisplay::before {
    content: 'Chapter ';
    font-size: calc(var(--mainFontSize) + .1rem);
}

.timestamp { left: 35px; }
.mes_timer { left: 205px; }
.tokenCounterDisplay { left: 165px; }

.mes_timer, .tokenCounterDisplay, .timestamp {
    bottom: 26px;
    position: absolute;
    font-size: calc(var(--mainFontSize) - .1rem) !important;
    font-weight: normal !important;
    opacity: 0.6 !important;
}

/* ============================================================
   6. 用户消息适配 (.mes[is_user="true"] 水平翻转)
   ============================================================ */
.mes[is_user="true"] .mesIDDisplay {
    position: absolute;
    right: 35px;
    font-size: 15px !important;
    opacity: 1 !important;
}

.mes[is_user="true"] .name_text {
    position: absolute;
    right: 35px !important;
    top: 35px;
}

.mes[is_user="true"] .ch_name.flex-container.justifySpaceBetween {
    flex-direction: row-reverse !important;
    margin-left: 5px;
}

.mes[is_user="true"] .timestamp {
    left: auto !important;
    right: 35px !important;
}

.mes[is_user="true"] .tokenCounterDisplay {
    left: auto !important;
    right: 165px !important;
}

.mes[is_user="true"] .mes_timer {
    left: auto !important;
    right: 205px !important;
}

/* ============================================================
   7. 思维链样式 (Reasoning)
   ============================================================ */
.mes_reasoning {
    color: rgba(57, 57, 57, 1);
}

.mes_reasoning_header.flex-container {
    background: transparent !important;
    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;
    padding: 10px 25px !important;
    min-height: auto !important;
    cursor: pointer;
}

.mes_reasoning_header.flex-container i,
.mes_reasoning_header.flex-container span,
.mes_reasoning_header.flex-container .fa-solid {
    display: none !important;
}

.mes_reasoning_header.flex-container::before {
    content: "序 /";
    font-family: "NanoOldSong-A", serif;
    font-size: calc(var(--mainFontSize) + .3rem);
    color: var(--SmartThemeBodyColor);
}

/* ============================================================
   8. UI 控件与通用组件 (复选框、滑块、按钮、Toast)
   ============================================================ */

/* 复选框二改by食南瓜者老师 */
input[type="checkbox"] {
    appearance: none;
    -webkit-appearance: none;
    width: 14px;
    height: 14px;
    border: 1px solid #666;
    background: transparent;
    transition: all 0.2s ease;
    position: relative;
}

input[type="checkbox"]:checked {
    background: #666;
    border-color: 0;
}

input[type="checkbox"]::after,
input[type="checkbox"]::before {
    content: none !important;
}

input[type="checkbox"]:checked::after {
    content: '=' !important;
    display: block !important;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #ddd;
    font-size: 11px;
}

input[type="checkbox"]:hover {
    border-color: #aaa;
}

/* 自定义滑块 */
input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    background: transparent !important;
    cursor: pointer;
}

input[type="range"]::-webkit-slider-runnable-track {
    filter: none !important;
    height: 3px;
    background: #808080;
    border: 0 !important;
}

/* 滚动条by墨千尾老师 */
::-webkit-scrollbar {
    width: 5px;
}

input[type="range"]::-webkit-slider-thumb {
    background-color: #000;
    border: 0;
    height: 9px;
    width: 9px;
    margin-top: -3px;
    box-shadow: none !important;
    filter: none !important;
    transition: transform 0.2s ease;
}

input[type="range"]:hover::-webkit-slider-thumb {
    transform: scale(1.1);
}

/* 下拉菜单与通知 */
select option {
    background: transparent !important;
    color: var(--SmartThemeBodyColor) !important;
}

/* 通知美化by serein老师 */
#toast-container > .toast {
    background-image: none !important;
}

.toast {
    background: #ffffff50 !important;
    border: none;
    border-radius: 0 !important;
    box-shadow: none !important;
    color: #555 !important;
    backdrop-filter: blur(20px) !important;
}

/* 通用交互元素 */
.menu_button, .right_menu_button {
    background-color: rgba(255, 255, 255, 0.2) !important;
    transition: background-color 0.3s ease;
    border-radius: 0;
}

.menu_button:hover, .right_menu_button:hover {
    background-color: rgba(255, 255, 255, 0.7) !important;
}

.avatar-container.interactable.selected {
    border-radius: 0;
    border: 0;
    background: #00000015;
}

/* ============================================================
   9. 面板、扩展与强制直角化 (SillyTavern Overrides)
   ============================================================ */
#right-nav-panel, #left-nav-panel {
    border-radius: 0;
    background-image: url("https://www.transparenttextures.com/patterns/blizzard.png"); /* 使用变量 */
}

.text_pole,
select,
input,
button,
textarea,
.select2-selection,
.select2-dropdown,
.inline-drawer,
.drawer-content,
.list-group,
dialog.popup,
.popup-content,
.mes_text,
#send_form,
#send_textarea,
#PersonaManagement,
#user-settings-block,
#rm_extensions_block,
#extensionsMenu,
#options,
#options-content,
#select_chat_popup,
.select2-selection__choice {
    border-radius: 0 !important;
}

/* 针对特定输入框的背景色 */
.text_pole, select, input[type="text"], input[type="number"], 
input[type="search"], #settingsSearch, .popup textarea, 
#description_textarea, #firstmessage_textarea, .edit_textarea.mdHotkeys, 
.reasoning_edit_textarea, .select2-container--default .select2-selection--single, 
.select2-container--default .select2-selection--multiple {
    padding: 0;
    background: rgba(189, 189, 189, 1);
}

/* 扩展栏图标与背景 */
.fa-circle-chevron-down, .fa-circle-chevron-up,
.inline-drawer-icon.fa-circle-chevron-down, 
.inline-drawer-icon.fa-circle-chevron-up {
    font-size: 15px;
    opacity: 0.8;
}

#rm_extensions_block .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4, .standoutHeader, 
#completion_prompt_manager_list, .reverse_proxy_warning {
    background: transparent !important;
}

.reverse_proxy_warning { color: #000 !important; }

/* Prompt Manager 微调 */
#completion_prompt_manager .completion_prompt_manager_header_advanced span,
#completion_prompt_manager .completion_prompt_manager_header div,
.prompt_manager_prompt_controls span {
    filter: none !important;
    color: #555 !important; /* 类名byMDMA老师 */
}

.completion_prompt_manager_prompt_disabled .prompt-manager-inspect-action {
    color: rgba(167, 167, 167, 1) !important;
}

.completion_prompt_manager_prompt .fa-asterisk,
.completion_prompt_manager_prompt .fa-square-poll-horizontal,
.completion_prompt_manager_prompt .fa-syringe,
.completion_prompt_manager_prompt .fa-thumb-tack,
.fa-xs.fa-solid.fa-robot {
    color: #555 !important; /* 类名by hy老师 */
}

dialog.popup, #select_chat_popup, select, .list-group, 
.select2-dropdown, #options, #options-content, #extensionsMenu {
    box-shadow: none !important;
}

.select2-selection__choice__display,.select2-selection__choice__remove {
    background: none !important;
}

.mes.selected {
    background-color: #00000050 !important;
}
