/* --- 绣春刀 v1.5
感谢代码楼里老师们的代码https://discord.com/channels/1291925535324110879/1380392618336194702
感谢尾老师，类名，输入框伪提示词来自尾老师，hy老师的去除思维链箭头，na老师的悬浮底栏和思维链文字自定义，楼层信息自定义，12的去思维链背景色。感谢NA老师的去阴影，感谢咩野老师的去除全局阴影.殷暮夜专属美化 --- */

/* --- 1. 字体导入 --- */
@import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond&display=swap'); /* 英文 */
@import url("https://fontsapi.zeoseven.com/440/main/result.css"); /* 荟圆体-方黑 - 全局 */
@import url("https://fontsapi.zeoseven.com/478/main/result.css"); /* 仓耳周珂正大榜书 - 标题 */
@import url("https://fontsapi.zeoseven.com/445/main/result.css"); /* 云峰静龙行书 - 引用/斜体 */
@import url("https://fontsapi.zeoseven.com/796/main/result.css"); /* Hangeuljaemin4.0 - 代码 */

/* --- 2. 核心颜色与样式变量 --- */
:root {
    --color-bg-dark: #19171b;
    --color-blood-red: #75020f;
    --color-dark-red: #51080d;
    --color-deep-red-black: #2b0307;
    --color-text-main: #E0D8D5;
    --color-text-subtle: #A39B98;

    --theme-bg: var(--color-bg-dark);
    --panel-bg-color: rgba(43, 3, 7, 0.85);
    --panel-bg-hover: rgba(81, 8, 13, 0.95);
    --card-bg: rgba(81, 8, 13, 0.9);
    --input-bg: rgba(43, 3, 7, 0.9); 
    --text-main: var(--color-text-main);
    --text-subtle: var(--color-text-subtle);
    --border-color: rgba(117, 2, 15, 0.4);
    --accent-color: var(--color-blood-red);
    --selected-color: #C0B0A8;

    --font-english: "Cormorant Garamond", serif;
    --font-main: "Huiwen-Fangsong", var(--font-english);
    --font-quote: "YFJLXS", var(--font-english);
    --font-code: "Hangeuljaemin4.0", monospace;
    --font-title: "TsangerZhoukeZhengdabangshu", var(--font-english);

    --border-radius: 16px;
}

/* --- 3. 全局样式 --- */
body { background-color: var(--theme-bg) !important; color: var(--text-main); font-family: var(--font-main); }
#sheld, #character_view { background: transparent !important; margin: 0 auto !important; left: 0; right: 0; border: none !important; box-shadow: none !important; overflow: visible !important; }
#chat { background: transparent !important; align-items: center !important; }

h1, h2, h3, h4, .standoutHeader, .inline-drawer-header, .title_restorable { font-family: var(--font-title); }

/* --- 4. 顶部导航栏 --- */
#top-settings-holder {
    background-color: #000000;
    box-shadow: none;
    border: none;
    backdrop-filter: blur(8px);
    background-image: none;
    position: relative;
    margin-bottom: 20px;
}

/* 挂饰 (左侧) - 无缝隙 */
#top-settings-holder::before {
    content: '';
    display: block;
    width: 140px;
    height: 250px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/1bjn/2048X2048/2026-02-24_170350.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: top left;
    position: absolute;
    top: 0;
    left: 0;
    opacity: 1;
    z-index: 10;
    pointer-events: none;
}

/* 导航栏底部装饰分割线 (放大) */
#top-settings-holder::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 100%;
    height: 45px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Z0sV/2048X2048/2026-02-24_172541.png/webp');
    background-size: auto 100%;
    background-repeat: repeat-x;
    z-index: 11;
    pointer-events: none;
}


.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }
.drawer-icon.fa-fw { width: 35px; height: 40px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.3s ease; }
.drawer-icon.fa-fw:hover { transform: scale(1.1); }

#ai-config-button .drawer-icon, .drawer-icon.fa-sliders { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Gidq/2048X2048/2026-02-24_183300.png/webp'); }
#sys-settings-button .drawer-icon, .drawer-icon.fa-plug { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/sSf2/2048X2048/2026-02-24_172645.png/webp'); }
#advanced-formatting-button .drawer-icon, .drawer-icon.fa-font { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Gidq/2048X2048/2026-02-24_183300.png/webp'); }
#WI-SP-button .drawer-icon, .drawer-icon.fa-book-atlas { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/sSf2/2048X2048/2026-02-24_172645.png/webp'); }
#user-settings-button .drawer-icon, .drawer-icon.fa-user { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Gidq/2048X2048/2026-02-24_183300.png/webp'); }
#bg_settings_button .drawer-icon, .drawer-icon.fa-image { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/sSf2/2048X2048/2026-02-24_172645.png/webp'); }
#extensions-settings-button .drawer-icon, .drawer-icon.fa-cubes { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Gidq/2048X2048/2026-02-24_183300.png/webp'); }
#persona-management-button .drawer-icon, .drawer-icon.fa-face-smile { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Gidq/2048X2048/2026-02-24_183300.png/webp'); }
#character_popup_button .drawer-icon, .drawer-icon.fa-address-card { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/sSf2/2048X2048/2026-02-24_172645.png/webp'); }
#table_database_settings_drawer .drawer-icon, .drawer-icon.fa-bars-progress { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/sSf2/2048X2048/2026-02-24_172645.png/webp'); }
.drawer-content { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/kmHG/4096X2102/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D4124443435736917149%26skey%3D%40crypt_5a237c93_fa39fbaae37c5f815fc07534c541a24f%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); background-size: cover; background-position: center; border: none; border-radius: var(--border-radius); box-shadow: none; }

/* --- 5. 底部输入栏 --- */
#send_textarea { clip-path: none !important; background-color: transparent !important; border: none !important; }
#send_form { 
    border-radius: 25px;
    background-image: none !important;
    background-color: var(--input-bg) !important;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: none !important;
    position: relative;
    transition: all 0.5s ease;
    padding: 10px 15px;
}
#send_form:hover { 
    transform: translateY(-5px);
    background-color: var(--panel-bg-hover) !important;
}
#rightSendForm, #leftSendForm { gap: 5px; }
#send_form:has(#send_textarea:focus-visible) { outline: none; }

#options_button::before, #send_but::before, #send_form #extensionsMenuButton::before, #ai_retry_button::before, #auto_completion_button::before { display: none !important; }
#options_button, #send_but, #send_form #extensionsMenuButton, #ai_retry_button, #auto_completion_button { width: 32px; height: 32px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.2s ease; font-size: 0; border: none; background-color: transparent; }
#options_button:hover, #send_but:hover, #send_form #extensionsMenuButton:hover, #ai_retry_button:hover, #auto_completion_button:hover { transform: scale(1.1); }
#options_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/YLkj/2048X2048/2026-02-24_183421.png/webp'); }
#send_form #extensionsMenuButton { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/YLkj/2048X2048/2026-02-24_183421.png/webp'); }
#send_but { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/0Dnj/2048X2048/2026-02-24_183213.png/webp'); }
#auto_completion_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/k259/2048X2048/2026-02-24_172620.png/webp'); }

#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "拔刀出鞘，言若锋芒...";
    color: var(--text-subtle);
    position: absolute;
    top: 50%; left: 50%;
    transform: translate(-50%, -50%);
    font-family: var(--font-main);
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
    opacity: 0.8;
}

/* --- 6. 核心：消息布局与样式 --- */
.mes { display: flex; flex-direction: column; align-items: stretch; gap: 0; padding: 0 !important; margin: 40px auto !important; width: 90%; max-width: 700px; background: transparent !important; border: none !important; box-shadow: none !important; overflow: visible !important; position: relative; }

.mes .mesAvatarWrapper {
    display: grid;
    grid-template-columns: 100px 1fr;
    gap: 0px 20px;
    align-items: center;
    padding: 20px 30px;
    border-radius: 0;
    width: 100%;
    max-width: none;
    background-size: cover;
    background-position: center;
    position: relative;
    z-index: 5;
    min-height: 160px;
    overflow: visible !important; /* ★ 确保装饰图溢出可见 */
}

.mes[is_user="true"] .mesAvatarWrapper { 
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/knQo/4578X2298/2026-02-24_163529.png/webp');
    grid-template-columns: 1fr 100px;
}
.mes[is_user="false"] .mesAvatarWrapper { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/c3Q1/4578X2298/2026-02-24_164155.png/webp'); }

/* --- ★ 1. 顶部装饰（遮罩），在背景图之上，且放大不截断 --- */
/* 用户侧 */
.mes[is_user="true"] .mesAvatarWrapper::before {
    content: ''; position: absolute; 
    top: -20px; left: -60px; /* ★ 负边距让贴图溢出 */
    width: 130%; height: 130%; /* ★ 放大尺寸 */
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Rvp1/2048X2048/2026-02-24_194142.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: top left;
    z-index: 20; /* ★ 绝对顶层 */
    pointer-events: none;
}
/* AI侧 */
.mes[is_user="false"] .mesAvatarWrapper::before {
    content: ''; position: absolute; 
    top: -10px; right: -30px; /* ★ 负边距让贴图溢出 */
    width: 130%; height: 130%; /* ★ 放大尺寸 */
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/rV2Z/2048X2048/2026-02-24_194421.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: top right;
    z-index: 20; /* ★ 绝对顶层 */
    pointer-events: none;
}

/* 确保内容不被遮挡 */
.mes .mesAvatarWrapper > * { position: relative; z-index: 25; }

.mes .avatar { grid-row: 1 / 3; grid-column: 1 / 2; width: 85px !important; height: 85px !important; flex-shrink: 0; display: block !important; position: relative; transform: rotate(-4deg); transition: transform 0.4s ease; }

/* AI头像位置调整：向右平移10px */
.mes[is_user="false"] .avatar { margin-left: 15px; }

.mes[is_user="true"] .avatar { grid-column: 2 / 3; }

.mes .avatar:hover { transform: rotate(0deg); }
.mes .avatar img { 
    border-radius: 0 !important;
    border: none !important; 
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.mes .avatar::before {
    content: '';
    position: absolute;
    top: -4px; left: -4px; right: -4px; bottom: -4px;
    border: 3px solid rgba(224, 216, 213, 0.85);
    border-radius: 0;
}

/* AI头像左侧装饰 */
.mes[is_user="false"] .avatar::after {
    content: '';
    position: absolute;
    bottom: -30px; left: -40px;
    width: 100px; height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/zqaN/2048X2048/2026-02-24_164500.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 10;
    pointer-events: none;
}

.mes .mesAvatarWrapper > .ch_name { grid-row: 1 / 2; grid-column: 2 / 3; display: block !important; font-weight: bold; font-size: 1.2em; color: var(--text-main); margin-bottom: 5px; font-family: var(--font-title); text-shadow: 0 1px 3px rgba(0,0,0,0.5); }
.mes .mesAvatarWrapper > .mes_timer, .mes .mesAvatarWrapper > .mesIDDisplay, .mes .mesAvatarWrapper > .tokenCounterDisplay { display: none !important; }

.mes[is_user="true"] .mesAvatarWrapper > .ch_name { grid-column: 1 / 2; text-align: right; }

/* 底部小横线装饰 */
.mes .mesAvatarWrapper::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 10px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/gKiy/2048X2048/2026-02-24_164530.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 6;
    pointer-events: none;
}

.mes .mes_block { 
    background-color: rgba(25, 23, 27, 0.9);
    backdrop-filter: blur(5px);
    border: none;
    border-radius: 0;
    padding: 25px 30px;
    margin-top: 0;
    position: relative;
    overflow: visible !important;
}

/* --- ★ 2. 连接分割线 (修正版) --- */
/* 挂载在正文框上，并向上提拉 */
.mes_block::before {
    content: '';
    display: block;
    position: absolute;
    top: -30px; /* ★ 提拉到两个板块中间 */
    left: 0; right: 0;
    height: 80px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/KQvp/2048X2048/2026-02-24_173826.png/webp');
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    z-index: 30; /* ★ 确保在最上层 */
    pointer-events: none;
transform: scale(3);
}

.mes .mes_text, .mes_reasoning { background: transparent !important; border: none !important; padding: 0 !important; margin: 0 !important; backdrop-filter: none !important; }

#chat .mes .mes_reasoning_header { background: transparent !important; border: none !important; box-shadow: none !important; border-radius: 0; backdrop-filter: none !important; -webkit-backdrop-filter: none !important; padding: 0 !important; margin-bottom: 10px !important; color: #C1B8B5 !important; font-size: 0.9em !important; display: inline-flex !important; align-items: center !important; transition: none !important; }
.mes_reasoning_arrow { display: none !important; }

.mes_reasoning_header h4, .mes_reasoning_header_title { font-size: 0 !important; visibility: hidden; }
.mes_reasoning_header h4::before, .mes_reasoning_header_title::before { content: "案牍卷宗，明察秋毫"; font-family: var(--font-title); font-size: 14px !important; color: #000000; visibility: visible; }

.mes_reasoning { border-left: 3px solid var(--accent-color) !important; padding-left: 15px !important; margin-bottom: 20px !important; }

.mes .mes_text { line-height: 1.9; color: var(--text-main); }

/* --- 7. 文本样式 --- */
.mes_text strong, .mes_text b { font-family: var(--font-title); color: var(--selected-color); font-weight: bold; }
.mes_text em, .mes_text i { font-family: var(--font-quote); font-style: normal !important; color: var(--accent-color); position: relative; padding: 0 0.5em; }
.mes_text em::before, .mes_text i::before { content: '「'; position: absolute; left: 0; top: 0; }
.mes_text em::after, .mes_text i::after { content: '」'; position: absolute; right: 0; top: 0; }
.mes_text blockquote { background-color: rgba(117, 2, 15, 0.2); border-left: 4px solid var(--accent-color); padding: 10px 15px; border-radius: 0 8px 8px 0; margin: 10px 0; }
.mes_text code { font-family: var(--font-code); background-color: rgba(117, 2, 15, 0.25) !important; color: var(--text-main) !important; padding: 2px 6px; border-radius: 6px; border: none; }

.mes .mes_block > *:not(.mes_buttons) { position: relative; z-index: 1; }

/* --- 消息框底部装饰 --- */
/* 用户消息框 - 底部右侧装饰 */
.mes[is_user="true"] .mes_block::after {
    content: ''; display: block; position: absolute;
    bottom: -50px; right: -20px;
    width: 100px; height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/Q6H2/2048X2048/2026-02-24_170603.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 20;
}

/* AI消息框 - 底部左侧装饰 (翻转) */
.mes[is_user="false"] .mes_block::after {
    content: ''; display: block; position: absolute;
    bottom: -50px; left: -15px;
    width: 120px; height: 120px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/KAV1/2048X2048/2026-02-24_164608.png/webp');
    background-size: contain; background-repeat: no-repeat;
    transform: scaleX(-1); /* 水平翻转 */
    pointer-events: none; z-index: 20;
}

/* --- ★ 3. 消息框背后的书签装饰 (修正版) --- */
/* 挂载在最外层容器，层级设为 -1，位于所有元素背后 */
.mes::before {
    content: '';
    display: block;
    position: absolute;
    width: 120px;
    height: 180px;
    background-size: contain;
    background-repeat: no-repeat;
    z-index: -1; /* ★ 确保在最底层，被消息框遮挡下半部分 */
    pointer-events: none;
}

/* AI书签：左上，冒出 */
.mes[is_user="false"]::before {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/8i6d/2048X2048/2026-02-24_194819.png/webp');
    top: -70px; /* ★ 向上冒出 */
    left: 20px;
}

/* 用户书签：右上，冒出 */
.mes[is_user="true"]::before {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260224/xqze/2048X2048/2026-02-24_194753.png/webp');
    top: -70px; /* ★ 向上冒出 */
    right: 20px;
}


/* --- 8. 元素重定位与自定义文本 --- */
.mes_block > .ch_name {
    position: absolute !important;
    top: -35px !important;
    left: 10px;
    right: 10px;
    height: 30px;
