    color: #BA0317 !important; /* 朱砂红 */
    border-bottom: 1px solid rgba(117, 2, 15, 0.3); /* 极细的装饰线 */
    padding-bottom: 4px;
    margin: 1.2em 0 0.8em 0 !important;
    letter-spacing: 2px; /* 增加字间距，更易读 */
    text-shadow: none; /* 去掉阴影，保持干净 */
}

/* --- 2. 被 ** 加粗的文字：核心标记 --- */
.mes_text strong, .mes_text b {
    font-family: var(--font-title) !important; /* 使用榜书字体作为重点标记 */
    font-weight: normal !important; /* 榜书自带厚度，无需额外加粗 */
