#preloader {
    background-image: url('https://origin.picgo.net/2025/12/16/100005892801756027cba09dc2.png') !important;
    background-size: cover !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
}

#load-spinner {
  margin: 0 !important;
  display: flex !important;
  justify-content: center !important;
  align-items: center !important;
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  animation: simple-sway 1.6s ease-in-out infinite !important;
  background: none !important; 
  border: none !important;
}

#load-spinner::before {
  content: '' !important;
  display: block !important;
  width: 50px !important;
  height: 50px !important;
  background-image: url('https://origin.picgo.net/2025/12/14/IMG_20251214_17391074f62558c2248d54.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  animation: inherit !important;
}

#load-spinner > *:not(::before),
#load-spinner::after {
  display: none !important;
}

@keyframes simple-sway {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(-4deg);
  }
  50% {
    transform: rotate(0deg);
  }
  75% {
    transform: rotate(4deg);
  }
}
