/* ===== 三、底栏 ===== */
/* --- 底栏背景及圆角 --- */
#send_form {
  background-color: rgba(255, 255, 255, 0.7) !important;
  background-image: none;
/* 覆盖可能的变量 */
  --SmartThemeBlurTintColor: rgba(255, 255, 255, 0.6) !important;
  border-radius: 12px !important;
  border: 1px solid rgba(197, 216, 228, 0.6) !important;
  padding: 8px;
  box-shadow: 0 -2px 12px rgba(168, 196, 212, 0.2);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
}

/* 输入框样式 */
#send_textarea {
  border-radius: 8px !important;
  background-color: rgb(245, 249, 251, 0.9) !important;
  border: 1px solid rgba(197, 216, 228, 0.5) !important;
  color: var(--snow-ui) !important;
  clip-path: none !important;
}

#send_textarea:focus {
  border-color: #A8C4D4 !important;
  box-shadow: 0 0 0 3px rgba(168, 196, 212, 0.25) !important;
}

/* --- 底栏图标样式 --- */
/* 底栏图标通用颜色 */
#send_form .fa-solid,
#send_form .fa-regular,
#send_form .fa-fw,
#options_button i,
#extensionsMenuButton i,
#mes_stop i {
  color: var(--snow-ui) !important;
  transition: all 0.2s ease;
}

/* 底栏图标悬停效果 */
#send_form .fa-solid:hover,
#send_form .fa-regular:hover,
#send_form .fa-fw:hover,
#options_button:hover i,
#extensionsMenuButton:hover i {
  filter: drop-shadow(0 0 6px rgba(168, 196, 212, 0.6));
  transform: scale(1.1);
}

/* 发送按钮 - 雪花图标 */
#send_but {
  position: relative;
}

#send_but::before {
  content: '' !important;
  display: inline-block;
  width: 22px;
  height: 22px;
  background-image: url("data:image/svg+xml;base64,") !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

/* 隐藏发送按钮原图标 */
#send_but i.fa-paper-plane {
  display: none !important;
}
/* 菜单图标 - 保持原样但改色 */
#options_button::before {
  content: '\f0c9' !important;
  font-family: "Font Awesome 6 Free" !important;
  font-weight: 900 !important;
  color: var(--snow-ui) !important;
  font-size: 18px !important;
  background-image: none !important;
}

/* 扩展图标 */
