/* ===== 四、聊天区域 ===== */
/* --- 1. 聊天区域背景 --- */
#chat {
  /* 轻微的半透明效果，让背景图可以透出 */
  background-color: var( --SmartThemeChatTintColor);
  backdrop-filter: blur(2px);
  -webkit-backdrop-filter: blur(2px);
}

/* --- 2. 聊天气泡 --- */

/* ai头像和三元素在上且横向靠左排列 */
.mes[is_user="false"] {
    display: flex !important;
    flex-direction: column !important; 
    gap: 40px;
}

.mes[is_user="false"]  .mesAvatarWrapper {
    display: flex;
    align-items: center; /* 垂直居中 */
    gap: 7px;
}

/* user头像和三元素在上且横向靠右排列 */
.mes[is_user="true"] {
    display: flex !important;
    flex-direction: column !important; 
    align-items: flex-end !important; /* 横向顺序反转的关键 */
    gap: 40px; 
}

.mes[is_user="true"] .mesAvatarWrapper {
    display: flex; /* mesAvatarWrapper能横向排列的关键 */
    flex-direction: row-reverse; /* 头像反转到右边的关键 */
    align-items: center; 
    gap: 7px; 
}



/* 通用气泡样式 - 基础设置 */
.mes {
  border: 1px solid rgba(197, 216, 228, 0.5) !important;
  border-radius: 16px !important;
  margin: 5px 0;
  box-shadow: 0 4px 16px rgba(168, 196, 212, 0.15);
  backdrop-filter: blur(6px);
  -webkit-backdrop-filter: blur(6px);
  transition: all 0.3s ease;
}

/* 气泡悬停效果 */
.mes:hover {
  box-shadow: 0 6px 20px rgba(168, 196, 212, 0.25);
  border-color: rgba(168, 196, 212, 0.7) !important;
