custom_css:
/*
 * SillyTavern Custom UI Theme：Star Ticketbyleviathan
 * 感谢：络络老师的思维链美化
 * 标题及文本设计有参考电波系老师的腐败枯蝶，票根设计来自xhs張張休眠中，感谢~
 * 图源pin站，侵删，二改请标明出处，禁止二传
 */

@import url("https://fontsapi.zeoseven.com/578/main/result.css");

body {
    font-family: "AR PL New Sung";
    font-weight: normal;
}

:root {
    /* --- 核心配色方案 --- */
    --panel-bg-color-solid: #0a0a0a;
    --panel-bg-color-frost: rgba(16, 16, 16, 0.65);
    --border-color: #444444;
    --accent-color: #FFFFFF;
    --hover-bg-color: rgba(255, 255, 255, 0.1);
    --scrollbar-thumb-color: #555555;
    --scrollbar-track-color: #111111;
    --button-text-color: #FFFFFF;
    --glow-color: rgba(255, 255, 255, 0.7);

    --border-radius-main: 10px !important;
    --avatar-border-radius: 5px !important;
}

/* --- 1. 动画定义 --- */
@keyframes glow {
    0% { box-shadow: 0 0 3px 0px var(--glow-color); }
    50% { box-shadow: 0 0 8px 2px var(--glow-color); }
    100% { box-shadow: 0 0 3px 0px var(--glow-color); }
}

@keyframes fadeIn {
    from { opacity: 0; transform: scale(0.98); }
    to { opacity: 1; transform: scale(1); }
}

/* --- 2. 滚动条美化 --- */
::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}
::-webkit-scrollbar-track {
    background: var(--scrollbar-track-color);
}
::-webkit-scrollbar-thumb {
    background: var(--scrollbar-thumb-color);
    border: none;
}
::-webkit-scrollbar-thumb:hover {
    background: #777;
}

/* --- 3. 主要面板与抽屉 (清透纹理质感版) --- */
.drawer-content, 
#right-nav-panel,
#WorldInfo, 
#Backgrounds, 
#user-settings-block, 
#character_popup, 
#dialogue_popup,
.popup,
.modal-dialog,
#advanced_char_def,
.dropdown-menu,
#form_role_play,
#lorebook-popup
{
    background-image: url('https://files.catbox.moe/72zhgq.jpg') !important;
    background-color: rgba(20, 20, 22, 0.45) !important; 
    background-blend-mode: overlay;
    background-size: cover;
    background-position: center center;
    backdrop-filter: blur(6px) saturate(130%) !important; 
    -webkit-backdrop-filter: blur(6px) saturate(130%) !important;
    border: 1px solid rgba(255, 255, 255, 0.2) !important;
    border-radius: var(--border-radius-main);
    box-shadow: none !important;
}

#top-bar, #top-settings-holder {
    background: rgba(10, 10, 10, 0.5) !important;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--border-color);
}

/* --- 强制覆盖全局“无模糊”设定 --- */
body.no-blur .drawer-content,
body.no-blur #right-nav-panel,
body.no-blur #WorldInfo,
body.no-blur #Backgrounds,
body.no-blur #user-settings-block,
body.no-blur #character_popup,
body.no-blur #dialogue_popup,
body.no-blur .popup,
body.no-blur .modal-dialog,
body.no-blur #advanced_char_def,
body.no-blur .dropdown-menu,
body.no-blur #form_role_play,
body.no-blur #lorebook-popup 
{
    backdrop-filter: blur(8px) saturate(140%) !important;
    -webkit-backdrop-filter: blur(8px) saturate(140%) !important;
}

/* --- 4. 聊天区域 --- */
.mes {
    border-bottom: 1px solid var(--border-color) !important;
    padding: 12px 8px;
    border-radius: 0 !important;
    margin: 0;
    transition: background-color 0.3s ease;
}

.mes .ch_name .name_text {
    color: var(--accent-color);
    font-weight: 600;
}

.mes_text {
    font-size: 14px;
    line-height: 1.65;
}

.avatar, .avatar img {
    border-radius: var(--avatar-border-radius);
}

/* --- 5. 输入框与表单元素 (添加光晕效果) --- */
#send_textarea, 
.text_pole, 
textarea, 
input[type="text"], 
input[type="search"], 
input[type="number"],
select {
    background-color: var(--panel-bg-color-frost) !important;
    border: 1px solid var(--border-color) !important;
    border-radius: var(--border-radius-main);
    padding: 8px;
    box-shadow: none !important;
    transition: box-shadow 0.3s ease, border-color 0.3s ease;
}

#send_textarea:focus, 
.text_pole:focus, 
textarea:focus,
input:focus {
    outline: none !important;
    border-color: var(--accent-color) !important;
    animation: glow 1.5s infinite ease-in-out;
}

#send_form {
    background-color: var(--panel-bg-color-solid);
    border-top: 1px solid var(--border-color);
}

/* --- 6. 按钮与选择框 (全新径向光晕效果 - 修正版) --- */
/* (1) 基础样式 */
.menu_button,
#options_button,
.swipe_left,
.swipe_right,
.mes_edit_done,
.mes_edit_cancel,
button,
select {
    position: relative; 
    z-index: 1; 
    overflow: visible; 
    background-color: transparent !important;
    border-radius: var(--border-radius-main);
    border: 1px solid var(--border-color);
    transition: background-color 0.3s ease, transform 0.1s ease, border-color 0.3s ease;
}


/* (2) 定义光晕伪元素 (使用 ::after) */
.menu_button::after,
#options_button::after,
.swipe_left::after,
.swipe_right::after,
.mes_edit_done::after,
.mes_edit_cancel::after,
button::after,
select::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 180%;
    height: 0;
    padding-bottom: 180%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.35) 0%, rgba(255, 255, 255, 0) 65%);
    border-radius: 50%;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease-in-out;
}


/* (3) 悬停/聚焦时，显示光晕 (使用 ::after) */
.menu_button:hover::after,
.menu_button:focus-within::after,
#options_button:hover::after,
#options_button:focus-within::after,
.swipe_left:hover::after,
.swipe_left:focus-within::after,
.swipe_right:hover::after,
.swipe_right:focus-within::after,
.mes_edit_done:hover::after,
.mes_edit_done:focus-within::after,
.mes_edit_cancel:hover::after,
.mes_edit_cancel:focus-within::after,
button:hover::after,
button:focus-within::after,
select:hover::after,
select:focus-within::after {
    opacity: 1;
}


/* (4) 悬停/聚焦时的背景和边框变色 */
.menu_button:hover,
.menu_button:focus-within,
#options_button:hover,
#options_button:focus-within,
.swipe_left:hover,
.swipe_left:focus-within,
.swipe_right:hover,
.swipe_right:focus-within,
.mes_edit_done:hover,
.mes_edit_done:focus-within,
.mes_edit_cancel:hover,
.mes_edit_cancel:focus-within,
button:hover,
button:focus-within,
select:hover,
select:focus-within {
    border-color: var(--accent-color) !important;
    background-color: var(--hover-bg-color) !important;
    outline: none !important;
}

/* (5) 点击效果 */
.menu_button:active, #send_but:active {
    transform: scale(0.95);
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header, 
#user-settings-block h4, 
.standoutHeader {
    background-image: url('https://files.catbox.moe/y5r7wa.jpg');
    background-size: cover; 
    background-position: center; 
    background-repeat: np-repeat; 
    margin-bottom: 5px;
    border-radius: 10px;
    padding: 2px 5px;
    transition: all 250ms;
    border: none;
}

/* --- 7. 特定UI元素美化 --- */
#rm_print_characters_block .character_select {
    background-color: transparent;
    border-bottom: 1px solid var(--border-color);
    border-radius: 0;
    transition: background-color 0.2s ease;
    padding: 8px 5px;
}

#rm_print_characters_block .character_select:hover {
    background-color: var(--hover-bg-color);
}

.tag {
    background-color: #222;
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius-main);
    padding: 2px 6px;
}

h3, h4 {
    border-bottom: 1px solid var(--border-color);
    padding-bottom: 8px;
    background: linear-gradient(to bottom, rgba(255,255,255,0.05), transparent);
}

hr {
    border-color: var(--border-color);
}

/* --- 8. 消除不必要的背景和阴影 --- */
#sheld, #form_sheld {
    background: transparent !important;
    box-shadow: none !important;
}

.mes_buttons, .mes_edit_buttons {
    background-color: transparent !important;
}


/* --- 加载动画 (已替换为自定义GIF) --- */
#preloader {
    background-image: url('https://files.catbox.moe/3o7989.gif');
    background-size: 200px;
    background-repeat: no-repeat;
    background-position: center;
}

/* --- 隐藏默认的加载图标 --- */
#load-spinner {
    display: none !important;
}


/* --- 替换“停止生成”按钮的图标 --- */
.mes_stop .fa-circle-stop::before {
    content: '';
    opacity: 0;
}

.mes_stop::before {
    content: '';
    display: block;
    width: 35px; 
    height: 35px;
    background-image: url('https://files.catbox.moe/cxnos2.gif');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


/* --- 替换“发送”按钮的图标 (修正版) --- */
#send_but {
    position: relative;
    font-size: 0 !important; 
}

#send_but::before {
    content: '';
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    background-image: url('https://files.catbox.moe/f8r8ag.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


/* --- .fa-bars 图标替换 --- */
.fa-solid::before, 
.fa-regular::before {
    vertical-align: middle;
}


.fa-bars:before, 
.fa-navicon:before {
    content: ''; 

    background-image: url('https://files.catbox.moe/rsn1en.png');
    width: 1.2em; 
    height: 1.2em; 

    background-size: contain; 
    background-repeat: no-repeat; 
    background-position: center; 

    display: inline-block;
}

/* --- .fa-magic-wand-sparkles 图标替换 --- */
.fa-magic-wand-sparkles:before, 
.fa-wand-magic-sparkles:before {
    content: '';

    background-image: url('https://files.catbox.moe/8gpo9j.png');
    width: 1.2em;
    height: 1.2em;

    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;

    display: inline-block;
    vertical-align: middle; 
}


/* --- 自定义图标替换 --- */
.drawer-icon {
    color: #ffffff !important;
    line-height: calc(var(--mainFontSize) * 2.1);
    width: calc(var(--mainFontSize) * 2.1);
    height: calc(var(--mainFontSize) * 2.1);
    display: inline-block;
    vertical-align: middle;
    position: relative;
}




.drawer-icon {
    opacity: 1 !important;
    font-size: calc(var(--icon-size) * 1.1);
    text-shadow: none !important;
    transition: color 0.2s ease;
}
.drawer:hover .drawer-icon {
    color: var(--uk-dark-brown);
}
.drawer:focus-within .drawer-icon {
    color: var(--text-on-dark-bg);
}
/* --- 9. Ticket-style Chat Messages (票根样式聊天消息) --- */

/* 9.1. 主消息容器设置 */
.mes {
    position: relative; 
    margin-bottom: 12px;
    padding: 0;
    background-color: transparent !important;
    border: none !important;
    padding-bottom: 30px;
    background-image: url('https://files.catbox.moe/237fgq.png');
    background-repeat: no-repeat;
    background-position: bottom center;
    background-size: 100% auto;
}

/* 9.1b. 为内容块设置定位锚点 */
.mes .mes_block {
    position: relative !important;
}

/* 9.2. 创建票根背景 */
.mes::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 290px;
    background-image: url('https://files.catbox.moe/8jxssw.png');
    background-size: 100% 100%;
    background-repeat: no-repeat;
    z-index: 0;
}

/* 9.2b. 为正文区创建独立的背景板 */
.mes::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 235px;
    bottom: 30px; 

    background-color: var(--panel-bg-color-solid);
    z-index: -1; 
    border-top: none;
}

/* 9.3. 定位头像 (柔和辉光版) */
.mes .avatar {
    position: absolute;
    top: 75px;
    left: 75px;
    width: 100px !important;
    height: 100px !important;
    border-radius: none;
    z-index: 1;
    object-fit: cover;

    border: none;
    box-shadow: 0 0 15px 2px rgba(255, 255, 255, 0.4);
}

.mes .avatar img {
    width: 100%;
    height: 100%;
}


/* 9.4. 定位角色名和时间戳 (最终稳定版) */
/* (1) 将 ch_name 容器相对于 mes_block 定位 */
.mes .ch_name {
    position: absolute !important;
    top: 50px; 
    left: 240px; 
    z-index: 1;
    padding: 0 !important;
    margin: 0 !important;
    background: none !important;
}

/* (2) 微调内部元素的相对位置 */
.mes .ch_name .name_text {
    position: static !important;
    padding-left: 20px !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    background: none !important;
    font-family: "Times New Roman", "Noto Serif CJK", serif;
    font-size: 20px !important;
    font-weight: bold;
    color: var(--accent-color) !important;
}

.mes .ch_name .timestamp {
    position: static !important;
    margin-top: 35px;
    padding: 0 !important;
    background: none !important;
    font-family: "Times New Roman", "Noto Serif CJK", serif;
    font-size: 16px !important;
    font-weight: bold;
    color: var(--accent-color) !important;
}

/* --- 隐藏指定的SVG图标 --- */
.icon-svg {
    display: none !important;
}

/* 9.5. 调整消息正文的间距 (已修正) */
.mes .mes_text {
    margin-top: 310px;
    min-height: 0;
    padding: 10px;
    padding-top: 20px;
    width: 99%; 
    margin-left: auto; 
    margin-right: auto; 
    margin-bottom: 30px;
    font-size: 16px;
    border: 2px dashed rgba(128, 128, 128, 0.5);
    border-radius: 15px;
    background-color: var(--panel-bg-color-solid); 
}


/* --- 9.8. (新增) 定位右上角元数据 --- */
/* (1) 为这三个元素设置通用的定位和外观 */
.mes .mes_timer,
.mes .mesIDDisplay,
.mes .tokenCounterDisplay {
    position: absolute !important;
    z-index: 1;
    font-family: "Times New Roman", "Noto Serif CJK", serif; 
    font-size: 12px !important;
    color: rgba(255, 255, 255, 0.65) !important;
    width: 150px;
    text-align: right;
    background: none;
    padding: 0;
}

/* (2) 分别设置每个元素的垂直(top)和水平(right)位置 */
.mes .mesIDDisplay {
    top: 80px;
    right: 100px;
}

.mes .tokenCounterDisplay {
    top: 100px; 
    right: 100px;
}

.mes .mes_timer {
    top: 120px;
    right: 100px;
}


/* --- 9.9. (最终版) 滑动按钮定位在文本框内部底端 --- */
/* (1) 为左右滑动按钮设置统一的绝对定位样式 */
.mes > .swipe_left, 
.mes .swipe_right {
    position: absolute !important;
    z-index: 10; 

    bottom: 30px; 

    width: 28px;
    height: 28px;
    font-size: 20px;
    opacity: 0.4;
    cursor: pointer;
    display: flex !important;
    align-items: center;
    justify-content: center;
    background-color: rgba(0, 0, 0, 0.2);
    border-radius: 50%;
    transition: all 0.2s ease;
}

.mes > .swipe_left:hover, 
.mes .swipe_right:hover {
    opacity: 1;
    background-color: rgba(0, 0, 0, 0.4);
}

/* (2) 分别设置左右按钮的水平位置 */
.mes > .swipe_left {
    left: 1%; 
}

.mes .swipe_right {
    right: 1%;
}

/* (3) 修正滑动计数器的位置 */
.mes .swipes-counter {
    position: absolute;
    bottom: 10px;
    right: 10px;
    font-size: 12px;
    color: var(--text-color);
    opacity: 0.3;
    z-index: 10;
}

/* --- 11. 正文元素美化 (柔和光影最终版) --- */
/* ==== 标题 ==== */
.mes_text h1 {
    font-size: 1.8em;
    font-weight: 600;
    text-align: center;
    color: #FFFFFF;
    background-color: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(8px) saturate(120%);
    -webkit-backdrop-filter: blur(8px) saturate(120%);
    padding: 12px 25px;
    margin: 30px auto;
    width: fit-content;
    max-width: 80%;
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 0 25px rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
}
.mes_text h1:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 35px rgba(255, 255, 255, 0.15);
}

.mes_text h2 {
    font-size: 1.5em;
    padding-bottom: 10px;
    margin: 25px 0;
    border-image: linear-gradient(to right, var(--accent-color), transparent 80%);
    border-image-slice: 1;
    border-bottom: 2px solid;
    text-shadow: 0 0 5px rgba(255, 255, 255, 0.5);
}

.mes_text h3 {
    font-size: 1.2em;
    display: flex;
    align-items: center;
    margin: 20px 0 15px 0;
}
.mes_text h3::before {
    content: '';
    width: 12px;
    height: 12px;
    margin-right: 12px;
    border-radius: 50%;
    background-color: var(--accent-color);
    box-shadow: 0 0 10px 2px var(--accent-color);
    transition: all 0.3s ease;
}
.mes_text h3:hover::before {
    transform: scale(1.2);
    box-shadow: 0 0 15px 4px var(--accent-color);
}


/* ==== 引用块 (高斯模糊版) ==== */
.mes_text blockquote {
    margin: 20px 5px;
    padding: 18px 20px 18px 25px;
    background-color: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-left: 3px solid var(--accent-color);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    color: rgba(255, 255, 255, 0.85);
    font-style: italic;
}
.mes_text blockquote p {
    margin: 0;
    padding: 5px 0;
}


/* ==== 行内样式 ==== */
.mes_text strong {
    font-weight: 700;
    color: #FFF;
    text-shadow: 0 0 3px rgba(255, 255, 255, 0.4);
}

.mes_text q {
    quotes: none;
    font-style: normal;
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 5px;
    padding: 2px 6px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.2);
    color: #FFFFFF;
    font-weight: 500;
}

.mes_text del {
    text-decoration: none;
    background: linear-gradient(to right, transparent, rgba(255,82,82,0.5), transparent);
    padding: 1px 0;
    color: rgba(255, 255, 255, 0.6);
}
.mes_text u {
    text-decoration: none;
    background-image: linear-gradient(to right, #a855f7, #f472b6);
    background-repeat: no-repeat;
    background-position: bottom left;
    background-size: 0% 2px;
    transition: background-size 0.4s ease;
}
.mes_text u:hover {
    background-size: 100% 2px;
}


/* ==== 代码块 ==== */
.mes_text code {
    font-family: "Cascadia Code", "Fira Code", monospace;
    font-size: 0.9em;
    color: #e5e7eb;
    background-color: #374151;
    padding: 4px 10px;
    margin: 0 3px;
    border-radius: 12px;
}

.mes_text pre {
    font-family: "Cascadia Code", "Fira Code", monospace;
    font-size: 0.95em;
    background-color: rgba(30, 30, 32, 0.75) !important;
    backdrop-filter: blur(10px) saturate(150%) !important;
    -webkit-backdrop-filter: blur(10px) saturate(150%) !important;
    border: 1px solid rgba(255, 255, 255, 0.2) !important;
    border-radius: 12px !important;
    padding: 18px;
    margin: 20px 0;
    overflow-x: auto;
    box-shadow: 0 4px 20px rgba(0,0,0,0.3);
}
.mes_text pre code, .mes_text .hljs {
    background: none !important;
    padding: 0;
}

.mes_text pre::-webkit-scrollbar {
    height: 8px;
    background-color: transparent;
}
.mes_text pre::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 4px;
}
.mes_text pre::-webkit-scrollbar-thumb:hover {
    background-color: rgba(255, 255, 255, 0.4);
}


/* --- 12. 思维链模块美化 (最终修正版) --- */
/* --- 主容器 --- */
details.mes_reasoning_details {
    margin: 20px auto;
    width: 95%;

    background-image: 
        linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)),
        url('https://files.catbox.moe/gtsxqd.jpg');
    background-size: cover;
    background-position: center;

    backdrop-filter: blur(8px) saturate(120%);
    -webkit-backdrop-filter: blur(8px) saturate(120%);

    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    box-shadow: 0 5px 30px rgba(0,0,0,0.35);
    transition: all 0.3s ease;
    overflow: hidden;
}

/* --- 标题栏 --- */
summary.mes_reasoning_summary {
    display: flex;
    align-items: center;
    padding: 13px 15px !important;
    cursor: pointer;
    list-style: none;
    outline: none;
    position: relative;
    background: transparent !important;
    transition: background-color 0.2s ease;
}
summary.mes_reasoning_summary:hover {
    background-color: rgba(255, 255, 255, 0.1) !important;
}
summary.mes_reasoning_summary::-webkit-details-marker,
summary.mes_reasoning_summary::marker { display: none; }


details.mes_reasoning_details summary.mes_reasoning_summary * {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
}


/* --- 标题与图标 --- */
.mes_reasoning_header_title {
    font-weight: 600;
    color: #FFFFFF !important;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}
.mes_reasoning_header_title::before {
    content: "✧";
    margin-right: 10px;
    color: #FFFFFF;
    text-shadow: 0 0 5px #FFFFFF;
}
.mes_reasoning_arrow {
    margin-left: auto;
    font-size: 1.2em;
    transition: transform 0.3s ease;
    color: rgba(255, 255, 255, 0.7);
}
details[open] summary .mes_reasoning_arrow {
    transform: rotate(90deg);
}
.mes_reasoning_actions .mes_button {
    color: rgba(255, 255, 255, 0.7) !important;
}
.mes_reasoning_actions .mes_button:hover {
    color: #FFFFFF !important;
    transform: scale(1.1);
}


/* --- 内容区 --- */
.mes_reasoning {
    padding: 18px;
    color: #EAEAEA;
    text-shadow: 0 1px 2px rgba(0,0,0,0.4);
    line-height: 1.7;
    overflow-y: auto;
    max-height: 350px;
    border-top: 1px solid rgba(255, 255, 255, 0.2);
}
details[open] .mes_reasoning {
    animation: mes_reasoning_smooth_expand 0.4s ease-out forwards;
}
@keyframes mes_reasoning_smooth_expand {
    from { opacity: 0; transform: translateY(-10px); }
    to   { opacity: 1; transform: translateY(0); }
}

.mes_reasoning::-webkit-scrollbar { width: 6px; }
.mes_reasoning::-webkit-scrollbar-track { background: transparent; }
.mes_reasoning::-webkit-scrollbar-thumb { background: var(--scrollbar-thumb-color); }
.mes_reasoning::-webkit-scrollbar-thumb:hover { background: #777; }


/* --- 状态动画 --- */
summary.mes_reasoning_summary::after {
    content: "";
    position: absolute;
    top: 0; left: 0; width: 100%; height: 100%;
    pointer-events: none;
    opacity: 0;
}
details[data-state="thinking"] summary.mes_reasoning_summary::after {
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    animation: nightfall_shimmer 2.5s infinite;
}
@keyframes nightfall_shimmer {
    0%   { transform: translateX(-100%); opacity: 0; }
    50%  { opacity: 1; }
    100% { transform: translateX(100%); opacity: 0; }
}
details[data-state="done"] summary.mes_reasoning_summary::after {
    background: radial-gradient(circle at 50% 50%, rgba(255,255,255,0.25), transparent 70%);
    animation: nightfall_pulse 0.8s ease-out;
}
@keyframes nightfall_pulse {
    from { transform: scale(0.5); opacity: 1; }
    to   { transform: scale(2); opacity: 0; }
}

/* --- 12a. (修正) 强制移除思维链标题栏内部的“框中框” --- */
details.mes_reasoning_details summary.mes_reasoning_summary * {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
}

/* --- 思维链组件对齐修正 --- */
details.mes_reasoning_details {
    margin-top: 300px !important;
}
details.mes_reasoning_details {
    margin-bottom: -300px !important;
}


