/* --- 云破月。感谢代码楼里老师们的代码https://discord.com/channels/1291925535324110879/1380392618336194702
感谢尾老师，类名，输入框伪提示词来自尾老师，hy老师的去除思维链箭头，na老师的悬浮底栏和思维链文字自定义，楼层信息自定义，12的去思维链背景色。感谢NA老师的去阴影，感谢咩野老师的去除全局阴影。 --- */

/* --- 1. 字体导入 --- */
@import url('https://fonts.googleapis.com/css2?family=EB+Garamond&display=swap');
@import url("https://fontsapi.zeoseven.com/440/main/result.css"); /* 荟圆体-方黑 - 全局 */
@import url("https://fontsapi.zeoseven.com/501/main/result.css"); /* 平方手书体 - 标题 */
@import url("https://fontsapi.zeoseven.com/445/main/result.css"); /* 云峰静龙行书 - 引用/斜体 */
@import url("https://fontsapi.zeoseven.com/796/main/result.css"); /* Hangeuljaemin4.0 - 代码 */

/* --- 2. 核心颜色与样式变量 --- */
:root {
    --color-deep-red: #730100;
    --color-sky-blue: #4B81AB;
    --color-deep-blue: #04256C;
    --color-moon-white: #DDE6ED;

    --theme-bg: #0A1931;
    --panel-bg-color: rgba(4, 22, 58, 0.85);
    --panel-bg-hover: rgba(10, 30, 68, 0.95);
    --card-bg: rgba(15, 35, 70, 0.9);
    --input-bg: rgba(10, 25, 49, 0.9); 
    --text-main: var(--color-moon-white);
    --text-subtle: #9DB2BF;
    --border-color: rgba(75, 129, 171, 0.4);
    --accent-color: var(--color-sky-blue);
    --selected-color: #AEC8E0;

    --font-english: "EB Garamond", serif;
    --font-main: "Huiwen-Fangsong", var(--font-english);
    --font-quote: "YFJLXS", var(--font-english);
    --font-code: "Hangeuljaemin4.0", monospace;
    --font-title: "平方手书体", var(--font-english);

    --border-radius: 16px; /* 保留变量，但消息框将覆盖为0 */
}

/* --- 3. 全局样式 --- */
body { background-color: var(--theme-bg) !important; color: var(--text-main); font-family: var(--font-main); }
#sheld, #character_view { background: transparent !important; margin: 0 auto !important; left: 0; right: 0; border: none !important; box-shadow: none !important; overflow: visible !important; }
#chat { background: transparent !important; align-items: center !important; }

h1, h2, h3, h4, .standoutHeader, .inline-drawer-header, .title_restorable { font-family: var(--font-title); }

/* --- 4. 顶部导航栏 --- */
#top-settings-holder {
    background-color: transparent;
    box-shadow: none;
    border: none;
    backdrop-filter: blur(8px);
    background-image: linear-gradient(rgba(75, 129, 171, 0.4), rgba(75, 129, 171, 0.4)), url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/XKXL/1279X1694/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D2983606182557451172%26skey%3D%40crypt_5a237c93_0e520476b8d0655e4f9e944a178943c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp');
    background-size: cover;
    background-position: center;
    position: relative;
}

/* 挂饰 */
#top-settings-holder::after {
    content: '';
    display: block;
    width: 120px;
    height: 180px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/T4lO/2112X3168/%E5%BA%97%E9%93%BA%3B%E5%B0%8F%E6%9C%A8%E7%BE%8E%E5%B7%A5%E7%B4%A0%E6%9D%90_%2839%29.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: -5px;
    left: 1px;
    opacity: 0.9;
    z-index: 10;
    pointer-events: none;
}


.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }
.drawer-icon.fa-fw { width: 35px; height: 40px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.3s ease; }
.drawer-icon.fa-fw:hover { transform: scale(1.1); }

#ai-config-button .drawer-icon, .drawer-icon.fa-sliders { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#sys-settings-button .drawer-icon, .drawer-icon.fa-plug { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#advanced-formatting-button .drawer-icon, .drawer-icon.fa-font { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#WI-SP-button .drawer-icon, .drawer-icon.fa-book-atlas { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#user-settings-button .drawer-icon, .drawer-icon.fa-user { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#bg_settings_button .drawer-icon, .drawer-icon.fa-image { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#extensions-settings-button .drawer-icon, .drawer-icon.fa-cubes { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#persona-management-button .drawer-icon, .drawer-icon.fa-face-smile { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#character_popup_button .drawer-icon, .drawer-icon.fa-address-card { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#table_database_settings_drawer .drawer-icon, .drawer-icon.fa-bars-progress { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
.drawer-content { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/xFgQ/3840X1961/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D3588069001168051114%26skey%3D%40crypt_5a237c93_0e520476b8d0655e4f9e944a178943c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); background-size: cover; background-position: center; border: none; border-radius: var(--border-radius); box-shadow: none; }

/* --- 5. 底部输入栏 --- */
#send_textarea { clip-path: none !important; background-color: transparent !important; border: none !important; }
#send_form { 
    border-radius: 25px;
    background-image: none !important;
    background-color: var(--input-bg) !important;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(75, 129, 171, 0.3) !important;
    position: relative;
    transition: all 0.5s ease;
    padding: 10px 15px;
}
#send_form:hover { 
    transform: translateY(-5px);
    background-color: rgba(15, 35, 70, 0.95) !important;
    border-color: rgba(75, 129, 171, 0.6) !important;
}
#rightSendForm, #leftSendForm { gap: 5px; }
#send_form:has(#send_textarea:focus-visible) { outline: none; }

#options_button::before, #send_but::before, #send_form #extensionsMenuButton::before, #ai_retry_button::before, #auto_completion_button::before { display: none !important; }
#options_button, #send_but, #send_form #extensionsMenuButton, #ai_retry_button, #auto_completion_button { width: 32px; height: 32px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.2s ease; font-size: 0; border: none; background-color: transparent; }
#options_button:hover, #send_but:hover, #send_form #extensionsMenuButton:hover, #ai_retry_button:hover, #auto_completion_button:hover { transform: scale(1.1); }
#options_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#send_form #extensionsMenuButton { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }
#send_but { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/kW5A/2048X2048/2026-01-14_142938.png/webp'); }
#auto_completion_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp'); }

#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "月色入怀，笔墨生香...";
    color: var(--text-subtle);
    position: absolute;
    top: 50%; left: 50%;
    transform: translate(-50%, -50%);
    font-family: var(--font-main);
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
    opacity: 0.8;
}

/* --- 6. 核心：消息布局与样式 --- */
.mes { display: flex; flex-direction: column; align-items: stretch; gap: 0; padding: 0 !important; margin: 30px auto !important; width: 90%; max-width: 700px; background: transparent !important; border: none !important; box-shadow: none !important; overflow: visible !important; }

.mes .mesAvatarWrapper {
    display: grid;
    grid-template-columns: 100px 1fr;
    gap: 0px 20px;
    align-items: center;
    padding: 20px 30px;
    border-radius: 0;
    width: 100%;
    max-width: none;
    background-size: cover;
    background-position: center;
    position: relative;
    z-index: 5;
    min-height: 160px;
}

.mes[is_user="true"] .mesAvatarWrapper { 
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/wufl/4578X2298/2026-01-14_173340.png/webp');
    grid-template-columns: 1fr 100px;
}
.mes[is_user="false"] .mesAvatarWrapper { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/1uEa/4578X2298/2026-01-14_143605%281%29.png/webp'); }

.mes .mesAvatarWrapper::before { content: ''; position: absolute; top: 0; left: 0; right: 0; bottom: 0; background-color: transparent; z-index: 0; border-radius: 0; }
.mes .mesAvatarWrapper > * { position: relative; z-index: 1; }

.mes .avatar { grid-row: 1 / 3; grid-column: 1 / 2; width: 85px !important; height: 85px !important; flex-shrink: 0; display: block !important; position: relative; transform: rotate(-4deg); transition: transform 0.4s ease; }
.mes[is_user="true"] .avatar { grid-column: 2 / 3; }

.mes .avatar:hover { transform: rotate(0deg); }
.mes .avatar img { 
    border-radius: 0 !important;
    border: none !important; 
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.mes .avatar::before {
    content: '';
    position: absolute;
    top: -4px; left: -4px; right: -4px; bottom: -4px;
    border: 3px solid rgba(221, 230, 237, 0.85);
    border-radius: 0;
}

.mes .mesAvatarWrapper > .ch_name { grid-row: 1 / 2; grid-column: 2 / 3; display: block !important; font-weight: bold; font-size: 1.2em; color: var(--text-main); margin-bottom: 5px; font-family: var(--font-title); text-shadow: 0 1px 3px rgba(0,0,0,0.5); }
.mes .mesAvatarWrapper > .mes_timer, .mes .mesAvatarWrapper > .mesIDDisplay, .mes .mesAvatarWrapper > .tokenCounterDisplay { display: none !important; }

.mes[is_user="true"] .mesAvatarWrapper > .ch_name { grid-column: 1 / 2; text-align: right; }

.mes .mesAvatarWrapper::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 10px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/Z6Ax/469X245/2026-01-14_184859.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 6;
    pointer-events: none;
}

.mes .mes_block { 
    background-color: rgba(10, 25, 49, 0.9);
    backdrop-filter: blur(5px);
    border: 1px solid var(--border-color);
    border-radius: 0;
    padding: 25px 30px;
    margin-top: 0;
    position: relative;
    overflow: visible !important;
}

.mes .mes_text, .mes_reasoning { background: transparent !important; border: none !important; padding: 0 !important; margin: 0 !important; backdrop-filter: none !important; }

#chat .mes .mes_reasoning_header { background: transparent !important; border: none !important; box-shadow: none !important; border-radius: 0; backdrop-filter: none !important; -webkit-backdrop-filter: none !important; padding: 0 !important; margin-bottom: 10px !important; color: #C1B8B5 !important; font-size: 0.9em !important; display: inline-flex !important; align-items: center !important; transition: none !important; }
.mes_reasoning_arrow { display: none !important; }

.mes_reasoning_header h4, .mes_reasoning_header_title { font-size: 0 !important; visibility: hidden; }
.mes_reasoning_header h4::before, .mes_reasoning_header_title::before { content: "云开雾散，灵犀一点"; font-family: var(--font-title); font-size: 14px !important; color: var(--accent-color); visibility: visible; }

.mes_reasoning { border-left: 3px solid var(--accent-color) !important; padding-left: 15px !important; margin-bottom: 20px !important; }

.mes .mes_text { line-height: 1.9; color: var(--text-main); }

/* --- 7. 文本样式 & 消息框装饰 --- */
.mes_text strong, .mes_text b { font-family: var(--font-title); color: var(--selected-color); font-weight: bold; }
.mes_text em, .mes_text i { font-family: var(--font-quote); font-style: normal !important; color: var(--accent-color); position: relative; padding: 0 0.5em; }
.mes_text em::before, .mes_text i::before { content: '「'; position: absolute; left: 0; top: 0; }
.mes_text em::after, .mes_text i::after { content: '」'; position: absolute; right: 0; top: 0; }
.mes_text blockquote { background-color: rgba(4, 37, 108, 0.4); border-left: 4px solid var(--accent-color); padding: 10px 15px; border-radius: 0 8px 8px 0; margin: 10px 0; }
.mes_text code { font-family: var(--font-code); background-color: rgba(4, 37, 108, 0.5) !important; color: var(--text-main) !important; padding: 2px 6px; border-radius: 6px; border: 1px solid var(--border-color); }



.mes .mes_block > *:not(.mes_buttons) { position: relative; z-index: 1; }

.mes[is_user="true"] .mes_block::before { display: none; }
.mes .mes_block::after {
    content: ''; display: block; position: absolute;
    top: -40px; right: -25px;
    width: 100px; height: 150px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/2S0X/2112X3168/%E5%BA%97%E9%93%BA%3B%E5%B0%8F%E6%9C%A8%E7%BE%8E%E5%B7%A5%E7%B4%A0%E6%9D%90_%2825%29.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 10;
}

/* --- 8. 元素重定位与自定义文本 (最终修复) --- */
/* 将整个名字容器提取到消息框上方 */
.mes_block > .ch_name {
    position: absolute !important;
    top: -35px !important; /* ★ 关键修改：向上移动35px，彻底离开消息内容区，避免被CoT思维链遮挡 */
    left: 10px;
    right: 10px;
    height: 30px;
    display: flex !important;
    align-items: center;
    justify-content: flex-end; /* 默认靠右（机器人） */
    gap: 10px;
    z-index: 100 !important; /* ★ 确保层级高于一切背景和思维链 */
    pointer-events: auto !important;
    background: transparent !important;
}

/* 用户侧靠左 */
.mes[is_user="true"] .mes_block > .ch_name {
    flex-direction: row-reverse; /* 反转排列，让按钮在最左边 */
    justify-content: flex-end;
}

/* 恢复名字和时间戳显示，并调整样式使其融入顶部栏 */
.mes_block .ch_name .name_text,
.mes_block .ch_name .timestamp {
    display: inline-block !important;
    font-size: 12px;
    color: rgba(221, 230, 237, 0.5);
    text-shadow: 0 1px 2px rgba(0,0,0,0.8);
    white-space: nowrap;
}

/* 强制显示操作按钮（三点和小铅笔） */
.mes_block .mes_buttons {
    display: flex !important;
    align-items: center;
    gap: 5px;
    opacity: 0.8;
    transition: all 0.2s ease;
}

/* 悬停时加亮 */
.mes_block .mes_buttons:hover {
    opacity: 1;
    transform: scale(1.05);
}

/* 编辑菜单（保存/删除等）保持默认，不强制显示，也不强制隐藏 */
.mes_block .mes_edit_buttons {
    /* 空置，由系统控制显示隐藏 */
}

#chat { counter-reset: ID-counter 0; }
.mes { counter-increment: ID-counter; }

.mes::after {
    content: '云笺 第 ' counter(ID-counter) ' 章 · 月下独酌 · 思绪成章';
    display: block;
    text-align: center;
    margin: 15px auto 0;
    padding: 5px 15px;
    background-color: var(--card-bg);
    border: 1px solid var(--border-color);
    border-radius: 0;
    font-size: 12px;
    color: var(--text-subtle);
    font-family: var(--font-main);
    max-width: 80%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}


/* --- 9. 全局UI颜色与样式统一 --- */
* { box-shadow: none !important; }
.standoutHeader, .inline-drawer-header, .title_restorable, #extensions_settings2 > div, #extensions_settings > div, .openai_logit_bias_preset_form, .range-block-title.justifyLeft.standoutHeader, details > summary, #power-user-options-block h4, #InstructSettingsColumn h4, #SystemPromptColumn h4, #ContextSettings h4, .persona_management_global_settings h4, #character_popup h4, #AdvancedFormatting h4, #rm_group_chats_block h4, div[name*='HandlingToggles'] h4, div[name*='MiscellaneousToggles'] h4 {
    background-image: none !important; background: var(--card-bg) !important; border: none !important; border-radius: var(--border-radius) !important; padding: 8px 12px !important; margin-bottom: 8px !important; color: var(--text-main) !important; }
input[type="text"], input[type="number"], input[type="search"], textarea, select, .text_pole {
    background-color: var(--input-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; border-radius: 8px !important; }
input:focus, textarea:focus, select:focus { border-color: var(--accent-color) !important; }
.menu_button { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; transition: all 0.2s ease; border: none !important; }
.menu_button:hover { background-color: var(--accent-color) !important; border-color: var(--accent-color) !important; color: white !important; }
input[type="checkbox"] { filter: none !important; border: 1px solid var(--border-color) !important; }
input[type="checkbox"]:checked::before { box-shadow: inset 1em 1em var(--accent-color) !important; }
.prompt_order > div { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; }

/* 弹窗 */
dialog.popup { position: relative; overflow: hidden; background-color: transparent !important; border: none !important; }
dialog.popup .popup-body, #dialogue_popup {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/xFgQ/3840X1961/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D3588069001168051114%26skey%3D%40crypt_5a237c93_0e520476b8d0655e4f9e944a178943c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp') !important;
    background-size: cover !important;
    background-position: center !important;
    border: none !important;
    border-radius: var(--border-radius) !important;
    position: relative;
}
dialog.popup::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: rgba(10, 25, 49, 0.8);
    border-radius: var(--border-radius);
    z-index: 0;
}
dialog.popup .popup-body, #dialogue_popup { background-color: transparent !important; z-index: 1; }

.menu_button.active, .menu_button.world_set, .menu_button.fav_on { background-color: var(--selected-color) !important; border-color: var(--selected-color) !important; color: var(--color-deep-blue) !important; font-weight: bold; }
.menu_button.active:hover, .menu_button.world_set:hover, .menu_button.fav_on:hover { filter: brightness(1.05); }

#left-nav-panel, #left-nav-panel * { color: var(--text-main) !important; text-shadow: none !important; }
#left-nav-panel .standoutHeader, #left-nav-panel .menu_button, #left-nav-panel select { background-color: var(--card-bg) !important; color: var(--text-main) !important; border: 1px solid var(--border-color) !important; }
#left-nav-panel .menu_button:hover { background-color: var(--accent-color) !important; color: white !important; }
#left-nav-panel .menu_button.selected { background-color: var(--selected-color) !important; color: var(--color-deep-blue) !important; border-color: var(--accent-color) !important; }

.select2-container--default .select2-selection--multiple .select2-selection__choice { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove { color: var(--text-subtle) !important; transition: color 0.2s ease; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove:hover { color: var(--text-main) !important; }
.select2-dropdown { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; }

input[type="range"]::-webkit-slider-thumb { -webkit-appearance: none !important; appearance: none !important; width: 28px !important; height: 28px !important; background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp') !important; background-size: contain !important; background-color: transparent !important; border: none !important; cursor: pointer !important; }
input[type="range"]::-moz-range-thumb { width: 28px !important; height: 28px !important; background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/P1lx/2048X2048/2026-01-14_142901.png/webp') !important; background-size: contain !important; background-color: transparent !important; border: none !important; cursor: pointer !important; }
.neo-range-slider { background: rgba(75, 129, 171, 0.5) !important; }
