/* ==========================================================
   名称：复古MacOS UI
   样式作者：@Junezz
   版本：v1.0
   发布于：类脑 Discord 社区

   本样式仅供个人使用与参考，禁止任何形式的商用。
   可二改不可二传。请保留本注释和原作者署名。
   ========================================================== */

/* 字体 */
@import url("https://fontsapi.zeoseven.com/532/main/result.css");

body {
    font-family: "NanoDyongChyangSong CN 24";
    font-weight: normal;
}

code, textarea, #send_textarea, input, select, button, .text_pole, .font-family-reset {
  font-family: inherit;
}

  :root {
    --mainFontSize: calc(var(--fontScale)* 16px); /* 主字体大小 */
    --avatar-base-width: 48px; /* 头像宽度 */
    --avatar-base-height: 48px; /* 头像高度 */
    --topbarIconSize: calc(var(--mainFontSize)* 1.3); /* 顶栏图标大小 */
    --genericRadius: 0px; /* 通用圆角 */
    --warning: #ce3636;
    --crimson70a: #acacac63;
    --crimson-hover: color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 90%) !important;
    --standardIconSize: calc(var(--mainFontSize)* 1);
    --bottomFormIconSize: calc(var(--mainFontSize)* 1.5);
    --bottomFormBlockPadding: calc(var(--mainFontSize) / 1.5);
    --scrollBarWidth: 3px;
    --pagePadding: calc(var(--scrollBarWidth) + 9px);
    --genericLineHeight: calc(var(--mainFontSize)* 1.9); /* 通用行高 */

    /* ==== 所有颜色定义 ==== */
    --utilityColor: var(--black50a); /* 说明文字 */
    --navBackgroundColor: var(--SmartThemeBlurTintColor); /* 顶栏和底栏背景颜色 */
    --buttonFill: rgb(255, 255, 255); /* 按钮背景颜色 */
    --menuBackgroundColor: var(--navBackgroundColor); /* 菜单背景颜色 */
    --accentColor: var(--SmartThemeUnderlineColor); /* 装饰色1 思维链，文字装饰，段落装饰等 */
    --accentColorOverlay: color-mix(in srgb, var(--SmartThemeBorderColor), transparent 95%); /* 装饰色2半透明 消息里头像和名字的背景颜色 */
    --scrollBarColor: var(--SmartThemeUnderlineColor); /* 滚动条颜色 */
    --boxShadowSubtle: 2px 2px 0px var(--SmartThemeBorderColor); /* 阴影颜色 */
    --boxShadowSubtleAlt: 2px 2px 0px var(--SmartThemeUnderlineColor); /* 阴影颜色 */
  }

  /* 聊天框背景 */
  #chat {
    padding: var( --pagePadding) 9px var( --pagePadding) var( --pagePadding);
    overflow-y: scroll;
    border:1px solid var(--SmartThemeBorderColor);
    border-top: none;
    border-bottom: none;
  }

  #form_sheld {
    margin-top: 0px !important;
  }

  #top-bar {
    height: var(--topBarBlockSize);
  }

  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4,
  .standoutHeader,
  .userSettingsInnerExpandable {
    border: transparent !important;
  }


  .drawer-content, #right-nav-panel, #left-nav-panel {
    background-color: var(--menuBackgroundColor) !important;
    border-radius: var(--genericRadius);
    padding: 8px;
    border-top: 0;
    border-right: 1px solid var(--SmartThemeBorderColor);
    border-bottom: 1px solid var(--SmartThemeBorderColor);
    border-left: 1px solid var(--SmartThemeBorderColor);
    padding-top: 8px;
  }

  #right-nav-panel {
    border-top: 1px solid var(--SmartThemeBorderColor);
    border-left: none;
  }

  #left-nav-panel {
    border-top: 1px solid var(--SmartThemeBorderColor);
    border-right: none;
  }

  /* Icon Sizes & Colors */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid,
  #rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div,
  #completion_prompt_manager .completion_prompt_manager_footer a,
  .fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid, .fab, .far, .fas {
    font-size: var(--standardIconSize);
    color: var(--SmartThemeUnderlineColor);
  }

  /* 预设icons */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
    color: var(--SmartThemeBodyColor)  !important;
  }

    /* 按钮圆角统一 */
  #character_sort_order,
  #form_character_search_form .menu_button, 
  #GroupFavDelOkBack .menu_button,
  .regex-script-label,
  .menu_button {
    border-radius: var(--genericRadius) !important;
  }

  .regex-script-label {
    border: none !important;
  }

  /* 对话框icons */
  .mes_button {
    color: var(--SmartThemeBodyColor);
  }

  #table_button_container {
    background-color: var(--buttonFill) !important; 
  }

  /* --- 顶栏图标 --- */

  /* Base Styling for Top Bar Icons (using ::before for images) */
  .drawer-icon::before {
    content: "";
    width: var(--topbarIconSize);
    height: var(--topbarIconSize);
    background-size: cover;
    display: inline-block;
    background-position: center;
    background-repeat: no-repeat;
    background-color: var(--SmartThemeUnderlineColor);

    mask-repeat: no-repeat;
    mask-position: center;
    mask-size: cover;

    -webkit-mask-repeat: no-repeat;
    -webkit-mask-position: center;
    -webkit-mask-size: cover;
  }

  /* Top Bar Icon Opacity (Closed State) */
  .drawer-icon.closedIcon {
    font-size: var(--topbarIconSize);
    opacity: 0.8;
  }

  /* Top Bar Icon Size (Open State) */
  .drawer-icon.openIcon {
    font-size: var(--topbarIconSize);
    transform: scale(1.2);
    border-radius: 0px;
  }

  /* Top Bar Icon Hover Effect */
  .drawer-icon.closedIcon:hover {
    transform: scale(1.2); /* Enlarge on hover */
    border-radius: 0px;
  }

  /* 顶栏图标导入 */
  #leftNavDrawerIcon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/preset.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/preset.svg');
  } /* Preset */
  #sys-settings-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/connection.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/connection.svg');
  } /* Connection/API */
  #advanced-formatting-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/format.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/format.svg');
  } /* Formatting */
  #WI-SP-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Worldinfo.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Worldinfo.svg');
  } /* World Info */
  #user-settings-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Settings.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Settings.svg');
  } /* Settings */
  #logo_block .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/background.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/background.svg');
  } /* Backgrounds */
  #extensions-settings-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Extensions.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/Extensions.svg');
  } /* Extensions */
  #table_database_settings_drawer .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/table.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/table.svg');
  } /* Table Database */
  #persona-management-button .drawer-icon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/persona.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/persona.svg');
  } /* Persona */
  #rightNavDrawerIcon::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/character.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/character.svg');
  } /* Characters */

/* --- 底栏图标 --- */

  /* Base Styling for Bottom Bar Icons */
  #rightSendForm>div, #leftSendForm>div {
    font-size: var(--bottomFormIconSize) !important;
    border-radius: 100px;
    color: var(--SmartThemeUnderlineColor);
    position: relative;
  }

  /* Bottom Bar Icon Alignment */
  #leftSendForm, #rightSendForm {
    align-items: center;
  }

  /* Base Styling for Bottom Bar Icons (using ::before for images) */
  #options_button::before,
  #extensionsMenuButton::before,
  #send_but::before,
  .fa-solid.fa-circle-stop::before {
    content: "";
    position: relative;
    width: var(--bottomFormIconSize);
    height: var(--bottomFormIconSize);
    background-size: cover;
    display: inline-block;
    background-position: center;
    background-repeat: no-repeat;
    background-color: var(--SmartThemeUnderlineColor);

    mask-repeat: no-repeat;
    mask-position: center;
    mask-size: cover;

    -webkit-mask-repeat: no-repeat;
    -webkit-mask-position: center;
    -webkit-mask-size: cover;
  }

  /* Bottom Bar Icon Hover Effect */
  #rightSendForm>div:not(.mes_stop):hover, #leftSendForm>div:hover, .fa-solid.fa-circle-stop:hover {
    transform: scale(1.1); /* Enlarge on hover */
  }

  /* 底栏图标导入 */
  #options_button::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/option.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/option.svg');
  } /* Options */
  #extensionsMenuButton::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/exten.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/exten.svg');
  } /* Extensions Menu */
  #send_but::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/send.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/send.svg');
  } /* Send */
  .fa-solid.fa-circle-stop::before {
    mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/stop.svg');
    -webkit-mask-image: url('https://cdn.jsdelivr.net/gh/Junejulyz/MacOSStyleAsset@main/stop.svg');
  } /* Stop */

  /* 标题样式 */

  /* 清除设置页相关模块标题的背景图，并精简上下 padding */
  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4, .standoutHeader{
    background-image: none !important;
    padding: 2px 0 !important;
  }

 /* 原始元素样式 */
  .standoutHeader:not(h4),
  #rm_PinAndTabs,
  #title_api,
  #rm_api_block .flex-container.alignItemsBaseline,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
  #WorldInfo .alignitemscenter:not(#world_popup *),
  #user-settings-block div[name="userSettingsRowOne"],
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter),
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p {
    position: relative; /* 必须要有 */
    padding-left: 12px;
    margin-top: calc(var(--mainFontSize) * 0.2) !important;
    margin-bottom: calc(var(--mainFontSize) * 0.2) !important;
  }

  /* 小竖线 */
  #rm_PinAndTabs::before,
  #title_api::before,
  #rm_api_block .flex-container.alignItemsBaseline::before,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3)::before,
  #WorldInfo .alignitemscenter:not(#world_popup *)::before,
  #user-settings-block div[name="userSettingsRowOne"]::before,
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter)::before,
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p::before {
    content: '';
    position: absolute;
    left: 0;
    top: 6px;
    bottom: 6px;
    width: 3px;
    background-color: var(--SmartThemeUnderlineColor);
    border-radius: 1px;
  }

  /* === 统一标题文字的行高（使阅读更舒适）=== */
  .standoutHeader strong,
  #right-nav-panel h2,
  .drawer-content > h3,
  .drawer-content .flex-container h3:first-of-type:not(#Backgrounds .textAlignCenter),
  .drawer-content .flex-container .flex-container h3:first-of-type,
  .popup h3:not(dialog *) {
    line-height: var(--genericLineHeight);
  }

  .flex-container {
    gap: 8px;
  }

  .redOverlayGlow {
    color: #9c2c2c;
  }

  /* AI聊天气泡样式 */
  body .mes[is_user="false"]{
    margin-top: 8px;
    margin-bottom: 16px;
  }

  /* menu颜色 */
  .ui-widget.ui-widget-content,
  #bulk_tag_popup,
  #dialogue_popup,
  .popup,
  #options,
  #extensionsMenu,
  .popup .popper-modal,
  .select2-dropdown,
  #select_chat_popup,
  #floatingPrompt,
  #cfgConfig,
  #logprobsViewer,
  #movingDivs > div {
    border-radius: var(--genericRadius);
    background-color: var(--menuBackgroundColor);
    box-shadow: var(--boxShadowSubtle);
  }

  #options, #extensionsMenu {
    border: 1px solid var(--SmartThemeBorderColor);
  }

  .extensions_toolbar {
    background-color: var(--menuBackgroundColor);
  }

  /* 统一样式 */
  #ccs-preview-modal > div,
  .bg_example, #toast-container > div,
  .bg_example:hover, #toast-container > div:hover {
    border-radius: var(--genericRadius) !important;
    border: 1px solid var(--SmartThemeBorderColor) !important;
    box-shadow: var(--boxShadowSubtle) !important;
  }

  .list-group, .extensions_info .extension_block, #completion_prompt_manager_popup {
    border-radius: var(--genericRadius);
    border-left: 1px solid var(--SmartThemeBorderColor);
    border-right: 1px solid var(--SmartThemeBorderColor);
    border-bottom: 1px solid var(--SmartThemeBorderColor);
  }

  .options-content {
    border: none;
  }

  /* 背景添加页面 */
  .bg_example.no-border.no-shadow.add_bg_but.interactable {
    background-color: var(--SmartThemeUnderlineColor);
  }   

  .BGSampleTitle {
    border-radius: var(--genericRadius);
  }

   /* 角色卡添加tag */
  body .ui-widget-content li:hover {
    background-color: var(--SmartThemeUnderlineColor);
  }

  .tag {
    background-color: var(--buttonFill);
  }

  /* 角色卡删除键 */
  .red_button:hover {
    background-color: var(--crimson-hover) !important;
  }

   /* 世界书 */
  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-results__option::before,
  .select2-results__option--selected.select2-results__option::before {
    background-color: var(--buttonFill);
  }

  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice,
  .select2-container--default .select2-selection--multiple .select2-selection__choice,
  .standoutHeader~.inline-drawer-content {
    box-shadow: var(--boxShadowSubtle);
    border-radius: var(--genericRadius);
  }

  .select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display,
  span.select2.select2-container .select2-selection__choice__remove,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    background-color: var(--accentColorOverlay);
  }

  .select2_multi_sameline+span.select2-container .select2-selection--multiple .select2-search__field {
    background-color: transparent !important;
    border: transparent !important;
  }

  .standoutHeader~.inline-drawer-content {
    background-color: var(--menuBackgroundColor);
  }

  .wi-card-entry {
    margin-top: 12px;
    padding: 8px;
    background-color: var(--accentColorOverlay);
    border: 1.5px solid var(--SmartThemeBorderColor);
    border-radius: var(--genericRadius);
  }

  .wi-card-entry .flex-container .flex1 {
    min-width: 40%;
  }

  .paginationjs-pages ul li a, .select_chat_block {
    border: none;
  }

  #wiActivationSettings #wiSliders .alignitemscenter.flex-container.flexFlowColumn.flexGrow.flexShrink.gap0.flexBasis48p::before,
  #wiActivationSettings #wiSliders .alignitemscenter.flex-container.flexFlowColumn.flexGrow.flexShrink.flexBasis48p::before {
    border-radius: var(--genericRadius) !important;
    width: 0px !important;
  }

  #wiActivationSettings #wiSliders .alignitemscenter.flex-container.flexFlowColumn.flexGrow.flexShrink.gap0.flexBasis48p,
  #wiActivationSettings #wiSliders .alignitemscenter.flex-container.flexFlowColumn.flexGrow.flexShrink.flexBasis48p {
    padding-left: 0px !important;
  }

  /* 聊天气泡 */
  body.bubblechat .mes {
    border-radius: var(--genericRadius);
    padding: 0px 0px 16px 0px;
    display: flex;
    flex-direction: column;
  }

  /* 头像 */
  .mesAvatarWrapper {
    display: flex;
    background-color: var(--accentColorOverlay);
    flex-direction: row; /* 从竖排改成横排 */
    align-items: center; /* 垂直居中 */
    gap: 8px; /* 头像和气泡之间的间距 */
    padding: 16px 16px 0px 16px; /* 间距 */
    border-radius: var(--genericRadius);
    overflow: hidden;
    width: 100%;
    padding-bottom: 3px;
  }

  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper {
    flex-direction: row-reverse; 
  }

  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper .avatar {
    flex: 0 0 auto;
  }

  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper > .mesIDDisplay,
  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper > .mes_timer,
  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper > .tokenCounterDisplay {
    margin-right: auto;
  }

  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper > .mes_timer,
  body.bubblechat .mes[is_user="true"] .mesAvatarWrapper > .tokenCounterDisplay {
    margin-right: 0;
  }

    /* 头像处理 */
  .mes .avatar img{
   padding: 0px;
  }

  body.square-avatars .avatar, body.square-avatars .avatar img{
    border-radius: var(--genericRadius) !important;
  }

  .avatar img {
    box-shadow: var(--boxShadowSubtle) !important;
  }

  body.big-avatars .avatar {
    align-items: self-start !important;
  }

  /* avatar 优化 */
  .fa-fw.fa-solid.fa-circle-xmark.dragClose::before {
    color: var(--buttonFill);
  }

  /* 名字 */
  .mes_block .ch_name {
    background-color: var(--accentColorOverlay);
    border-radius: var(--genericRadius);
    padding: 16px; /* 间距 */
    margin-bottom: 8px;
    border-bottom: 1px solid var(--SmartThemeBorderColor);
  }

  .mes_block .ch_name .flex-container {
    min-width: 0;  
    flex: 1 1 auto;
  }

  body .mes[is_user="true"] .mes_block .ch_name, body .mes[is_user="true"] .mes_block .ch_name .flex-container {
    flex-direction: row-reverse;
  }

  /* 正文 */
  .mes_text {
    padding: 0px 18px !important;
    line-height: var(--genericLineHeight);
  }

  .mes_text p {
    margin-bottom: 12px;
  }

  .last_mes .mesAvatarWrapper {
    padding-bottom: 5px !important;
  }

  .mes_block {
    padding-left: 0px !important;
  }

  /* 正文edit按键 */
  .mes_edit_buttons {
    filter: drop-shadow(0px 0px 2px var(--buttonFill));
  }

  .mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
    color: var(--SmartThemeBodyColor);
  }

   /* 引用 */
  .mes_text blockquote, .mes_reasoning blockquote {
    background-color: var(--accentColorOverlay);
    border-left: var(--accentColor) 2px solid;
    padding: 8px;
    margin-bottom: 8px;
  }

  /* extension */
  .extensions_info .extension_disabled {
    color: var(--SmartThemeEmColor);
  }

  /* 快捷回复 */
  .menu_button  {
    filter: none !important;
    background-color: var(--buttonFill);
    box-shadow: var(--boxShadowSubtle);
  }

  .menu_button:hover {
    background-color: var(--crimson-hover) !important;
  }

  /* 输入助手 */
  .integrated-button-row {
    background-color: var(--menuBackgroundColor) !important;
  }

  /* 发送&top bar */
  #top-bar, #send_form {
    background-color: color-mix( in srgb, var(--navBackgroundColor) 50%, transparent 50%) !important;
    padding: 6px 8px;
    border: 1px solid var(--SmartThemeBorderColor);
  }

  body.no-blur #top-bar, body.no-blur #send_form {
    background-color: var(--navBackgroundColor) !important;
    padding: 6px 8px;
  }

  #send_form, body.no-blur #send_form {
    border-radius: var(--genericRadius);
  }

   /* 顶栏阴影 */
  body #top-bar, body #send_form {
    box-shadow: none;
  }

  /* Checkbox */
  input[type='checkbox'] {
    background-color: var(--buttonFill);
    box-shadow: var(--boxShadowSubtle);
    border-radius: var(--genericRadius);
  }

  input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--SmartThemeEmColor);
  }

  input[type="checkbox"]:disabled {
    background-color: color-mix(in srgb, var(--buttonFill) 20%, black 10%);
    opacity: 0.7;
