/* --- 奇迹之夜暖冬小猫，感谢代码楼里老师们的代码https://discord.com/channels/1291925535324110879/1380392618336194702
感谢尾老师，类名，输入框伪提示词来自尾老师，hy老师的去除思维链箭头，na老师的悬浮底栏和思维链文字自定义，楼层信息自定义，12的去思维链背景色。感谢NA老师的去阴影，感谢咩野老师的去除全局阴影。  --- */

/* --- 1. 字体导入 --- */
@import url('https://fonts.googleapis.com/css2?family=Gaegu&display=swap');
@import url("https://fontsapi.zeoseven.com/200/main/result.css");
@import url("https://fontsapi.zeoseven.com/387/main/result.css");
@import url("https://fontsapi.zeoseven.com/813/main/result.css");
@import url("https://fontsapi.zeoseven.com/119/main/result.css");

/* --- 2. 核心颜色与样式变量 --- */
:root {
    --color-light-pink: #FBE4E6;
    --color-dark-rose: #785055;
    --color-medium-pink: #E599A6;
    --color-cream: #FFFBF0;

    --theme-bg: #FAF6F8;
    --panel-bg-color: rgba(251, 228, 230, 0.85);
    --panel-bg-hover: rgba(251, 228, 230, 0.95);
    --card-bg: rgba(255, 251, 240, 0.9);
    --input-bg: rgba(255, 251, 240, 0.9); 
    --text-main: var(--color-dark-rose);
    --text-subtle: var(--color-medium-pink);
    --border-color: rgba(229, 153, 166, 0.3);
    --accent-color: var(--color-medium-pink);
    --selected-color: var(--color-light-pink);

    --font-english: "Gaegu", cursive;
    --font-main: "cjkFonts 全瀨體", var(--font-english);
    --font-quote: "Yomeng Script", var(--font-english);
    --font-code: "Nowar Rounded TW Wc", monospace;
    --font-title: "CEF Fonts CJK", var(--font-english);

    --border-radius: 16px;
}

/* --- 3. 全局样式 --- */
body { background-color: var(--theme-bg) !important; color: var(--text-main); font-family: var(--font-main); }
#sheld, #character_view { background: transparent !important; margin: 0 auto !important; left: 0; right: 0; border: none !important; box-shadow: none !important; overflow: visible !important; }
#chat { background: transparent !important; align-items: center !important; }

h1, h2, h3, h4, .standoutHeader, .inline-drawer-header, .title_restorable { font-family: var(--font-title); text-shadow: 1px 1px 2px rgba(251, 228, 230, 0.7); }

/* --- 4. 顶部导航栏 --- */
#top-settings-holder {
    background-color: transparent;
    box-shadow: none;
    border: none;
    backdrop-filter: blur(8px);
    background-image: none;
    position: relative;
}
#top-settings-holder::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: var(--panel-bg-color);
    z-index: -1;
}

.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }
.drawer-icon.fa-fw { width: 35px; height: 40px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.3s ease; }
.drawer-icon.fa-fw:hover { transform: scale(1.1); }

/* 导航栏图标组 1 */
#ai-config-button .drawer-icon, .drawer-icon.fa-sliders,
#advanced-formatting-button .drawer-icon, .drawer-icon.fa-font,
#user-settings-button .drawer-icon, .drawer-icon.fa-user,
#extensions-settings-button .drawer-icon, .drawer-icon.fa-cubes,
#persona-management-button .drawer-icon, .drawer-icon.fa-face-smile {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/qPbd/180X180/%E5%90%89%E5%88%A9chan_%2838%29.png/webp');
}
/* 导航栏图标组 2 */
#backgrounds-button .drawer-icon,
#sys-settings-button .drawer-icon, .drawer-icon.fa-plug,
#WI-SP-button .drawer-icon, .drawer-icon.fa-book-atlas,
#bg_settings_button .drawer-icon, .drawer-icon.fa-image,
#character_popup_button .drawer-icon, .drawer-icon.fa-address-card,
#table_database_settings_drawer .drawer-icon, .drawer-icon.fa-bars-progress {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/SNED/180X180/%E5%90%89%E5%88%A9chan_%2837%29.png/webp');
}

.drawer-content { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/vPu3/3840X1971/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D5387506541456723720%26skey%3D%40crypt_5a237c93_3aa3521e9c321cceaefc988f483d35c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); background-size: cover; background-position: center; border: none; border-radius: var(--border-radius); box-shadow: none; }

/* --- 5. 底部输入栏 --- */
#send_textarea { clip-path: none !important; background-color: transparent !important; border: none !important; }
#send_form { 
    border-radius: 25px;
    background-image: none !important;
    background-color: var(--input-bg) !important;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
    position: relative;
    transition: all 0.5s ease;
    padding: 10px 15px;
}
#send_form:hover { 
    transform: translateY(-5px);
    background-color: rgba(255, 251, 240, 0.95) !important;
    box-shadow: 0 8px 25px rgba(120, 80, 85, 0.1);
}
#rightSendForm, #leftSendForm { gap: 5px; }
#send_form:has(#send_textarea:focus-visible) { outline: none; }

#options_button::before, #send_but::before, #send_form #extensionsMenuButton::before, #ai_retry_button::before, #auto_completion_button::before { display: none !important; }
#options_button, #send_but, #send_form #extensionsMenuButton, #ai_retry_button, #auto_completion_button { width: 32px; height: 32px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.2s ease; font-size: 0; border: none; background-color: transparent; }
#options_button:hover, #send_but:hover, #send_form #extensionsMenuButton:hover, #ai_retry_button:hover, #auto_completion_button:hover { transform: scale(1.1); }
#options_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/I7CY/180X180/%E5%90%89%E5%88%A9chan_%2814%29.png/webp'); }
#send_form #extensionsMenuButton { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/done/180X180/%E5%90%89%E5%88%A9chan_%2832%29.png/webp'); }
#send_but { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/cotX/180X180/%E5%90%89%E5%88%A9chan_%2823%29%281%29.png/webp'); }
#auto_completion_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/fzZr/180X180/%E5%90%89%E5%88%A9chan_%2825%29.png/webp'); }

/* 自定义输入框提示 */
#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "和猫咪说点什么吧...";
    color: var(--text-subtle);
    position: absolute;
    top: 50%; left: 50%;
    transform: translate(-50%, -50%);
    font-family: var(--font-main);
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
    opacity: 0.8;
}

/* --- 6. 核心：消息布局与样式 --- */
.mes { display: flex; flex-direction: column; align-items: center; gap: 10px; padding: 0 !important; margin: 30px auto !important; width: 90%; max-width: 700px; background: transparent !important; border: none !important; box-shadow: none !important; overflow: visible !important; }

.mes .mesAvatarWrapper { display: grid; grid-template-columns: 100px 1fr; gap: 0px 20px; align-items: center; padding: 20px 30px; border-radius: var(--border-radius); box-shadow: none; width: 100%; max-width: 450px; order: 1; background-size: cover; background-position: center; position: relative; overflow: hidden; }
.mes[is_user="false"] .mesAvatarWrapper { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/vPu3/3840X1971/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D5387506541456723720%26skey%3D%40crypt_5a237c93_3aa3521e9c321cceaefc988f483d35c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); }
.mes[is_user="true"] .mesAvatarWrapper { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/To6C/1845X2975/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D8762577387946363761%26skey%3D%40crypt_5a237c93_3aa3521e9c321cceaefc988f483d35c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); }

.mes .mesAvatarWrapper::before { content: ''; position: absolute; top: 0; left: 0; right: 0; bottom: 0; background-color: rgba(255,255,255,0.2); z-index: 0; }
.mes .mesAvatarWrapper > * { position: relative; z-index: 1; }

/* 方形拼贴头像 */
.mes .avatar { grid-row: 1 / 3; grid-column: 1 / 2; width: 85px !important; height: 85px !important; flex-shrink: 0; display: block !important; position: relative; transform: rotate(-4deg); transition: transform 0.4s ease; }
.mes .avatar:hover { transform: rotate(0deg); }
.mes .avatar img { 
    border-radius: 10px !important;
    border: none !important; 
    width: 100%;
    height: 100%;
    object-fit: cover;
}
/* 方形头像装饰边框 */
.mes .avatar::before {
    content: '';
    position: absolute;
    top: -4px; left: -4px; right: -4px; bottom: -4px;
    border: 3px solid rgba(255, 255, 255, 0.85);
    border-radius: 12px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.mes .mesAvatarWrapper > .ch_name { grid-row: 1 / 2; grid-column: 2 / 3; display: block !important; font-weight: bold; font-size: 1.2em; color: var(--text-main); margin-bottom: 5px; font-family: var(--font-title); }
.mes .mesAvatarWrapper > .mes_timer, .mes .mesAvatarWrapper > .mesIDDisplay, .mes .mesAvatarWrapper > .tokenCounterDisplay { grid-column: 2 / 3; display: inline !important; font-size: 0 !important; margin-right: 8px; }

.mes::after { content: '喵~ 和你说个小秘密'; display: flex; justify-content: center; align-items: center; padding: 5px 15px; background-color: var(--card-bg); border-radius: 15px; font-size: 13px; color: var(--text-subtle); box-shadow: none; order: 2; }

.mes .mes_block { width: 100%; position: relative; order: 3; overflow: visible !important; }
.mes .mes_text { background-color: var(--color-cream); border: 1px solid var(--border-color); border-radius: var(--border-radius); padding: 25px; box-shadow: none; line-height: 1.9; color: var(--text-main); position: relative; overflow: visible !important; }

/* --- 7. 文本样式 & 贴图装饰 --- */

/* 贴图装饰 - 尺寸已缩小 */
.mes[is_user="true"] .mes_text::before {
    content: ''; display: block; position: absolute;
    top: -40px; left: -20px;
    width: 85px; height: 85px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/wbLK/180X180/%E5%90%89%E5%88%A9chan_%289%29.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 10;
}
.mes[is_user="true"] .mes_text::after {
    content: ''; display: block; position: absolute;
    bottom: -20px; right: -20px;
    width: 85px; height: 85px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/j0xg/180X180/%E5%90%89%E5%88%A9chan_%2825%29%281%29.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 10;
}
.mes[is_user="false"] .mes_text::before {
    content: ''; display: block; position: absolute;
    top: -25px; right: -15px;
    width: 100px; height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/0m5j/180X180/%E5%90%89%E5%88%A9chan_%287%29.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: top right;
    pointer-events: none; z-index: 10;
}
.mes[is_user="false"] .mes_text::after {
    content: ''; display: block; position: absolute;
    bottom: -20px; left: -15px;
    width: 85px; height: 85px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/zc0u/180X180/%E5%90%89%E5%88%A9chan_%2839%29.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: bottom right;
    pointer-events: none; z-index: 10;
}

/* --- 8. 元素重定位与自定义文本 --- */
.mes_block .ch_name .name_text, .mes_block .ch_name .timestamp { display: none !important; }
.mes_block .mes_buttons { position: absolute; top: 15px; right: 25px; z-index: 20; display: flex; align-items: center; gap: 8px; }

/* 扮演按钮图标替换 */
#mes_impersonate::before { content: '' !important; font-size: 0 !important; }
#mes_impersonate { 
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/fzZr/180X180/%E5%90%89%E5%88%A9chan_%2825%29.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: center;
    width: 24px; height: 24px;
    transition: transform 0.2s ease;
}
#mes_impersonate:hover { transform: scale(1.1); }

.mes .mesIDDisplay, .mes .mes_timer, .mes .tokenCounterDisplay { color: var(--text-subtle); white-space: nowrap; }
.mes .mesIDDisplay::before, .mes .mes_timer::before, .mes .mes_timer::after, .mes .tokenCounterDisplay::before, .mes .tokenCounterDisplay::after { font-size: 12px !important; font-family: var(--font-main); }
.mes[is_user="true"] .mesIDDisplay, .mes[is_user="true"] .mes_timer, .mes[is_user="true"] .tokenCounterDisplay, .mes[is_user="true"] .mesAvatarWrapper > .ch_name { color: var(--text-main) !important; text-shadow: none !important; }

/* AI楼层信息颜色 */
.mes[is_user="false"] .mesIDDisplay::before,
.mes[is_user="false"] .mes_timer::before, .mes[is_user="false"] .mes_timer::after,
.mes[is_user="false"] .tokenCounterDisplay::before, .mes[is_user="false"] .tokenCounterDisplay::after {
    color: var(--color-cream) !important;
    text-shadow: 0px 0px 3px var(--color-dark-rose);
}

#chat { counter-reset: ID-counter -1; }
.mes { counter-increment: ID-counter; }
.mesIDDisplay::before { content: '第' counter(ID-counter) '个猫脚印'; }
.mes_timer::before { content: "猫咪时间 "; margin-right: 3px; }
.mes_timer::after { content: " 打个盹"; margin-left: 3px; }
.tokenCounterDisplay::before { content: "叼来了 "; margin-right: 3px; }
.tokenCounterDisplay::after { content: " 小鱼干"; margin-left: 3px; }

/* 思维链 */
.mes_reasoning_header h4, .mes_reasoning_header_title {
    font-size: 0 !important;
    visibility: hidden;
}
.mes_reasoning_header h4::before, .mes_reasoning_header_title::before {
    content: "猫咪的奇思妙想...";
    font-family: var(--font-title);
    font-size: 14px !important;
    color: var(--accent-color);
    visibility: visible;
    display: block;
}
#chat .mes .mes_reasoning_header { background: transparent !important; border: none !important; box-shadow: none !important; border-radius: 0; backdrop-filter: none !important; padding: 0 !important; margin-bottom: 5px !important; color: var(--text-subtle) !important; font-size: 0.9em !important; display: inline-flex !important; align-items: center !important; }
.mes_reasoning {
    background-color: var(--card-bg);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    padding: 15px;
    margin-top: -5px;
}
.mes_reasoning_arrow { display: none !important; }

/* --- 9. 全局UI颜色与样式统一 --- */
* { box-shadow: none !important; border-color: transparent; }
.standoutHeader, .inline-drawer-header, .title_restorable, #extensions_settings2 > div, #extensions_settings > div, .openai_logit_bias_preset_form, .range-block-title.justifyLeft.standoutHeader, details > summary, #power-user-options-block h4, #InstructSettingsColumn h4, #SystemPromptColumn h4, #ContextSettings h4, .persona_management_global_settings h4, #character_popup h4, #AdvancedFormatting h4, #rm_group_chats_block h4, div[name*='HandlingToggles'] h4, div[name*='MiscellaneousToggles'] h4 {
    background-image: none !important; background: var(--card-bg) !important; border: none !important; border-radius: var(--border-radius) !important; padding: 8px 12px !important; margin-bottom: 8px !important; color: var(--text-main) !important; }
input[type="text"], input[type="number"], input[type="search"], textarea, select, .text_pole {
    background-color: var(--input-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; border-radius: 8px !important; }
input:focus, textarea:focus, select:focus { border-color: var(--accent-color) !important; }
.menu_button { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; transition: all 0.2s ease; }
.menu_button:hover { background-color: var(--accent-color) !important; border-color: var(--accent-color) !important; color: white !important; }
input[type="checkbox"] { filter: none !important; border: 1px solid var(--border-color) !important; }
input[type="checkbox"]:checked::before { box-shadow: inset 1em 1em var(--accent-color) !important; }
.prompt_order > div { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; }

/* 弹窗 */
dialog.popup { position: relative; overflow: hidden; background-color: transparent !important; border: none !important; }
dialog.popup .popup-body, #dialogue_popup {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260118/rOFK/2060X3474/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D9177033623642745061%26skey%3D%40crypt_5a237c93_3aa3521e9c321cceaefc988f483d35c5%26mmweb_appid%3Dwx_webfilehelper.jpg/webp') !important;
    background-size: cover !important;
    background-position: center !important;
    border: none !important;
    border-radius: var(--border-radius) !important;
    position: relative;
}
dialog.popup::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: rgba(255, 251, 240, 0.85);
    border-radius: var(--border-radius);
    z-index: 0;
}
dialog.popup .popup-body, #dialogue_popup { background-color: transparent !important; z-index: 1; }

.menu_button.active, .menu_button.world_set, .menu_button.fav_on { background-color: var(--selected-color) !important; border-color: var(--selected-color) !important; color: var(--text-main) !important; font-weight: bold; }
.menu_button.active:hover, .menu_button.world_set:hover, .menu_button.fav_on:hover { filter: brightness(1.05); }

#left-nav-panel, #left-nav-panel * { color: var(--text-main) !important; text-shadow: none !important; }
#left-nav-panel .standoutHeader, #left-nav-panel .menu_button, #left-nav-panel select { background-color: var(--card-bg) !important; color: var(--text-main) !important; border: 1px solid var(--border-color) !important; }
#left-nav-panel .menu_button:hover { background-color: var(--accent-color) !important; color: white !important; }
#left-nav-panel .menu_button.selected { background-color: var(--selected-color) !important; color: var(--text-main) !important; border-color: var(--accent-color) !important; }

.select2-container--default .select2-selection--multiple .select2-selection__choice { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove { color: var(--text-subtle) !important; transition: color 0.2s ease; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove:hover { color: var(--text-main) !important; }
