/* === 输入模块 === */
input[type="text"],
input[type="number"],
input[type="switch"],
input:not([type="checkbox"]):focus,
textarea:not([type="search"]) {
  background-color: transparent !important;
  color: #466b6b !important;
  border: 1px solid rgba(142, 202, 204, 0.4) !important;
  caret-color: #4dd0e1 !important;
  border-radius: 6px;
  padding: 8px 12px;
  font-size: 14px;
  line-height: 1.4;
  transition: all 0.2s ease;
  box-shadow: none;
}

/* 聚焦*/
input:focus,
textarea:focus {
  background-color: rgba(230, 245, 245, 0.55) !important;
  color: #2e7d8f !important;
  border-color: rgba(100, 180, 185, 0.3) !important;
  box-shadow: 0 0 0 2px rgba(142, 219, 214, 0.1);
