}


#user_avatar_block {
  max-height: 300px;
  overflow-y: auto;
  overflow-x: hidden;
  padding-right: 4px;
  scrollbar-width: thin;
}

/* 卡片容器*/
#user_avatar_block .avatar-container {
  position: relative;
  display: flex;
  align-items: center;
  gap: 12px;
  margin: 6px 0;
  padding: 10px 14px;
  border-radius: 14px;
  background: rgba(255, 255, 255, 0.4);
  border: 1px solid rgba(120, 160, 170, 0.2);
  box-shadow: 0 1px 3px rgba(100, 140, 150, 0.05);
  transition: background 0.3s ease, box-shadow 0.3s ease;
  cursor: pointer;
}

/* hover 提亮+柔光感 */
#user_avatar_block .avatar-container:hover {
  background: rgba(240, 250, 250, 0.5);
  box-shadow: 0 2px 6px rgba(80, 140, 150, 0.1);
}

/* 选中状态 */
#user_avatar_block .avatar-container.selected {
  border: 1.5px solid var(--customThemeColor-gray);
  background: rgba(220, 240, 240, 0.5);
}

#user_avatar_block .avatar-container.default_persona .avatar {
  outline: none;
}

@keyframes bubbleFloat {
  0% {
    transform: translateY(0) scale(0.8);
    opacity: 0.3;
  }
  50% {
    transform: translateY(-10px) scale(1);
    opacity: 0.8;
  }
  100% {
    transform: translateY(-20px) scale(1.2);
    opacity: 0;
  }
}

#user_avatar_block .avatar-container.selected::after {
  content: "❉";
  position: absolute;
  top: 5px;
  right: 10px;
  font-size: 18px;
  color: rgba(100, 200, 220, 0.7);
  animation: bubbleFloat 1.2s ease-out;
  pointer-events: none;
}
#user_avatar_block .avatar-container {
  animation: fadeSlideIn 0.4s ease forwards;
}

#user_avatar_block .avatar-container .avatar {
  transition: transform 0.3s ease;
}

#user_avatar_block .avatar-container:hover .avatar {
  transform: scale(1.05);
}
#user_avatar_block .avatar-container.selected .avatar {
  box-shadow: 0 0 6px rgba(90, 180, 190, 0.3);
  border-radius: 50%;
}

