/* ===== 引用文本 ===== */

body.bubblechat .mes q {
  position: relative;
  color: #4a6a78; 

  line-height: 1.5;
  padding: 5px 16px 5px 22px;
  margin: 3px 0;
  display: inline-block;
  background: linear-gradient(135deg, rgba(150, 210, 220, 0.2), rgba(180, 230, 240, 0.1));
  border-radius: 10px;
  box-shadow: 0 2px 5px rgba(140, 190, 200, 0.1);
  font-family: 'STDongGuanTi', serif;
  border-left: 6px solid rgba(120, 180, 200, 0.3);
}

/* 甜筒圆点装饰 */
body.bubblechat .mes q::before {
  content: "";
  position: absolute;
  top: 50%;
  left: 4px;
  transform: translateY(-50%);
  width: 12px;
  height: 12px;
  background: radial-gradient(circle at center, #7bb4c4 60%, transparent 60%);
  border-radius: 50%;
  box-shadow: 0 0 5px rgba(123, 180, 196, 0.3);
}

/* 非用户引用 */
body.bubblechat .mes:not([is_user="true"]) q {
  background: linear-gradient(135deg, rgba(170, 220, 230, 0.25), rgba(200, 240, 250, 0.15));
  border-left-color: rgba(140, 200, 210, 0.25);
  box-shadow: 0 1px 3px rgba(160, 210, 220, 0.07);
}

/* 用户引用 */
body.bubblechat .mes[is_user="true"] q {
  background: linear-gradient(135deg, rgba(120, 190, 200, 0.25), rgba(150, 220, 230, 0.15));
  border-left-color: rgba(100, 170, 180, 0.3);
  box-shadow: 0 2px 4px rgba(110, 180, 190, 0.1);
}

