body.bubblechat .mes q:hover {
    background: rgba(210, 235, 240, 0.9);
    transform: translateY(-1px);
    box-shadow: 2px 2px 6px rgba(160, 210, 220, 0.4);
}

body.bubblechat .mes q:hover::before,
body.bubblechat .mes q:hover::after {
    opacity: 0.8;
    color: rgba(70, 160, 180, 0.8);
}

