custom_css:
/* --- 【仙气素雅】(V49 - 编辑模式终极版) --- */
/* 引入在线字体：方正宋刻本秀楷简体 */
@import url('https://fonts.font.im/css?family=FZSKBXKJW--GB1-0');

/* --- 全局设定 --- */
* { box-sizing: border-box; }
body, button, input, textarea, code, select {
  font-family: "FZSKBXKJW--GB1-0", Georgia, serif !important;
}

/* 定义我们的仙气调色盘 */
:root {
    --text-silver: rgba(120, 108, 100, 1);
    --accent-pink: #FFDDDD;
    --accent-green: #BAD5D3;
    --border-color: rgba(224, 218, 208, 1);
    --border-color-disabled: rgba(200, 190, 180, 0.8);
}

/* 强制所有元素恢复色彩和亮度 */
*:disabled, *[disabled], [aria-disabled="true"], [readonly], .disabled, .inactive, .disabled *, .inactive * {
    color: inherit !important;
    -webkit-text-fill-color: inherit !important;
    text-fill-color: inherit !important;
    opacity: 1 !important;
    filter: none !important;
}
svg, img {
    opacity: 1 !important;
    filter: none !important;
}


/* --- 隐藏所有浏览器滚动条 --- */
::-webkit-scrollbar {
    display: none;
    width: 0;
    height: 0;
}
* {
    scrollbar-width: none; /* Firefox */
    -ms-overflow-style: none;  /* IE and Edge */
}


/* --- 聊天区域核心布局 --- */
.mes {
  display: flex !important;
  flex-direction: column;
  align-items: center;
  max-width: 95%;
  margin: 30px auto 0 auto !important;
  padding: 0 5px 15px !important;
  position: relative;
  background: transparent !important;
}
.mesAvatarWrapper {
  position: absolute !important;
  top: -10px !important;
  left: 50% !important;
  transform: translateX(-50%);
  width: auto !important;
  margin: 0 !important;
  text-align: center;
  z-index: 3;
  pointer-events: none !important;
}
.mesAvatarWrapper .avatar {
  position: relative;
  width: 64px !important;
  height: 64px !important;
  margin: 0 auto 12px !important;
  z-index: 1;
}
.mes .avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 50%;
    border: 3px solid rgba(255, 255, 255, 0.8);
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}
.mes_block {
  position: relative;
  padding: 210px 35px 25px 35px;
  margin-top: 25px !important;
  border-radius: 24px;
  background-color: rgba(255, 255, 255, 0.6);
  backdrop-filter: blur(10px);
  width: 100%;
}
/* 修正用户气泡的顶部间距 */
.mes[is_user='true'] .mes_block {
    padding-top: 150px !important;
}
.mes_block::before {
  content: "";
  position: absolute;
  top: 16px;
  left: 16px;
  right: 16px;
  bottom: 16px;
  background-color: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  z-index: 0;
}
.mes_block::after {
  content: "";
  position: absolute;
  top: 16px;
  left: 16px;
  right: 16px;
  background-position: center;
  border-radius: 20px 20px 0 0;
  z-index: 1;
}
.mes[is_user='false'] .mes_block::after {
    height: 180px;
    background-size: cover;
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/185dae5e7c560f92795201d9d6735a97.png');
}
.mes[is_user='true'] .mes_block::after {
    height: 0;
    padding-bottom: 36.36%;
    background-size: 100% 100%;
    background-image: url('https://s1.imagehub.cc/images/2025/08/04/3308ccaf239536b95090ba63422e75f5.png');
}
.mes_info_bar {
  position: absolute;
  top: 60px;
  left: 50% !important;
  transform: translateX(-50%);
  display: inline-block !important;
  white-space: nowrap;
  background: rgba(255, 255, 255, 0.7) !important;
  border: 1px solid rgba(200, 200, 200, 0.5) !important;
  backdrop-filter: blur(2px);
  z-index: 4;
  padding: 2px 10px;
  border-radius: 12px;
}
.mesIDDisplay, .mes_timer, .tokenCounterDisplay {
  display: inline-block !important;
  color: #666 !important;
  opacity: 1;
  margin: 0 5px;
}
.mes .mes_block > * {
  position: relative;
  z-index: 2;
  padding: 0 !important;
}
.mes_text {
    text-align: justify;
}

/* --- 【V48 新增】对话气泡美化 --- */
.mes_text q {
    display: inline-block; /* 确保padding和border正确包裹 */
    border: 2px dashed var(--accent-green);
    background-color: rgba(219, 237, 235, 0.5); /* 极淡的薄荷绿背景 */
    border-radius: 12px;
    padding: 0.3em 0.8em; /* 使用em单位，使其随字体大小缩放 */
    margin: 2px 4px;
    color: rgba(100, 90, 85, 1); /* 略微加深的文字颜色以保证清晰 */
    transform: translateY(-2px); /* 向上微调，增加轻盈感 */
    box-shadow: 0 1px 4px rgba(255, 255, 255, 0.7); /* 柔和的白色辉光 */
}

/* --- 思维链美化 --- */
.mes_reasoning_header {
    position: relative !important;
    border: none !important;
    background: transparent !important;
    cursor: pointer !important;
    user-select: none !important;
    color: transparent !important;
    font-size: 0 !important;
    width: 150px !important;
    height: 50px !important;
    margin: 10px auto !important;
}
.mes_reasoning_header::after {
    content: '' !important;
    position: absolute !important;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%) !important;
    width: 100% !important;
    height: 100% !important;
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/fbb2e733cabec8c78bcff43ca05d98af.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    transition: transform 0.2s ease, filter 0.2s ease !important;
}
.mes_reasoning_header:hover::after {
    transform: translate(-50%, -50%) scale(1.05) !important;
}
.mes_reasoning_header:active::after {
    transform: translate(-50%, -50%) scale(0.95) !important;
    filter: brightness(0.9) !important;
    transition-duration: 0.1s !important;
}


/* --- 顶/底部栏最终修正 --- */
/* 1. 强制白色背景 */
body.no-blur #top-bar,
body.no-blur #send_form {
    background-color: white !important;
}
/* 2. 输入框布局与边框 */
#send_form {
    align-items: center !important;
    width: calc(100% - 20px) !important;
    margin: 0 auto 10px auto !important;
    padding: 5px 10px !important;
    border: 2px solid var(--accent-green) !important;
    border-image: none !important;
    border-radius: 12px !important;
    min-height: auto !important;
}
#send_form:focus-within {
    border-color: var(--accent-pink) !important;
}
/* 3. 统一左侧图标尺寸和颜色 */
#top-settings-holder .drawer-icon,
#leftNavDrawerIcon,
#leftSendForm > div::before {
    font-size: 16px !important;
    width: 16px !important;
    height: 16px !important;
    line-height: 1 !important;
    color: #ABD6C1 !important;
    padding: 0 !important;
    margin: 0 !important;
    transition: all 0.2s ease !important;
}
/* 4. 用图片替换发送/停止按钮 */
#send_but, #stscript_stop.active {
    font-size: 0 !important;
    color: transparent !important;
    background: url('https://s1.imagehub.cc/images/2025/08/09/ec7ba6140c83dc7c3f413d3a1e3b7378.png') center/contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
    border: none !important;
    padding: 0 !important;
    margin: 0 0 0 5px !important;
    transition: transform 0.2s ease !important;
}
#send_but:hover, #stscript_stop.active:hover {
    transform: scale(1.1);
}
#send_but:active, #stscript_stop.active:active {
    transform: scale(0.9);
}
/* 5. 统一悬停效果 */
#top-settings-holder .drawer-icon:hover,
#leftNavDrawerIcon:hover,
#leftSendForm > div:hover::before {
    color: var(--accent-pink) !important;
    transform: scale(1.2);
}
/* 6. 输入框文本区样式 */
#send_textarea {
    background: transparent !important;
    color: var(--text-silver) !important;
    border: none !important;
    box-shadow: none !important;
}
#send_textarea::placeholder {
    color: var(--accent-green) !important;
    opacity: 0.7;
}


/* --- 设置面板 --- */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    background: var(--accent-green) !important;
    border: 2px solid var(--accent-pink) !important;
    color: var(--text-silver) !important;
    padding: 4px 15px 4px 55px !important;
    margin: 5px 0 !important;
    width: 100% !important;
    border-radius: 12px !important;
    box-shadow: 0 2px 8px rgba(180, 190, 200, 0.2) !important;
    position: relative !important;
}
#extensions_settings .inline-drawer-toggle.inline-drawer-header::before, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header::before, #user-settings-block h4::before, .standoutHeader::before {
    content: '' !important; display: block !important; position: absolute !important; left: 15px !important; top: 50% !important;
    transform: translateY(-50%) !important;
    width: 32px !important;
    height: 32px !important;
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/ec7ba6140c83dc7c3f413d3a1e3b7378.png') !important;
    background-size: contain !important; background-repeat: no-repeat !important;
    filter: none !important;
}
.inline-drawer-icon {
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/ec7ba6140c83dc7c3f413d3a1e3b7378.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    width: 20px !important;
    height: 20px !important;
    font-size: 0 !important;
    margin-right: 10px !important;
    transition: transform 0.3s ease !important;
}
.inline-drawer-toggle.open .inline-drawer-icon {
    transform: rotate(90deg) !important;
}
textarea, input[type="text"], input[type="number"] {
    background-color: rgba(255, 255, 255, 0.8) !important;
    border: 2px solid var(--accent-green) !important;
    border-radius: 8px; padding: 8px !important;
    color: var(--text-silver);
    transition: all 0.2s ease-in-out;
}
textarea:focus, input:focus {
    border-color: var(--accent-pink) !important;
    box-shadow: 0 0 8px var(--accent-pink) !important;
}
select {
    -webkit-appearance: none; -moz-appearance: none; appearance: none;
    background-color: rgba(255, 255, 255, 0.8) !important;
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/319cf76fae3f40e9143162b3fd220b99.png') !important;
    background-repeat: no-repeat !important; background-position: right 10px center !important; background-size: 16px !important;
    border: 2px solid var(--accent-green) !important;
    border-radius: 8px !important; padding-right: 30px !important;
}
input[type="range"] {
    -webkit-appearance: none !important; appearance: none !important;
    width: 100% !important; height: 6px !important;
    background: var(--accent-green) !important;
    border-radius: 3px !important;
    outline: none !important; margin: 20px 0 !important;
}
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none !important; appearance: none !important;
    width: 36px !important;
    height: 36px !important;
    background: url('https://s1.imagehub.cc/images/2025/08/09/d23c5a346f42ff00d54e1bc4b4457101.png') center / contain no-repeat !important;
    cursor: pointer !important; border: none !important;
    filter: drop-shadow(0 0 4px rgba(255, 255, 255, 0.9)) !important;
}
input[type="checkbox"] {
    -webkit-appearance: none !important; appearance: none !important;
    background-color: var(--accent-green) !important;
    border: none !important;
    width: 16px !important; height: 16px !important; border-radius: 4px !important;
    display: inline-block !important; position: relative !important;
    vertical-align: middle !important; cursor: pointer !important;
    transition: all 0.2s ease-in-out !important;
}
input[type="checkbox"]::before { content: none !important; }
input[type="checkbox"]:checked {
    background-color: var(--accent-pink) !important;
}
input[type="checkbox"]:checked::after {
    content: '' !important;
    display: block !important;
    position: absolute !important;
    width: 4px !important;
    height: 7px !important;
    left: 5px !important;
    top: 3px !important;
    border: solid white !important;
    border-width: 0 2px 2px 0 !important;
    transform: rotate(45deg) !important;
}
.avatar-container.selected {
    outline: 2px solid var(--accent-pink) !important;
    outline-offset: -2px !important;
    box-shadow: 0 0 15px var(--accent-pink) !important;
    background-color: rgba(255, 245, 248, 1) !important;
}
.world_entry .inline-drawer-icon {
    background-image: url('https://s1.imagehub.cc/images/2025/08/09/ec7ba6140c83dc7c3f413d3a1e3b7378.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    width: 24px !important;
    height: 24px !important;
    font-size: 0 !important;
    margin-right: 10px !important;
    filter: none !important;
    opacity: 1 !important;
    transition: transform 0.3s ease, opacity 0.3s ease !important;
}
.world_entry .inline-drawer-icon:hover {
    transform: scale(1.2) rotate(15deg) !important;
}
.world_entry .inline-drawer-icon:active {
    transform: scale(0.9) !important;
    transition-duration: 0.1s !important;
}
.world_entry .inline-button {
    background-color: rgba(255, 255, 255, 0.7) !important;
    border: 1px solid var(--border-color) !important;
    color: var(--text-silver) !important;
    border-radius: 5px !important;
    padding: 2px 6px !important;
    margin: 0 2px !important;
    transition: all 0.2s ease !important;
    filter: none !important;
    opacity: 1 !important;
}
.world_entry .inline-button:hover {
    background-color: var(--accent-pink) !important;
    border-color: var(--accent-pink) !important;
    color: white !important;
}

/* --- V30 核心：背景色更换 --- */

/* 【V30 修正】把所有设置面板的背景都换成干净的纯白色 */
.drawer-content {
    background-color: white !important;
}

/* --- 【V33】预设面板终极修复版 --- */
/* 结合V32的美学与修复方案的强度，确保文字在任何情况下都清晰可见 */

/* 预设列表项 - 基础样式 (保留V32的美学) */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    background-color: rgba(240, 248, 247, 0.6) !important; /* 极淡的薄荷绿背景 */
    border: 2px dotted var(--accent-green) !important;
    border-radius: 12px !important;
    margin-bottom: 8px !important;
    padding: 10px 15px !important;
    transition: all 0.3s ease !important;
    box-shadow: none !important;
    backdrop-filter: none !important;
}

/* 预设列表项 - 悬停 & 选中状态 (保留V32的美学) */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:hover,
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt.selected {
    border-style: solid !important;
    border-color: var(--accent-pink) !important;
    background-color: rgba(255, 245, 248, 0.9) !important; /* 极淡的樱花粉背景 */
    box-shadow: 0 0 10px var(--accent-pink) !important;
}

/* 预设列表项 - 禁用状态 (保留V32的美学) */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
    background: rgba(235, 235, 235, 0.5) !important; /* 柔和的灰色背景 */
    border: 2px dashed var(--border-color-disabled) !important;
    box-shadow: none !important;
}

/* 【核心修正】强制为所有预设内的文字和图标应用主题色 */
#completion_prompt_manager .completion_prompt_manager_prompt_name,
#completion_prompt_manager .completion_prompt_manager_prompt_name *,
#completion_prompt_manager .fa-solid {
    color: var(--text-silver) !important;
    -webkit-text-fill-color: var(--text-silver) !important; /* 强制覆盖WebKit填充色 */
    text-shadow: none !important;
    background: transparent !important; /* 防止继承不透明背景 */
}

/* 【核心修正】强制为禁用的预设应用禁用颜色 */
#completion_prompt_manager .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name *,
#completion_prompt_manager .completion_prompt_manager_prompt_disabled .fa-solid {
    color: var(--border-color-disabled) !important;
    -webkit-text-fill-color: var(--border-color-disabled) !important; /* 强制覆盖WebKit填充色 */
}

/* 操作按钮图标 - 美化 (保留V32的美学) */
#completion_prompt_manager .completion_prompt_manager_prompt_actions .fa-solid {
    all: unset !important; /* 重置所有继承的样式 */
    font-family: "Font Awesome 6 Free" !important; /* 确保图标字体正确加载 */
    font-weight: 900 !important;
    display: inline-flex !important;
    align-items: center;
    justify-content: center;
    width: 28px !important;
    height: 28px !important;
    /* 颜色由上面的核心修正规则控制，这里只定义背景和边框 */
    background-color: rgba(255, 255, 255, 0.5) !important;
    border: 1px solid var(--accent-green) !important;
    border-radius: 50% !important;
    cursor: pointer !important;
    transition: all 0.2s ease !important;
    text-shadow: none !important;
}

/* 操作按钮图标 - 悬停状态 (保留V32的美学) */
#completion_prompt_manager .completion_prompt_manager_prompt_actions .fa-solid:hover {
    color: white !important; /* 悬停时颜色可以覆盖 */
    -webkit-text-fill-color: white !important;
    background-color: var(--accent-pink) !important;
    border-color: var(--accent-pink) !important;
    transform: scale(1.1) rotate(10deg);
}

/* 确保面板整体背景透明，继承白色底板 */
#completion_prompt_manager,
#completion_prompt_manager #completion_prompt_manager_list {
    background-color: transparent !important;
}
/* 底栏QR按键隐藏 */
#send_form:hover #qr--bar {
    height: 35px;
    transition: all 0.5s ease;
}
#qr--bar {
    height: 0.5px;
}
