custom_css:
@font-face {
    font-family: 'DroidSansChinese';
    src: url("https://cdn.jsdelivr.net/gh/dkebbd/my-fonts/liumangxing100.ttf") format('truetype'); /* 修改为新的字体链接 */
    font-weight: normal; /* 设置字体粗细为400 */
    font-style: normal;
}
body, button, input, textarea, code, select {
  font-family: "DroidSansChinese", "Microsoft YaHei", sans-serif !important;
}

:root{
    --white30a: #8fa09188;
    --white50a: #62708294;
    --golden: rgb(141 158 143);
    --grey7070a: #6b786d88;
    --black50a: transparent;
    --black30a: #fff;
}

/* 强制覆盖所有禁用元素的样式 */
[disabled], 
:disabled {
    /* 移除灰色滤镜和背景 */
    filter: none !important;
    background-color: transparent !important;
    background-image: none !important;

    /* 恢复文字颜色 */
    color: var(--text-normal) !important; /* 使用主题变量 */
    opacity: 1 !important;

    /* 修复边框 */
    border-color: var(--border-color-primary) !important;

    /* 修复图标颜色 */
    svg, img {
        opacity: 1 !important;
        filter: none !important;
    }
}

/* 针对特定禁用按钮的强化覆盖 */
button[disabled] {
    background: linear-gradient(to bottom, #f0f0f0, #e0e0e0) !important;
    color: #666 !important;
    border: 1px solid #d0d0d0 !important;
}

/* 修复输入框禁用状态 */
input[disabled], 
textarea[disabled] {
    background: #f8f8f8 !important;
    -webkit-text-fill-color: #333 !important; /* 修复 Safari/Chrome 文字颜色 */
}
#chat{
    display: flex
;
    align-items: center;
}
#chat .mes {
    width: 350px;
    margin-left: 10px;
    display: flex
;
    border-radius: 13px;
    margin-top: 70px !important;
    color: var(--SmartThemeBodyColor);
    position: relative;
    background: url(https://files.catbox.moe/d0gity.png) top center no-repeat, url(https://files.catbox.moe/rj3hgd.png) center repeat-y, url(https://files.catbox.moe/0ll8kq.png) bottom / 100% auto no-repeat !important;
    background-size: 100% auto, /* 顶部 */ 100% auto, /* 中间 */ 100% auto !important;
    padding: 15px 10px 50px 10px !important;
    background-blend-mode: normal !important;
    z-index: 1 !important;
    flex-direction: column;
}
.mes::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 20px;
    background: url(https://files.catbox.moe/0ll8kq.png) bottom / 100% auto no-repeat !important;
    z-index: 2;
    pointer-events: none;
}
#chat .mes .mesAvatarWrapper {
    display: flex
;
    flex-direction: row;
    height: 160px;
    width: auto;
    align-items: flex-start;
}
#chat .mes .avatar{
    cursor: pointer;
    padding: 95px 0px 0px 15px;
}
#chat .mes .avatar img {
    width: 60px;
    height: 60px;
    border: none;
    box-shadow: none;
}
.mes .mes_timer, .mes .mesIDDisplay, .mes .tokenCounterDisplay {
    cursor: default;
    opacity: 0.7;
    font-size: calc(var(--mainFontSize) * 0.9);
    font-weight: 600;
    text-align: center;
    display: contents;
}
#chat .mes .alignItemsBaseline {
    align-items: baseline !important;
    display: flex
;
    flex-direction: column;
    padding-bottom: 10px;
}
.timestamp{
    font-size: calc(var(--mainFontSize) * 0.7);
    font-weight: 400;
    padding-top: 25px;
}
.mes_block {
    padding-left: 10px;
    padding-right: 10px;
}
.mes_text {
    padding-left: 5px;
    padding-bottom: 5px;
    padding-right: 5px;
    padding-top: 0px;
}

#chat .mes .name_text {
    padding-left: 15px;
    padding-top: 0px;
    color: #4d6646;
}
.last_mes{
    margin-bottom: 25px !important;
}
/* siwelian */
.mes_reasoning_header {
    font-family: 'DroidSansChinese';
    background: transparent;
    border-bottom: 2px solid #8b979e;
    border-radius: 0px;
    position: relative;
    padding: 5px 20px 5px 10px;
    margin: 0px 0px 10px 15px;
}
.mes_reasoning_header::after {
    content: "";
    display: block;
    position: absolute;
    width: 30px;
    height: 30px;
    background-image: url(https://files.catbox.moe/1ent8h.png);
    background-size: contain;
    background-repeat: no-repeat;
    bottom: -5px;
    left: -15px;
    pointer-events: none;
    z-index: 10;
}
/*  顶部 */
body.no-blur #top-bar, body.no-blur #send_form {
    background-color: #f2f2f1 !important;
}
#extensionTopBar, 
#top-bar {
    border-radius: 0px !important;
    margin-bottom: 0px !important;
    box-shadow: none !important;
    background-color: #f2f2f1 !important;
}

#advanced-formatting-button .drawer-toggle,
#user-settings-button .drawer-toggle ,
#ai-config-button .drawer-toggle,
#extensions-settings-button .drawer-toggle,
#persona-management-button .drawer-toggle
{
    background-image: url('https://files.catbox.moe/srioeh.png') !important;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    width: 27px;
    height: 27px;
    display: inline-block;
    text-indent: -9999px; /* 隐藏原始文本（如果有） */
}
#table_database_settings_drawer .drawer-toggle,
#sys-settings-button .drawer-toggle,
#WI-SP-button .drawer-toggle,
#logo_block .drawer-toggle,
#rightNavHolder .drawer-toggle{
    background-image: url('https://files.catbox.moe/bcn0mq.png') !important;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    width: 27px;
    height:27px;
    display: inline-block;
   color: transparent !important; /* 隐藏原始文本（如果有） */
}
/* 基础样式 */
.drawer-toggle {
  transition: transform 0.3s cubic-bezier(0.2, 0.8, 0.4, 1.5);
}

/* 悬浮时弹性放大 */
.drawer-toggle:hover {
  transform: scale(1.2); /* 放大20% */
}

/* 点击时更有弹性的效果 */
.drawer-toggle:active {
  transform: scale(0.9) rotate(5deg); /* 点击时先缩小再回弹 */
}

/* 输入框 */
#send_form {
    height: auto !important;
    min-height: 55px !important;
    border-radius: 14px;
    width: -webkit-fill-available;
border-color: transparent !important;
    outline: 2px solid #4e684ba3;
    outline-offset: -5px;
    margin: 0px 5px 2px 5px;
    flex-direction: row !important;
    justify-content: center !important;
    align-items: stretch;
}
#rightSendForm > .interactable:not([style*="display: none"]) .fa-solid, #rightSendForm > .interactable[style*="display: flex"] .fa-solid {
    font-size: 0.7em !important;
    width: 28px !important;
    height: 28px !important;
    color: rgb(141 158 143);
}

/* 底栏QR按键隐藏 */
#send_form:hover #qr--bar {
    height: 30px;
    transition: all 0.5s ease;
}
#qr--bar {
    height: 0.5px;
}

.fa-bars::before,
.fa-magic-wand-sparkles::before{
    display: none !important; /* 隐藏 Font Awesome 图标 */
}

#send_but::before{
    display: none !important; /* 隐藏 Font Awesome 图标 */
}

#options_button {
    background-image: url(https://files.catbox.moe/i8df4e.png) !important;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 28px !important;
    height: 28px !important;
    margin-left: 10px !important;}
.options-content{
    font-family:"DroidSansChinese";
}
#extensionsMenuButton {
    background-image: url(https://files.catbox.moe/8buca0.png) !important;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 28px !important;
    height: 28px !important;
    margin-left: 10px !important;
}
/* 替换 "发送消息" (纸飞机按钮) */
#send_but {
    background-image: url(https://files.catbox.moe/mh2d0k.png) !important;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 28px !important;
    height: 28px !important;
    margin-right: 10px !important;
}
.fa-solid.fa-circle-stop {
    /* 隐藏Font Awesome的字体图标 */
    font-size: 0 !important;
    color: transparent !important;

    /* 用自定义图片替换 */
    display: inline-block;
    width: 28px !important;
    height: 28px !important;
    margin-right: 10px !important;
    background-image: url('https://files.catbox.moe/eeym6n.png');
    background-size: contain;
}
/* 选择框 */
select {
    outline: none !important;
    border: 2px dashed #8fa09180 !important;
    padding: 6px 6px !important;
    box-shadow: none !important;
    position: relative;
    background: url(https://files.catbox.moe/x1psqx.png);
    background-size: 40px;
    background-repeat: no-repeat;
    background-position: center;
}
select.text_pole {
    background: #fff !important;
}
.text_pole {
    background-color: transparent !important;
}
/*  导入框 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    border: 1px solid #ffffff !important;
    font-family: 'MiaoMiao', monospace !important;
    padding: 4px 8px !important;
    background-color: #f2f2f1 !important;
    background-blend-mode: overlay !important;
    box-shadow: none !important;
    position: relative;
    font-family: 'DroidSansChinese' !important;
}
#user-settings-block h4::after, .standoutHeader::after {
    content: "";
    position: absolute;
    background-image: url(https://files.catbox.moe/nswnxa.png);
    width: 140px;
    height: 60px;
    background-size: contain;
    background-repeat: no-repeat;
    left: -10px;
    bottom: -27px;
    pointer-events: none;
    z-index: 10;
}
/* text框 */
textarea {
    background: transparent !important;
    height: auto;
}
#customCSS, 
.edit_textarea {
    border: 2px dashed #91a29277;
    background: #ffffff !important;
    border-radius: 10px;
}
#description_textarea,
.mdHotkeys,
#persona_description{
    border: 2px dashed #91a29277;
    background: #f1f1f250 !important;
    border-radius: 10px;
    padding: 5px;
}
/* 主题色和别的 */
.fa-chevron-circle-up:before, .fa-circle-chevron-up:before,
.fa-chevron-circle-up:before, .fa-circle-chevron-down:before {
    content: "\f578";
    color: #8fa091;
    font-size: calc(var(--mainFontSize) * 1.2);
}

/* 拓展 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, .standoutHeader {
    background: #ffffff !important;
    border: 2px solid #d2d2d2 !important;
}

/* 世界书 */
.world_entry {
    border: 2px dashed #8fa09180 !important;
    margin: 15px 0px;
    border-radius: 10px;
    padding: 6px 6px !important;
    box-shadow: none !important;
    background: url(https://files.catbox.moe/dvs9ro.jpg);
    background-size: 400px;
    background-repeat: repeat;
    background-position: center;
}

/* 小铅笔 */
.fa-ellipsis-h:before, .fa-ellipsis:before {
    content: "你好" !important;
    color: rgba(0, 0, 0, 0.5);
    font-size: 0.9em;
}
.mes_button.mes_edit.interactable::before {
    content: "!" !important;
    font-size: 0.9em;
}
.mes_button, .extraMesButtons>div {
    cursor: pointer;
    transition: opacity 0.2s ease-in-out;
    filter: drop-shadow(0px 0px 2px black);
    opacity: 0.3;
    padding: 13px 3px;
}
/* 滑块 */
/* 清除所有浏览器默认外框 */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 2px;
    border-radius: 0px;
    background-size: 70% 100%;
    background-repeat: no-repeat;
    box-shadow: none;
    cursor: ew-resize;
}
input[type="range"] {
    -webkit-appearance: none !important;
    border-radius: 0px !important;
    height: 2px !important;
    margin-bottom: 15px !important;
    background: rgb(107 120 109 50%) !important;
    box-shadow: none !important;
}
input[type="number"]{
    background-color: rgb(107 120 109 / 9%);
}

input[disabled], textarea[disabled] {
    background: transparent !important;
    -webkit-text-fill-color: #333 !important;
}
/* 滑块按钮样式 */
input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  margin-top:5px;
  width:30px;
  height: 38px;
   border: none !important;
  background: url(https://files.catbox.moe/sla4g2.png) center/cover;
  cursor: pointer;
}
/* check框 */
input[type="checkbox"] {
    background: #fff;
    border: 2px solid #6b786d88 !important;
    box-shadow: none;
}
input[type="checkbox"]:checked {
    background: transparent;
    border:none !important;
}
input[type="checkbox"]::before {
    background: url(https://files.catbox.moe/8buca0.png);
    background-size: cover;
    background-repeat: no-repeat;
    box-shadow: none;
    transform-origin: bottom left;
    width: 1.5em;
    height: 1.5em;
    clip-path: none;
}
/* 代码块和反引号 */
code {
    display: inline-block;
    border: 2px dashed rgb(125 75 56 / 65%);
    border-radius: 8px;
    background-color: #ffffff;
    padding: 4px 6px;
    color: rgb(105 82 73 / 56%);
    line-height: normal !important;
}
.hljs {
    background: #ffffff;
    border: none;
}

/* 引用 */
.mes_text q{
    margin: 0px;
background: linear-gradient(0deg, #90a09150 0%, #91a39388 30%, transparent 30%, transparent 100%);
    padding: 0px 2px;
    border-radius: 2px;
    font-style: normal;
    -webkit-box-decoration-break: clone;
}
/* 角色选择 */
.avatar-container{
    border: var(--avatar-base-border-radius) dashed rgb(141 159 144 / 61%);
   margin: 5px 0px;
}
#rm_characters_block .bogus_folder_select,
#rm_characters_block .character_select,
#rm_characters_block .avatar-container.selected,
#user_avatar_block .bogus_folder_select,
#user_avatar_block .character_select,
#user_avatar_block .avatar-container.selected {
    border: var(--avatar-base-border-radius) dashed rgb(141 159 144 / 61%);
    background: #f1f1f2;
   margin: 5px 0px;
   position:relative;
}
#rm_characters_block .bogus_folder_select::after,
#rm_characters_block .character_select::after,
#rm_characters_block .avatar-container.selected::after,
#user_avatar_block .bogus_folder_select::after,
#user_avatar_block .character_select::after,
#user_avatar_block .avatar-container.selected::after {
    content: "";
    position: absolute;
    background-image: url(https://files.catbox.moe/v14hlr.png);
    width: 40px;
    height: 40px;
    background-size: contain;
    background-repeat: no-repeat;
    right: -5px;
    bottom: -10px;
    pointer-events: none;
    z-index: 10;
}
/* 杂七杂八 */
#ai_response_configuration .inline-drawer-header {
    padding: 5px 10px;
    margin: 10px 0px;
    cursor: pointer;
    border: 2px dashed #102d1642;
    border-radius: 10px;
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    align-items: center;
    padding: 0.5em;
    border: 2px solid rgb(14 71 21 / 30%);
    border-radius: 5px;
    outline-offset: 5px;
    font-family: 'DroidSansChinese';
    background: #fff;
}
#completion_prompt_manager_list{
    background: url(https://files.catbox.moe/dvs9ro.jpg);
    background-size: 400px;
    background-repeat: repeat;
    background-position: center;
}
#scoped_scripts_block {
    margin-right: 40px;
}
#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    filter: none;
}
.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
    outline: 2px dashed rgb(29 91 39 / 44%);
}
.justifyCenter {
    justify-content: center;
    margin: 0 auto;
    border: 2px dashed #6B786D88;
    border-radius: 10px;
    background: #f1f1f2;
}
.inline-drawer-content {
    padding-right: 30px;
}
