/* --- 春日宴
感谢代码楼里老师们的代码https://discord.com/channels/1291925535324110879/1380392618336194702
感谢尾老师，类名，输入框伪提示词来自尾老师，hy老师的去除思维链箭头，na老师的悬浮底栏和思维链文字自定义，楼层信息自定义，12的去思维链背景色。感谢NA老师的去阴影，感谢咩野老师的去除全局阴影 --- */

/* --- 1. 字体导入 --- */
@import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond&display=swap');
@import url("https://fontsapi.zeoseven.com/871/main/result.css");
@import url("https://fontsapi.zeoseven.com/6/main/result.css");
@import url("https://fontsapi.zeoseven.com/223/main/result.css");
@import url("https://fontsapi.zeoseven.com/498/main/result.css");

/* --- 2. 核心颜色与样式变量 --- */
:root {
    --color-light-green: #C2E3B8;
    --color-dark-green: #2D5256;
    --color-medium-green: #50795D;
    --color-beige: #EBE4C3;

    --theme-bg: #F8FBF6;
    --panel-bg-color: rgba(220, 235, 215, 0.85);
    --panel-bg-hover: rgba(225, 240, 220, 0.95);
    --card-bg: rgba(250, 253, 249, 0.9);
    --input-bg: rgba(245, 248, 242, 0.9); 
    --text-main: var(--color-dark-green);
    --text-subtle: var(--color-medium-green);
    --border-color: rgba(80, 121, 93, 0.4);
    --accent-color: var(--color-medium-green);
    --selected-color: var(--color-light-green);

    --font-english: "Cormorant Garamond", serif;
    --font-main: "ChillHuoFangSong", var(--font-english);
    --font-quote: "huangkaihuaLawyerfont", var(--font-english);
    --font-code: "XuandongKaishu", monospace;
    --font-title: "平方刘昂乐天体", var(--font-english);

    --border-radius: 16px;
}

/* --- 3. 全局样式 --- */
body { background-color: var(--theme-bg) !important; color: var(--text-main); font-family: var(--font-main); }
#sheld, #character_view { background: transparent !important; margin: 0 auto !important; left: 0; right: 0; border: none !important; box-shadow: none !important; overflow: visible !important; }
#chat { background: transparent !important; align-items: center !important; }

h1, h2, h3, h4, .standoutHeader, .inline-drawer-header, .title_restorable { font-family: var(--font-title); }

/* --- 4. 顶部导航栏 --- */
#top-settings-holder {
    background-color: transparent;
    box-shadow: none;
    border: none;
    backdrop-filter: blur(8px);
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/pUtC/3000X3000/35.jpg/webp');
    background-size: cover;
    background-position: center;
    position: relative;
}
#top-settings-holder::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: var(--panel-bg-color);
    z-index: -1;
}

/* 灯笼挂饰 */
#top-settings-holder::after {
    content: '';
    display: block;
    width: 100px;
    height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/MeAw/2048X2048/2026-01-13_190922.png/webp');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 35px;
    left: 15px;
    opacity: 0.9;
    transform: scale(1.2);
    z-index: 10;
    pointer-events: none;
}

.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }
.drawer-icon.fa-fw { width: 35px; height: 40px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.3s ease; }
.drawer-icon.fa-fw:hover { transform: scale(1.1); }

#ai-config-button .drawer-icon, .drawer-icon.fa-sliders { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/ozCg/2048X2048/2026-01-13_195547.png/webp'); }
#sys-settings-button .drawer-icon, .drawer-icon.fa-plug { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/Jeod/2048X2048/2026-01-13_195535.png/webp'); }
#advanced-formatting-button .drawer-icon, .drawer-icon.fa-font { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/ozCg/2048X2048/2026-01-13_195547.png/webp'); }
#WI-SP-button .drawer-icon, .drawer-icon.fa-book-atlas { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/Jeod/2048X2048/2026-01-13_195535.png/webp'); }
#user-settings-button .drawer-icon, .drawer-icon.fa-user { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/ozCg/2048X2048/2026-01-13_195547.png/webp'); }
#bg_settings_button .drawer-icon, .drawer-icon.fa-image { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/Jeod/2048X2048/2026-01-13_195535.png/webp'); }
#extensions-settings-button .drawer-icon, .drawer-icon.fa-cubes { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/ozCg/2048X2048/2026-01-13_195547.png/webp'); }
#persona-management-button .drawer-icon, .drawer-icon.fa-face-smile { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/ozCg/2048X2048/2026-01-13_195547.png/webp'); }
#character_popup_button .drawer-icon, .drawer-icon.fa-address-card { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/Jeod/2048X2048/2026-01-13_195535.png/webp'); }
#table_database_settings_drawer .drawer-icon, .drawer-icon.fa-bars-progress { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/Jeod/2048X2048/2026-01-13_195535.png/webp'); }
.drawer-content { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/da8X/3806X1978/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D3314714814557869195%26skey%3D%40crypt_5a237c93_d3496fe646274620f780d35935a8675a%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); background-size: cover; background-position: center; border: none; border-radius: var(--border-radius); box-shadow: none; }

/* --- 5. 底部输入栏 --- */
#send_textarea { clip-path: none !important; background-color: transparent !important; border: none !important; }
#send_form { 
    border-radius: 25px;
    background-image: none !important;
    background-color: var(--input-bg) !important;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
    position: relative;
    transition: all 0.5s ease;
    padding: 10px 15px;
}
#send_form:hover { 
    transform: translateY(-5px);
    background-color: rgba(250, 253, 248, 0.95) !important;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
}
#rightSendForm, #leftSendForm { gap: 5px; }
#send_form:has(#send_textarea:focus-visible) { outline: none; }

#options_button::before, #send_but::before, #send_form #extensionsMenuButton::before, #ai_retry_button::before, #auto_completion_button::before { display: none !important; }
#options_button, #send_but, #send_form #extensionsMenuButton, #ai_retry_button, #auto_completion_button { width: 32px; height: 32px; background-size: contain; background-repeat: no-repeat; background-position: center; transition: transform 0.2s ease; font-size: 0; border: none; background-color: transparent; }
#options_button:hover, #send_but:hover, #send_form #extensionsMenuButton:hover, #ai_retry_button:hover, #auto_completion_button:hover { transform: scale(1.1); }
#options_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20250930/KbWN/1046X1319/2025-09-30_154449.png/webp'); }
#send_form #extensionsMenuButton { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20250930/Kzib/1346X944/2025-09-30_154603.png/webp'); }
#send_but { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20250930/mZOJ/1260X1228/2025-09-30_154549.png/webp'); }
#auto_completion_button { background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/bwKF/1966X1276/2026-01-13_142914.png/webp'); }

/* 自定义输入框提示 */
#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "春意入笔端...";
    color: var(--text-subtle);
    position: absolute;
    top: 50%; left: 50%;
    transform: translate(-50%, -50%);
    font-family: var(--font-main);
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
    opacity: 0.8;
}

/* --- 6. 核心：消息布局与样式 --- */
.mes { display: flex; flex-direction: column; align-items: center; gap: 10px; padding: 0 !important; margin: 30px auto !important; width: 90%; max-width: 700px; background: transparent !important; border: none !important; box-shadow: none !important; overflow: visible !important; }

.mes .mesAvatarWrapper { display: grid; grid-template-columns: 100px 1fr; gap: 0px 20px; align-items: center; padding: 20px 30px; border-radius: var(--border-radius); box-shadow: none; width: 100%; max-width: 450px; order: 1; background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/7srn/1872X3157/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D4961583836137998401%26skey%3D%40crypt_5a237c93_d3496fe646274620f780d35935a8675a%26mmweb_appid%3Dwx_webfilehelper.jpg/webp'); background-size: cover; background-position: center; position: relative; overflow: hidden; }
.mes .mesAvatarWrapper::before { content: ''; position: absolute; top: 0; left: 0; right: 0; bottom: 0; background-color: rgba(255,255,255,0.2); z-index: 0; }
.mes .mesAvatarWrapper > * { position: relative; z-index: 1; }

/* 方形拼贴头像 */
.mes .avatar { grid-row: 1 / 3; grid-column: 1 / 2; width: 85px !important; height: 85px !important; flex-shrink: 0; display: block !important; position: relative; transform: rotate(-4deg); transition: transform 0.4s ease; }
.mes .avatar:hover { transform: rotate(0deg); }
.mes .avatar img { 
    border-radius: 10px !important;
    border: none !important; 
    width: 100%;
    height: 100%;
    object-fit: cover;
}
/* 方形头像装饰边框 */
.mes .avatar::before {
    content: '';
    position: absolute;
    top: -4px; left: -4px; right: -4px; bottom: -4px;
    border: 3px solid rgba(255, 255, 255, 0.85);
    border-radius: 12px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.mes .mesAvatarWrapper > .ch_name { grid-row: 1 / 2; grid-column: 2 / 3; display: block !important; font-weight: bold; font-size: 1.2em; color: var(--text-main); margin-bottom: 5px; font-family: var(--font-title); }
.mes .mesAvatarWrapper > .mes_timer, .mes .mesAvatarWrapper > .mesIDDisplay, .mes .mesAvatarWrapper > .tokenCounterDisplay { grid-column: 2 / 3; display: inline !important; font-size: 0 !important; margin-right: 8px; }

.mes::after { content: '一席春日宴，共话好时光'; display: flex; justify-content: center; align-items: center; padding: 5px 15px; background-color: var(--card-bg); border-radius: 15px; font-size: 13px; color: var(--text-subtle); box-shadow: none; order: 2; }

.mes .mes_block { width: 100%; position: relative; order: 3; overflow: visible !important; }
.mes .mes_text { background-color: #F8FBF6; border: 1px solid var(--border-color); border-radius: var(--border-radius); padding: 25px; box-shadow: none; line-height: 1.9; color: var(--text-main); position: relative; overflow: visible !important; }

/* --- 7. 文本样式 & 贴图装饰 --- */
/* 粗体 */
.mes_text strong, .mes_text b { font-family: var(--font-title); color: var(--color-dark-green); font-weight: bold; }
/* 斜体 */
.mes_text em, .mes_text i { font-family: var(--font-quote); font-style: normal !important; color: var(--accent-color); position: relative; padding: 0 0.5em; }
.mes_text em::before, .mes_text i::before { content: '「'; position: absolute; left: 0; top: 0; }
.mes_text em::after, .mes_text i::after { content: '」'; position: absolute; right: 0; top: 0; }
/* 引用块 */
.mes_text blockquote { background-color: rgba(220, 235, 215, 0.4); border-left: 4px solid var(--accent-color); padding: 10px 15px; border-radius: 0 8px 8px 0; margin: 10px 0; }
/* 反引号代码块 */
.mes_text code { font-family: var(--font-code); background-color: rgba(220, 235, 215, 0.5) !important; color: var(--text-main) !important; padding: 2px 6px; border-radius: 6px; border: 1px solid var(--border-color); }

/* 贴图装饰 */
.mes[is_user="true"] .mes_text::before {
    content: ''; display: block; position: absolute;
    top: -20px; left: -25px;
    width: 100px; height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/AOtb/2309X2082/2026-01-13_195115.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 10;
}
.mes[is_user="true"] .mes_text::after {
    content: ''; display: block; position: absolute;
    bottom: -20px; right: -25px;
    width: 100px; height: 100px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/mi7U/2423X2330/2026-01-13_195142.png/webp');
    background-size: contain; background-repeat: no-repeat;
    pointer-events: none; z-index: 10;
}
/* ★ 新增AI消息框顶部贴图 */
.mes[is_user="false"] .mes_text::before {
    content: ''; display: block; position: absolute;
    top: -25px; right: 10px;
    width: 150px; height: 150px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260114/R2eI/4000X2300/2026-01-14_104150.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: top right;
    pointer-events: none; z-index: 10;
}
.mes[is_user="false"] .mes_text::after {
    content: ''; display: block; position: absolute;
    bottom: -95px; right: -60px;
    width: 150px; height: 410px;
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/VICD/660X1830/%E6%9C%AA%E6%A0%87%E9%A2%98-1.png/webp');
    background-size: contain; background-repeat: no-repeat; background-position: bottom right;
    pointer-events: none; z-index: 10;
}

/* --- 8. 元素重定位与自定义文本 --- */
.mes_block .ch_name .name_text, .mes_block .ch_name .timestamp { display: none !important; }
.mes_block .mes_buttons { position: absolute; top: 15px; right: 25px; z-index: 20; display: flex; gap: 8px; }

.mes .mesIDDisplay, .mes .mes_timer, .mes .tokenCounterDisplay { color: var(--text-subtle); white-space: nowrap; }
.mes .mesIDDisplay::before, .mes .mes_timer::before, .mes .mes_timer::after, .mes .tokenCounterDisplay::before, .mes .tokenCounterDisplay::after { font-size: 12px; font-family: var(--font-main); }
.mes[is_user="true"] .mesIDDisplay, .mes[is_user="true"] .mes_timer, .mes[is_user="true"] .tokenCounterDisplay, .mes[is_user="true"] .mesAvatarWrapper > .ch_name { color: var(--text-main) !important; text-shadow: none; }

#chat { counter-reset: ID-counter -1; }
.mes { counter-increment: ID-counter; }
.mesIDDisplay::before { content: '第' counter(ID-counter) '封春信'; font-size: 12px; }
.mes_timer::before { content: "春日慢"; margin-right: 3px; }
.mes_timer::after { content: "提笔书"; margin-left: 3px; }
.tokenCounterDisplay::before { content: "缀成"; margin-right: 3px; }
.tokenCounterDisplay::after { content: "诗行"; margin-left: 3px; }

/* 思维链 */
.mes_reasoning_header h4, .mes_reasoning_header_title {
    font-size: 0 !important;
    visibility: hidden;
}
.mes_reasoning_header h4::before, .mes_reasoning_header_title::before {
    content: "春风拂面，思绪如絮";
    font-family: var(--font-title);
    font-size: 14px !important;
    color: var(--accent-color);
    visibility: visible;
    display: block;
}
#chat .mes .mes_reasoning_header { background: transparent !important; border: none !important; box-shadow: none !important; border-radius: 0; backdrop-filter: none !important; padding: 0 !important; margin-bottom: 5px !important; color: var(--text-subtle) !important; font-size: 0.9em !important; display: inline-flex !important; align-items: center !important; }
.mes_reasoning {
    background-color: var(--card-bg);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    padding: 15px;
    margin-top: -5px;
}

/* --- 9. 全局UI颜色与样式统一 --- */
* { box-shadow: none !important; border-color: var(--border-color); }
.standoutHeader, .inline-drawer-header, .title_restorable, #extensions_settings2 > div, #extensions_settings > div, .openai_logit_bias_preset_form, .range-block-title.justifyLeft.standoutHeader, details > summary, #power-user-options-block h4, #InstructSettingsColumn h4, #SystemPromptColumn h4, #ContextSettings h4, .persona_management_global_settings h4, #character_popup h4, #AdvancedFormatting h4, #rm_group_chats_block h4, div[name*='HandlingToggles'] h4, div[name*='MiscellaneousToggles'] h4 {
    background-image: none !important; background: var(--card-bg) !important; border: none !important; border-radius: var(--border-radius) !important; padding: 8px 12px !important; margin-bottom: 8px !important; color: var(--text-main) !important; }
input[type="text"], input[type="number"], input[type="search"], textarea, select, .text_pole {
    background-color: var(--input-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; border-radius: 8px !important; }
input:focus, textarea:focus, select:focus { border-color: var(--accent-color) !important; }
.menu_button { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; transition: all 0.2s ease; }
.menu_button:hover { background-color: var(--accent-color) !important; border-color: var(--accent-color) !important; color: white !important; }
input[type="checkbox"] { filter: none !important; border: 1px solid var(--border-color) !important; }
input[type="checkbox"]:checked::before { box-shadow: inset 1em 1em var(--accent-color) !important; }
.prompt_order > div { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; }

/* 弹窗 */
dialog.popup { position: relative; overflow: hidden; background-color: transparent !important; border: none !important; }
dialog.popup .popup-body, #dialogue_popup {
    background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/da8X/3806X1978/_cgi-bin_mmwebwx-bin_webwxgetmsgimg__%26MsgID%3D3314714814557869195%26skey%3D%40crypt_5a237c93_d3496fe646274620f780d35935a8675a%26mmweb_appid%3Dwx_webfilehelper.jpg/webp') !important;
    background-size: cover !important;
    background-position: center !important;
    border: none !important;
    border-radius: var(--border-radius) !important;
    position: relative;
}
dialog.popup::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: rgba(248, 251, 246, 0.8);
    border-radius: var(--border-radius);
    z-index: 0;
}
dialog.popup .popup-body, #dialogue_popup { background-color: transparent !important; z-index: 1; }

.menu_button.active, .menu_button.world_set, .menu_button.fav_on { background-color: var(--selected-color) !important; border-color: var(--selected-color) !important; color: var(--text-main) !important; font-weight: bold; }
.menu_button.active:hover, .menu_button.world_set:hover, .menu_button.fav_on:hover { filter: brightness(1.05); }

#left-nav-panel, #left-nav-panel * { color: var(--text-main) !important; text-shadow: none !important; }
#left-nav-panel .standoutHeader, #left-nav-panel .menu_button, #left-nav-panel select { background-color: var(--card-bg) !important; color: var(--text-main) !important; border: 1px solid var(--border-color) !important; }
#left-nav-panel .menu_button:hover { background-color: var(--accent-color) !important; color: white !important; }
#left-nav-panel .menu_button.selected { background-color: var(--selected-color) !important; color: var(--text-main) !important; border-color: var(--accent-color) !important; }

.select2-container--default .select2-selection--multiple .select2-selection__choice { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; color: var(--text-main) !important; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove { color: var(--text-subtle) !important; transition: color 0.2s ease; }
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove:hover { color: var(--text-main) !important; }
.select2-dropdown { background-color: var(--card-bg) !important; border: 1px solid var(--border-color) !important; }

