input[type="range"]::-moz-range-thumb { width: 28px !important; height: 28px !important; background-image: url('https://tc-new.z.wiki/autoupload/1N2_UJVYUo2KjM-_22IFnCfNcKcqEnRmcljopnyJoMs/20260113/C4Zw/1913X1886/2026-01-13_190539.png/webp') !important; background-size: contain !important; background-color: transparent !important; border: none !important; cursor: pointer !important; }
