custom_css:
/* 这是字体部分 */
#chat .mes_block,
#chat .mes_text,
#chat .mes_name,
#chat .name_text {
    font-family: "SimSun", "宋体", "STSong", "Songti SC", serif !important;
    letter-spacing: 0.01em;
    font-weight: 200;       
    font-size: 15.5px;
}

/* 这也是字体部分 */
#chat .mes_block *:not([class*="icon"]):not([class*="fa"]) {
    font-family: "Times New Roman", "SimSun", serif !important;
}

/* ===== 在这调整tk ===== */
:root {
  /* 基础定位 */
  --mes-base-y: 140px;     /* 垂直基准位置 */
  --mes-base-x: 107px;    /* 水平基准偏移 */
  --mes-gap:42px;        /* 卡片间距 */
}

/* 公共样式 - 所有卡片共用 */
.mesIDDisplay,
.tokenCounterDisplay,
.mes_timer {
  position: absolute;
  top: var(--mes-base-y);
  display: inline-flex;
  align-items: center;
  padding: 4px 12px 4px 12px;
  z-index: 20;
}

/* AI 卡片的右对齐（右对齐的统一规则） */
.mes[is_user] .mesIDDisplay    { left: calc(var(--mes-base-x) + var(--mes-gap) * 0); }
.mes[is_user] .tokenCounterDisplay { left: calc(var(--mes-base-x) + var(--mes-gap) * 1); }
.mes[is_user] .mes_timer       { left: calc(var(--mes-base-x) + var(--mes-gap) * 2); }

/*这里是名字*/
.mes .name_text {
  display: inline-block; /* 确保元素类型支持边距 */
  margin-top: -105px;
  margin-left: 30px; /* 同时添加右移 */
  position: relative; /* 使它与 .mes_name 一起定位 */
}

/*这里是时间戳*/
.mes .timestamp {
  position: absolute; /* 绝对定位，以便它可以溢出容器 */
  top: -90px; /* 调整位置向上移动 */
  left: 100px; /* 与用户名对齐 */
  font-size: 12px; /* 调整字体大小，避免溢出时过大 */
  color: #333; /* 可自定义颜色 */
  white-space: nowrap; /* 确保时间戳不换行 */
  z-index: 1; /* 确保它在上层，防止被遮挡 */
}

/* 这里调头图 */
#chat .mes_block::before {
    content: "";
    position: absolute;
    top: -170px;
    left: -15px;
    width: 104%;
    height: 300px;
    background: url('https://sharkpan.xyz/f/xJj3UX/%E6%9C%AA%E6%A0%87%E9%A2%98-12.png') no-repeat top left/contain;
    opacity: 1;
    z-index: 0;
    pointer-events: none;
    transform: rotate(0deg);
    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

/* 固定图案颜色 */
.drawer-icon.closedIcon {
    opacity: 0.8;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
    opacity: 1;
}

/* 设置预设管理器的标题文字颜色 */
#completion_prompt_manager .completion_prompt_manager_header {
  color: var(--SmartThemeBodyColor);
}

/* 正则优化：针对 .regex-script-label 类元素的样式调整，通常用于标签或按钮布局 */
.regex-script-label {
  --flex-wrap: wrap; /* 允许 flex 容器中的子元素换行，覆盖可能的 nowrap 设置 */
  border-radius: var(--genericRadius) !important; /* 设置圆角半径，使用自定义变量 --genericRadius；!important 提升优先级 */
  background-color: var(--buttonFill) !important; /* 设置背景色为自定义变量 --buttonFill；!important 提升优先级 */
  gap: 2px !important; /* 设置 flex 子元素之间的间距为 4 像素；!important 提升优先级 */
  padding-top: 2px !important; /* 设置顶部内边距为 8 像素；!important 提升优先级 */
  padding-bottom: 4px !important; /* 设置底部内边距为 4 像素；!important 提升优先级 */
  margin-right: 35px !important; /* 设置右侧外边距为 24 像素；!important 提升优先级 */
}

/* 强制所有禁用状态文字保持原色 - 增强版 */
*:disabled, 
*[disabled], 
*[aria-disabled="true"], 
*[readonly],
.disabled, 
.inactive,
.disabled *,
.inactive * {
    color: inherit !important;
    -webkit-text-fill-color: inherit !important;
    text-fill-color: inherit !important;
    opacity: 1 !important;
    filter: none !important;
}

/* 覆盖所有输入类型 */
input:disabled,
input[disabled],
textarea:disabled,
textarea[disabled],
select:disabled,
select[disabled] {
    color: inherit !important;
    -webkit-text-fill-color: inherit !important;
    text-fill-color: inherit !important;
    -webkit-opacity: 1 !important;
    -moz-opacity: 1 !important;
    opacity: 1 !important;
}

/* 覆盖浏览器默认阴影 */
input:-webkit-autofill,
input:-webkit-autofill:hover, 
input:-webkit-autofill:focus,
input:-webkit-autofill:active {
    -webkit-text-fill-color: inherit !important;
    box-shadow: 0 0 0 1000px transparent inset !important;
}

/* 覆盖所有伪元素 */
::placeholder,
::-webkit-input-placeholder,
::-moz-placeholder,
:-ms-input-placeholder,
input::placeholder,
textarea::placeholder {
    color: inherit !important;
    opacity: 0.7 !important;
}

/* 特殊框架覆盖 */
[class*="disabled"]:not(.keep-style),
[class*="disabled"] *:not(.keep-style),
[class*="is-disabled"]:not(.keep-style),
[class*="is-disabled"] *:not(.keep-style) {
    color: inherit !important;
    opacity: 1 !important;
}

/* 强制光标样式 */
:disabled {
    cursor: not-allowed !important;
}

