padding-right: 1px !important; /* 增加右侧内边距，避免文字太贴近边缘 */
}

/* 定义文本装饰的CSS变量 */
.mes_text q {
--wave-color: #598422; /* 波浪线颜色  */
--bg-color: rgba(255, 228, 225, 0); /* 背景色 - 半透明浅粉色 */

display: inline; /* 内联元素特性 */
box-decoration-break: clone; /* 跨行装饰样式克隆（关键属性） */
-webkit-box-decoration-break: clone; /* Safari兼容写法 */
padding: 0 0.3em; /* 左右内边距保持文字间距 */

background: var(--bg-color); /* 应用背景色变量 */
line-height: 2; /* 2倍行高保证波浪线空间（核心设置） */

/* 现代文本装饰属性 */
text-decoration-line: underline; /* 下划线类型 */
text-decoration-style: wavy; /* 波浪线样式 */
text-decoration-color: var(--wave-color); /* 应用波浪线颜色 */
text-decoration-thickness: 1.5px; /* 波浪线粗细 */
text-underline-offset: 3px; /* 下划线垂直偏移量（关键定位） */

text-shadow: 0 1px 1px rgba(255,255,255,0.7); /* 文字白边投影提升可读性 */
color: #598422; /* 主文字颜色 - 深灰色 */
}

/* Safari浏览器兼容方案 */
@supports (-webkit-text-decoration: wavy) {
.mes_text q {
-webkit-text-decoration: wavy underline var(--wave-color); /* Safari专用波浪线 */
text-decoration: none; /* 禁用标准下划线避免重复渲染 */
}
}

/* 定义旋转动画（仅处理旋转） */
@keyframes spin {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}

/* 头像定位（保持原始样式） */
.mes[is_user] .avatar {
    position: absolute !important;
    top: 125px !important; /* 上移超出气泡 */
    right: 11.5% !important; /* 机器人头像移到右侧 */
    transform: translateX(-50%) !important; /* 水平居中 */
    z-index: 10 !important; /* 确保显示在装饰元素上方 */
}

/* 头像图片样式（添加旋转） */
.mes .mesAvatarWrapper .avatar img {
    width: 60px !important;  /* 固定尺寸 */
    height: 60px !important;
    transform-origin: center center; /* 旋转中心 */
    animation: spin 15s linear infinite; /* 头像原地旋转 */
}

/* 头像框样式（保持原始定位，不旋转） */
.mes .mesAvatarWrapper .avatar::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: var(--frame-width); /* 190% */
    height: var(--frame-height); /* 240% */
    transform: translate(var(--frame-offset-x), var(--frame-offset-y)); /* 原始偏移：-55%, -45% */
    background-repeat: no-repeat;
    background-position: center center;
    background-size: contain;
    pointer-events: none;
    z-index: 1;
}

/* 用户头像框 */
.mes[is_user='true'] .mesAvatarWrapper .avatar::after {
    background-image: var(--user-frame); /* 应用用户头像框 */
}

/* 机器人头像框 */
.mes[is_user='false'] .mesAvatarWrapper .avatar::after {
    background-image: var(--bot-frame); /* 应用机器人头像框 */
}

/* 顶部菜单栏的背景 */
.nav-menu-bar,
#top-nav,
#top-bar {
  background: rgba(255, 255, 255, 0) !important; /* 固定全透明背景 */
  backdrop-filter: blur(8px);
  border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
  box-shadow: 0 4px 20px rgba(255, 255, 255, 0) !important;
  border-radius: 0 0 12px 12px;
  overflow: visible; /* 允许溢出 */
}

/* 装饰伪元素作为背景 */
.nav-menu-bar::before,
#top-nav::before,
#top-bar::before {
  content: '';
  position: absolute;
  top: 0px; /* 向上溢出 */
  left: 0px; /* 向左溢出 */
  width: calc(100% + 0px); /* 拉伸宽度以溢出 */
  height: calc(100% + 15px); /* 拉伸高度以溢出 */
  background-image: url('https://sharkpan.xyz/f/a6jxFY/%E6%9C%AA%E6%A0%87%E9%A2%98-6.png');
  background-size: 100% 100%; /* 拉伸图片以填充容器 */
  background-position: center; /* 居中显示 */
  background-repeat: no-repeat;
  z-index: -1; /* 置于内容下方 */
  opacity: 1; /* 适度透明以确保内容可读 */
}

/* 移动端适配 */
@media (max-width: 768px) {
  .nav-menu-bar,
  #top-nav {
    background: linear-gradient(
      to bottom,
      rgba(255, 255, 255, 0) 100%,
      rgba(255, 255, 255, 0) 100%
    ) !important;
    border-radius: 0 !important;
    padding: 0px 16px !important;
    box-shadow: 0 0px 10px rgba(150, 180, 170, 0.1) !important;
  }
}

/* 思维链头部容器 - 果绿科技风 */
.mes_reasoning_header {
  background: rgba(138, 189, 74, 0.2); /* 半透明果绿色 */
  border: 1px solid #000; /* 纯黑边框 */
  border-radius: 0; /* 直角设计 */
  padding: 10px 15px 10px; /* 底部额外空间容纳装饰图 */
  margin: 0 10px 15px;
  color: #000; /* 黑色文字 */
  position: relative;
  overflow: visible; /* 允许装饰图溢出 */
}

/* 顶部装饰图容器 */
.mes_reasoning_header::before {
  content: "";
  position: absolute;
  background: url('https://sharkpan.xyz/f/2K5MIQ/%E6%9C%AA%E6%A0%87%E9%A2%98-8.png') no-repeat center center;
  top: -31px; /* 向上溢出位置 */
  left: 50%; /* 水平居中 */
  transform: translateX(-50%); /* 精确居中 */
  width: 80px; /* 宽度调节 */
  height: 60px; /* 高度调节 */
  background-size: contain; /* 自适应缩放 */
  z-index: 10;
}

/* 标题样式 */
.mes_reasoning_header_title {
  font-size: 12px;
  font-weight: bold;
  padding: 0 5px;
  text-shadow: 0 1px 2px rgba(255, 255, 255, 0.5); /* 白色文字阴影增强可读性 */
  letter-spacing: 0.5px;
}

/* 简约直角半透明引用块 */
.mes_text blockquote {
    /* 基础样式 */
    background-color: rgba(255, 255, 255, 0.4); /* 半透明白色背景 */
    border: 1px solid #000; /* 黑色直角边框 */
    color: #333;
    padding: 20px 25px 40px 20px; /* 底部留更多空间给图标 */
    margin: 20px 0;
    position: relative;
    transition: all 0.3s ease;
    line-height: 1.7;

    /* 直角边框（无圆角） */
    border-radius: 0;

    /* 右下角装饰 */
    &::after {
        content: '';
        position: absolute;
        right: 0px;
        bottom: -9px;
        width: 190px;
        height: 190px;
        background: url('https://sharkpan.xyz/f/z611f5/%E6%9C%AA%E6%A0%87%E9%A2%98-1033.png') no-repeat center;
        background-size: contain;
        opacity: 0.7;
        transition: all 0.4s ease;
        z-index: -1;
    }
}

/* 嵌套引用 */
.mes_text blockquote blockquote {
    border-width: 1px;
    background-color: rgba(255, 255, 255, 0.9);
    padding: 15px 20px 35px 15px;

    &::after {
        width: 40px;
        height: 40px;
        bottom: 8px;
        right: 10px;
    }
}

/* 深色模式适配 */
@media (prefers-color-scheme: dark) {
    .mes_text blockquote {
        background-color: rgba(30, 30, 30, 0.8); /* 半透明深灰背景 */
        border-color: #aaa; /* 浅灰边框 */
        color: #e0e0e0;
        box-shadow: 0 4px 8px rgba(0,0,0,0.3);

        &::before {
            color: rgba(255, 255, 255, 0.2);
        }

        &::after {
            filter: brightness(1.5);
        }
    }

    .mes_text blockquote:hover {
        background-color: rgba(40, 40, 40, 0.85);
    }

    .mes_text blockquote blockquote {
        background-color: rgba(50, 50, 50, 0.8);
    }
}

