/* === 波点纹理增强版 === */
input[type="text"],
input[type="number"],
textarea,
.text_pole,
.search_field {
  /* 核心边框 */
  border: 1.8px solid #000;
  border-radius: 0;
  box-sizing: border-box;

  /* 波点纹理背景系统 */
  background-color: rgba(212, 227, 0, 0.2);
  background-image: 
    radial-gradient(rgba(0,0,0,0.2) 10%, transparent 11%);
  background-size: 12px 12px;
  background-position: 6px 6px;
}

/* 极简直线滑轨样式 */
input[type="range"] {
    /* 重置默认样式 */
    -webkit-appearance: none;
    width: 100%;
    background: transparent;

    /* 滑轨基础样式 */
    &::-webkit-slider-runnable-track {
        height: 2px;
        background: rgba(212, 227, 0, 1);
        border-radius: 0;
        box-shadow: none;
    }

    &::-moz-range-track {
        height: 2px;
        background: #1a1a1a;
        border: 0;
    }

    /* 焦点状态增强 */
    &:focus {
        outline: none;

        &::-webkit-slider-runnable-track {
            box-shadow: 0 0 0 2px rgba(0,0,0,0.1);
        }
    }
}

/* 滑块自定义图标 - 垂直定位优化版 */
input[type="range"] {
    /* 确保容器可定位 */
    position: relative;
    margin: 20px 0; /* 增加垂直间距 */
}

input[type="range"]::-webkit-slider-thumb {
    /* 基础样式 */
    width: 50px !important;
    height: 50px !important;
    border: 0px solid #0F2A4A !important;
    border-radius: 50% !important;
    background:  url('https://sharkpan.xyz/f/rQjoU6/64%E5%B0%8F%E6%98%93%E8%AE%BE%E8%AE%A1%E7%B4%A0%E6%9D%90.png') no-repeat center/30px !important;
    box-shadow: 0 2px 4px rgba(15, 42, 74, 0);
    transition: all 0.2s ease;

    /* 垂直定位参数 */
    position: relative;
    top: 50% !important;          /* 垂直居中基准位置 */
    transform: translateY(-50%);  /* 精确垂直居中 */
    margin-top: 0px !important;  /* 微调参数（根据轨道高度调整） */
    margin-bottom: -6px !important;
}

/* 复选框/单选框 */
input[type="checkbox"],
input[type="radio"] {
  border-color: rgba(0, 0, 0, 1) !important; /* 未选中状态边框 */
  background-color: rgba(245, 245, 220, 0.9) !important; /* 未选中状态填充 */
  -webkit-appearance: none; /* 移除默认样式 */
  -moz-appearance: none;
  appearance: none;
  width: 17px; /* 确保大小一致 */
  height: 17px;
  border-radius: 50%; /* 复选框圆角 */
}
input[type="radio"] {
  border-radius: 50%; /* 单选框保持圆形 */
}
input[type="checkbox"]:checked,
input[type="radio"]:checked {
  background-color: rgba(185, 207, 65, 0.5) !important; /* 选中状态填充 */
  border-color: rgba(0, 0, 0, 1) !important; /* 边框 */
}

