custom_css:
/* 本样式二改自Rivelle老师分享的自定义CSS，并参考了Junezz老师的简约卡片，仅发布于Discord旅程社区。请勿二传或商用！ */

/* 请粘贴网址并更改字体名称 */
/* 正文字体 */
@import url("https://fontsapi.zeoseven.com/199/main/result.css");
/* 角色名字字体 */
@import url("https://fontsapi.zeoseven.com/147/main/result.css");

:root {
/* 正文字体名称 */
    --main-body-font: "JiangChengZhiYinTi 400W";
/* 角色名字字体名称 */
    --char-name-font: "JiangChengLvDongSong";

/* 角色图片 */
/* 缩放比例 (1 = 100%, 1.5 = 150%) */
    --banner-scale: 1;
/* 水平位置 (0% = 最左, 50% = 居中, 100% = 最右) */
    --banner-pos-x: 50%;
/* 垂直位置 (0% = 最顶, 50% = 居中, 100% = 最底) */
    --banner-pos-y: 50%;
/* 整体高度 */
    --banner-height: 200px;
}

/* 请替换当前角色名字 */
/* 聊天界面图片 */
#chat .mes[is_user="false"]::before {
    background-image: url("https://imgs.freeimg.cn/s/688b856619e68.jpg");
}
/* 角色选择界面图片 */
body:has(.mes[is_user="false"]) #right-nav-panel-tabs::after {
    background-image: url("https://imgs.freeimg.cn/s/688b856619e68.jpg");
}

:root {
/* 菜单背景模糊度 */
    --blur-degree: 25px;
/* 文字编辑背景，深色改black，浅色改white，数值自改 */
    --popup-bg: color-mix(in srgb, var(--SmartThemeBlurTintColor), white 10%);
/* 角色名字位置 */
    --text-top-padding: calc(var(--banner-height) - 28px);
}

/* 自定义思维链标题，请在引号内输入文字 */
/* 回复中 */
.mes:has(.mes_reasoning:empty) .mes_reasoning_header_title::before {
  content: "♡对方正在回复中…♡";
}
/* 回复完成 */
.mes_reasoning_header_title::before {
  content: "叮！你的外卖已送达(*˘︶˘*).。.:*♡";
}

/* 隐藏头像时请把65px改为0px */
#chat .mes .ch_name {
    margin-left: calc(65px * var(--fontScale));
}

/* 自定义部分结束 */

/* 文本字体 */
body, .text_pole, button, select, code, input, .options-content span, .height100p, p, textarea, #send_textarea, #form_create textarea, .reasoning_edit_textarea, .edit_textarea {
    font-family: var(--main-body-font) !important;
    font-weight: normal;
line-height: 2.0;
}

/* 名字样式 */
.mes .name_text,
#rm_button_selected_ch h2 {
    display: inline-flex !important;
    align-items: baseline;
    font-family: var(--char-name-font);
    font-weight: normal;
    font-size: calc(36px * var(--fontScale));
    font-style: normal;
    line-height: 1;
    text-align: left;

/* 渐变 */
    background: linear-gradient(to bottom,
        var(--SmartThemeChatTintColor),
        var(--SmartThemeBorderColor)
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;

/* 阴影 */
    text-shadow:
    0 0 5px color-mix(in srgb, var(--SmartThemeChatTintColor), transparent 80%),
    0 0 5px color-mix(in srgb, var(--SmartThemeBorderColor), transparent 80%) !important;
}

.mes[is_user="false"] .name_text img,
.mes[is_user="false"] .name_text span,
.mes[is_user="false"] .name_text svg,
.mes[is_user="false"] .icon-svg,
.mes[is_user="false"] .timestamp {
    fill: currentColor;
    height: calc(14px * var(--fontScale));
    aspect-ratio: 1;
    place-self: unset;
    margin-right: calc(5px * var(--fontScale));
    white-space: nowrap;
}

/* 正文编辑按键 */
.mes .mes_button,
.mes .extraMesButtons > div {
    place-self: center baseline;
    align-self: center;
    font-size: calc(15px * var(--fontScale));
    padding: calc(5px * var(--fontScale));
    margin-top: calc(2px * var(--fontScale));
    margin-right: calc(2px * var(--fontScale));
    border-radius: 50%;
    background: linear-gradient(to bottom,
        var(--SmartThemeChatTintColor),
        var(--SmartThemeBorderColor)
    );
    color: rgba(255, 255, 255, 1) !important;
    transition: all 0.3s ease-in-out;
    box-shadow: 0 0 5px var(--SmartThemeBorderColor);
    filter: none;
}

.mes_button {
    opacity: 0.4 !important;
}

.mes_edit_buttons .menu_button,
.mes_reasoning_actions .edit_button {
    background: linear-gradient(to bottom,
        var(--SmartThemeChatTintColor),
        var(--SmartThemeBorderColor)
    );
    color: rgba(255, 255, 255, 1) !important;
    box-shadow: 0 0 5px var(--SmartThemeBorderColor);
    opacity: 0.7;
    filter: none;
}

/* 长截图按钮 */
.fa-camera-alt:before, .fa-camera:before {
    color: rgba(255, 255, 255, 1) !important;
}

/* AI消息边框 */
#chat .mes[is_user="false"] {
    position: relative;
    padding: var(--text-top-padding) calc(20px * var(--fontScale)) calc(20px * var(--fontScale)) !important;
    background:
        linear-gradient(to top, rgba(0, 0, 0, 0) 0%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    border: none !important;
}

/* 用户消息边框 */
#chat .mes[is_user="true"] {
    padding: calc(26px * var(--fontScale)) calc(20px * var(--fontScale)) calc(13px * var(--fontScale)) !important;
    position: relative;
    background:
        linear-gradient(to top, rgba(0, 0, 0, 0) 0%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    border: none !important;
}

/* 聊天界面图片 */
#chat .mes[is_user="false"]::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--banner-height);
    background-size: calc(100% * var(--banner-scale));
    background-position: var(--banner-pos-x) var(--banner-pos-y);
    background-repeat: no-repeat;
    mask-image: linear-gradient(to bottom, white 60%, rgba(255,255,255,0) 100%);
    -webkit-mask-image: linear-gradient(to bottom, white 60%, rgba(255,255,255,0) 100%);
    z-index: 2;
    pointer-events: none;
    border-radius: 8px 8px 0 0;
    transition: background-size 0.3s ease, background-position 0.3s ease;
}

/* 气泡背景颜色 */
#chat .mes::after {
    content: "";
    position: absolute;
    top: 2px;
    right: 2px;
    bottom: 2px;
    left: 2px;
    background:
        linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(0, 0, 0, 0) 50%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    z-index: 1;
    pointer-events: none;
    border-radius: 8px;
}

body.bubblechat #chat .mes[is_user="false"]::before {
    border-radius: 10px;
}

#chat .mes .mes_block,
.mesAvatarWrapper {
    position: relative;
    z-index: 3;
}

/* 角色选择界面 */
body:has(.mes[is_user="false"]) #CharListButtonAndHotSwaps {
    margin-bottom: calc(var(--banner-height) - 40px);
}

body:has(.mes[is_user="false"]) #CharListButtonAndHotSwaps + hr {
    display: none !important;
}

/* 角色选择界面图片 */
body:has(.mes[is_user="false"]) #right-nav-panel-tabs::after {
    content: "";
    position: absolute;
    top: 85px;
    left: 0;
    width: 100%;
    height: var(--banner-height);
    background-size: calc(100% * var(--banner-scale));
    background-position: var(--banner-pos-x) var(--banner-pos-y);
    background-repeat: no-repeat;
    mask-image: linear-gradient(to bottom, white 60%, rgba(255,255,255,0) 100%);
    -webkit-mask-image: linear-gradient(to bottom, white 60%, rgba(255,255,255,0) 100%);
    pointer-events: none;
    z-index: -1;
    transition: background-size 0.3s ease, background-position 0.3s ease;
}

body.big-avatars:has(.mes[is_user="false"]) #right-nav-panel-tabs::after {
    top: calc(120px * var(--fontScale));
}

/* 头像区域 */
#chat .mes .avatar img {
    width: calc(55px * var(--fontScale));
    height: calc(55px* var(--fontScale));
}

#chat .mes .timestamp {
    margin-left: calc(2px * var(--fontScale));
    font-size: calc(11px * var(--fontScale));
}

#chat .mes .ch_name {
    margin-bottom: calc(20px * var(--fontScale));
}

.alignItemsBaseline:has(> .name_text):has(> .timestamp) {
    display: flex;
    flex-wrap: wrap;
    align-items: baseline;
    row-gap: calc(9px * var(--fontScale));
    column-gap: calc(7px * var(--fontScale));
    opacity: 0.75;
}

.alignItemsBaseline:has(> .name_text):has(> .timestamp) > .timestamp {
    flex-basis: 100%;
    margin-top: calc(-5px * var(--fontScale));
}

/* Timer & Token & ID */
.mes .mesAvatarWrapper {
    pointer-events: none;
    position: absolute;
    margin-top: calc(39px * var(--fontScale));
    right: calc(26px * var(--fontScale));
    left: calc(10px * var(--fontScale));
    display: flex;
    justify-content: flex-end;
    align-items: end;
    gap: calc(8px * var(--fontScale));
    z-index: 4;
}

.mes .mesAvatarWrapper .avatar img {
    pointer-events: auto;
    border: none;
    box-shadow: none;
    outline: 1px solid var(--SmartThemeUnderlineColor);
    outline-offset: 3px;
}

.mes .mesAvatarWrapper .avatar {
    pointer-events: none;
    position: absolute;
    top: calc(-39px * var(--fontScale));
    left: calc(14px * var(--fontScale));
}

.mes .mes_timer,
.mes .mesIDDisplay,
.mes .tokenCounterDisplay {
    color: var(--SmartThemeBodyColor);
    opacity: 0.75;
    white-space: nowrap;
    font-size: calc(11px * var(--fontScale));
    font-weight: 500;
}

/* 菜单背景颜色 */
#left-nav-panel,
#completion_prompt_manager_popup,
#rm_api_block,
#AdvancedFormatting,
#WorldInfo,
#user-settings-block,
#Backgrounds,
#rm_extensions_block,
.drawer-content.openDrawer,
#right-nav-panel {
    background:
        linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(0, 0, 0, 0) 50%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    border: none;
    backdrop-filter: blur(var(--blur-degree)) !important;
    padding: 10px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
}

/* Icon */
.drawer-icon,
.fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid, .fab, .far, .fas,
#form_character_search_form .menu_button, #GroupFavDelOkBack .menu_button, .avatar-container .menu_button,
#rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div {
    color: var(--SmartThemeUnderlineColor) !important;
    font-size: calc(17px * var(--fontScale)) !important;
}

/* 底栏Icon */
#rightSendForm>div,
#leftSendForm>div,
#rightSendForm > div > [class*="fa-"],
#rightSendForm > div > i,
#quickPersona {
    font-size: calc(20px * var(--fontScale)) !important;
}

/* 箭头Icon */
.inline-drawer-icon, .right_menu_button {
    filter: none;
    color: var(--SmartThemeUnderlineColor);
    border-radius: 50%;
    line-height: 1;
}

/* Menu Button */
.welcomeRecent .recentChatList .recentChat .chatActions button,
.extensions_info .extension_block .menu_button,
#left-nav-panel .menu_button,
#rm_api_block .menu_button,
#AdvancedFormatting .menu_button,
#WorldInfo .menu_button,
#user-settings-block .menu_button,
#completion_prompt_manager_popup .completion_prompt_manager_popup_entry_form_footer #completion_prompt_manager_popup_entry_form_save,
#completion_prompt_manager_popup .completion_prompt_manager_popup_entry_form_footer #completion_prompt_manager_popup_entry_form_close,
#rm_extensions_block .menu_button,
.drawer-content.openDrawer .menu_button,
#right-nav-panel .menu_button {
    background: transparent !important;
    border: 1px solid var(--SmartThemeUnderlineColor) !important;
}

/* 悬停发光 */
.menu_button:hover {
    box-shadow: 0 0 12px var(--SmartThemeUnderlineColor) !important;
    filter: brightness(1.5);
}

/* Menu Button Color */
select.chat_world_info_selector.wide100p, select.character_world_info_selector.wide100p,
.avatar-container.selected,
body.bubblechat .welcomePanel,
.regex-script-label,
#dialogue_del_mes_ok,
.menu_button.popup-button-ok,
#bulk_tag_popup_reset, #bulk_tag_popup_remove_mutual, #dialogue_popup_ok,
.select_chat_block[highlight],
.BGSampleTitle,
#form_create textarea,
#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item .qr--content .qr--set-itemMessage,
.neo-range-input,
.textarea_compact,
.drawer-content select,
.range-block-counter input,
.menu_button,
.api_button,
.text_pole,
.select2-container--default .select2-selection--single,
.select2-container--default .select2-selection--multiple,
.paginationjs-nav a,
.paginationjs-go-button > input,
.paginationjs-go-button > .menu_button {
    background:
        linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 20%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    border: none !important;
    filter: none !important;
    box-shadow: none !important;
}

.script-item,
.shortcut-input,
#extensionSideBar #extensionSideBarContainer .sideBarItem.selected,
.sheet-header-cell-top,
.dynamic-popup-menu,
.title-item-active,
#quick-reply-menu-menu-style-panel,
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message,
.quick-reply-item {
    background:
        linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 20%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    border: none !important;
    filter: none !important;
    box-shadow: none !important;
    background-color: var(--SmartThemeUserMesBlurTintColor) !important;
}

/* 弹窗 */
.st-screenshot-popup,
.reasoning_edit_textarea, .edit_textarea,
.select2-container--open .select2-dropdown--below,
#bulk_tag_popup, #dialogue_popup,
#dialogue_del_mes,
#floatingPrompt,
#cfgConfig,
#logprobsViewer,
#movingDivs>div,
#select_chat_popup,
.options-content,
.list-group,
.popup {
    background-color: var(--SmartThemeBlurTintColor) !important;
    border: none !important;
    box-shadow: none;
}

#options, .popup .popper-modal {
    background-color: transparent !important;
}

#options, #extensionsMenu, .popup .popper-modal {
    box-shadow: none;
}

.edit_textarea, .height100p,
.st-screenshot-option {
    background-color: var(--popup-bg) !important;
    border: none;
}

/* 标题 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader,
.inline-drawer-header {
    background-image: none !important;
    border: none;
}

/* 滑动条 */
input[type="range"],
input[type="range"]:disabled {
    filter: none;
    background: color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 30%) !important;
    box-shadow: none !important;
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    background-color: var(--SmartThemeUnderlineColor);
    height: calc(12px * var(--fontScale));
    width: calc(12px * var(--fontScale));
    border-radius: 50%;
    cursor: pointer;
    box-shadow: 0px 0px 7px var(--SmartThemeUnderlineColor);
    border: none;
}

/* Checkbox */
input[type="checkbox"] {
    background-color: transparent;
    border: 1px solid var(--SmartThemeBodyColor);
    border-radius: 4px;
    box-shadow: none;
    opacity: 0.85;
}

input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--SmartThemeBodyColor);
    opacity: 0.85;
}

/* 预设 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    border: 1px solid var(--SmartThemeBodyColor);
    border-radius: 6px;
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
    filter: none;
    opacity: 0.65;
    color: var(--SmartThemeBodyColor) !important;
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: color-mix(in srgb, var(--SmartThemeBodyColor), transparent 40%);
}

#completion_prompt_manager .completion_prompt_manager_header_advanced span,
#completion_prompt_manager .completion_prompt_manager_header div {
    filter: none;
    color: color-mix(in srgb, var(--SmartThemeBodyColor), transparent 30%);
}

/* 世界书 */
span.select2.select2-container .select2-selection__choice__remove,
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display,
.select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    background-color: transparent !important;
    box-shadow: none;
    filter: none;
    border-right: 0px;
}

.select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    border: 1px solid var(--SmartThemeBodyColor);
    font-family: var(--main-body-font) !important;
}

.standoutHeader~.inline-drawer-content {
    background-color: transparent;
    border: none;
}

.wi-card-entry {
    background: transparent !important;
    opacity: 1 !important;
    border: 1px solid var(--SmartThemeBodyColor);
    border-radius: 6px;
    padding: 5px 10px 8px;
    margin: 10px 0px;
    transition: background 1s ease-in-out;
}

.wi-card-entry:has(.killSwitch.fa-toggle-on) {
    background:
        linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 20%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor) !important;
}

.wi-card-entry .text_pole,
.wi-card-entry select {
    background: transparent !important;
    border: 1px solid var(--SmartThemeBodyColor) !important;
    box-shadow: none !important;
}

.disabledWIEntry {
    opacity: 1 !important;
    filter: none !important;
}

.world_entry .killSwitch.fa-toggle-on {
    color: var(--SmartThemeUnderlineColor);
}

/* 背景 */
.bg_example {
    box-shadow: none;
    border: none;
}

.bg_example.locked {
    outline: 2px solid var(--SmartThemeUnderlineColor);
}

/* user人设 */
.avatar img {
    outline: 1px solid var(--SmartThemeUnderlineColor);
    outline-offset: 3px;
    box-shadow: none !important;
    border: none;
}

.avatar-container.selected {
    border: 1px solid var(--SmartThemeBodyColor) !important;
    border-radius: 6px;
    transition: background 1s ease-in-out;
}

.avatar-container .avatar {
    margin: 5px;
}

/* 正则 */
.regex-script-label:has(.disable_regex:checked) {
    background: transparent;
}

.regex-script-label {
    border: 1px solid var(--SmartThemeBodyColor) !important;
    border-radius: 6px !important;
}

@media (max-width: 600px) {
    .regex-script-label {
        flex-wrap: wrap;
        border-radius: none !important;
        gap: 4px !important;
        padding-top: 8px !important;
        padding-bottom: 4px !important;
        margin-right: 100px !important;
    }
    .regex-script-label > div:last-of-type {
      flex-basis: 100%;
      width: 100%;
    }
    .regex-script-label:has(.disable_regex:checked) {
      background-color: transparent !important;
    }
}

/* 角色选择界面 */
.character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name,
.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
    color: var(--SmartThemeUnderlineColor);
    outline: none !important;
    text-decoration: 1px underline solid var(--SmartThemeUnderlineColor);
    text-underline-offset: 3px;
    overflow: visible;
}

#rm_print_characters_block .character_select_container, #rm_print_characters_block .group_select_container {
    padding-left: 2px;
}

.hotswap .avatar img {
    outline: 0px;
}

.avatar_div .avatar {
    margin-left: 3px;
}

#rm_print_characters_pagination {
    padding: 3px 0px;
}

#HotSwapWrapper > div {
    overflow: auto hidden;
    flex-wrap: nowrap;
    justify-content: flex-start;
    gap: 8px;
}

#HotSwapWrapper {
    overflow: hidden;
}

#rm_button_selected_ch h2 {
    white-space: normal !important;
    display: block !important;
    line-height: 1.2;
    word-break: break-all;
    margin-left: -3px;
    padding-bottom: 0 !important;
}

#form_character_search_form .menu_button, #GroupFavDelOkBack .menu_button, .avatar-container .menu_button {
    font-size :calc(15px * var(--fontScale)) !important;
}

.flex1.height100p {
    background-color: transparent !important;
}

#right-nav-panel .world_set, .toggleEnabled, #right-nav-panel .fav_on {
    color: color-mix(in srgb, var(--SmartThemeUnderlineColor), white 50%) !important;
}

/* 分隔线 */
hr {
    display: none !important;
}

/* Chat */
#chat {
    background-color: transparent !important;
    padding: 0px;
    border: none;
}

#chat .mes.selected {
    background: white !important;
}

/* Swipe */
#chat .mes:last-of-type {
    padding-bottom: calc(70px * var(--fontScale)) !important;
}

#chat .swipe_left {
    bottom: calc(35px * var(--fontScale));
    left: calc(20px * var(--fontScale));
}

#chat .swipeRightBlock {
    bottom: calc(15px * var(--fontScale));
    right: calc(10px * var(--fontScale));
}

#chat .swipe_left,
#chat .swipeRightBlock {
    opacity: 0.85 !important;
    z-index: 7;
}

/* Main Text */
.mes_text {
    line-height: 1.6;
    letter-spacing: 0.3px;
}

.mes_text p {
    margin-top: 0;
    margin-bottom: 0.8em;
}

.mes_text p:last-child {
    margin-bottom: 0;
}

/* 引用文本 */
.mes_text q {
    text-shadow: 0px 0px 5px var(--SmartThemeShadowColor) !important;
}

/* 斜体、下划线、代码块 */
.mes_text em,
code:not(pre code.hljs) {
    color: var(--SmartThemeEmColor);
    background-color: transparent !important;
    background-image:
    linear-gradient(to left, var(--SmartThemeUserMesBlurTintColor) 0%, var(--SmartThemeBotMesBlurTintColor) 100%);
    border: none;
    border-radius: 6px;
    margin-right: 3px;
    padding: 2px 5px;
    font-style: normal;
}

.mes_text pre code, .mes_reasoning {
    color: var(--SmartThemeBodyColor) !important;
    background: transparent !important;
    position: relative;
    border: 2px solid;
    border-image: linear-gradient(to top, var(--SmartThemeBorderColor), var(--SmartThemeChatTintColor)) 1;
    clip-path: inset(0 round 25px);
    margin: 20px 0px;
    padding: 20px;
}

.hljs-tag {
    color: var(--SmartThemeBodyColor) !important;
}

/* 思维链 */
.mes_reasoning_header {
    background:
        linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 20%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    padding-right: 14px;
}

.mes_reasoning_details .mes_reasoning_arrow {
    display: none;
}

.mes_reasoning_header_title {
    font-size: 0;
}

.mes:has(.mes_reasoning:empty) .mes_reasoning_header_title::before, .mes_reasoning_header_title::before {
    font-size: var(--mainFontSize);
    letter-spacing: 0.3px;
}

/* 扩展 */
.title-item {
   background-color: transparent !important;
    color: var(--SmartThemeBodyColor) !important;
}

.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder #qr--modal-message,
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor #qr--modal-send_textarea {
    color: var(--SmartThemeBodyColor) !important;
    border: none !important;
    background-color:  color-mix(in srgb, var(--SmartThemeBlurTintColor), white 10%);
}

.extensions_info .extension_block {
    border: none;
}

/* 表格 */
#table_button_container, .select2-results__option--selected.select2-results__option::before, .select2-results__option::before, .sheet-cell-other, .history-tab.active {
    background-color: transparent !important;
}

.history-cell-item {
    background-color:  color-mix(in srgb, var(--SmartThemeBlurTintColor), white 10%) !important;
}

#extensionsMenu>.extension_container>div, #extensionsMenu>div:not(.extension_container), .options-content a, .list-group-item {
    opacity: 0.85 !important;
}

.tableDom td, .tableDom th {
    border: 1px solid var(--SmartThemeBodyColor) !important;
    color: var(--SmartThemeBodyColor) !important;
}

.sheet-header-cell-left {
    background:
        linear-gradient(to top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 20%, var(--SmartThemeBotMesBlurTintColor) 100%),
        var(--SmartThemeUserMesBlurTintColor);
    background-color: var(--SmartThemeUserMesBlurTintColor) !important;
}

/* Tor Bar Extension */
#extensionTopBar {
    border: none !important;
    border-radius: 0px !important;
}

#extensionConnectionProfiles, #extensionSideBar.visible, #extensionConnectionProfiles.visible {
    border: none !important;
    border-radius: 0px !important;
}

#extensionSideBar #extensionSideBarContainer .sideBarItem, .select_chat_block {
    border: 1px solid var(--SmartThemeBodyColor) !important;
    border-radius: 6px !important;
    margin: 5px;
}

#extensionTopBarSearchInput:placeholder-shown {
    opacity: 1 !important;
}

#extensionTopBar .right_menu_button {
    opacity: 0.5;
}

/* Persona Switch */
#quickPersonaImg, .quickPersonaMenuImg {
    width: 80% !important;
    height: 80% !important;
}

#quickPersonaImg, .quickPersonaMenuImg {
    outline: 1px solid var(--SmartThemeUnderlineColor) !important;
    outline-offset: 3px;
    box-shadow: none !important;
    border: none;
}

#quickPersonaMenu li {
    align-items: center !important;
}

/*  输入助手 */
.integrated-button-row, .button-preview, .shortcut-clear-btn {
    background-color: transparent !important;
    border: 1px solid var(--SmartThemeBodyColor) !important;
    border-radius: 6px !important;
}

/* QR助手 */
#quick-reply-menu {
    background-color: var(--SmartThemeBlurTintColor) !important;
    border:  1px solid var(--SmartThemeBodyColor) !important;
    box-shadow: none !important;
}

.quick-reply-item {
    background-color: var(--SmartThemeUserMesBlurTintColor) !important;
    color: var(--SmartThemeBodyColor) !important;
}

.quick-reply-list ,.quick-reply-style-group {
    border: none !important;
}

.quick-reply-list-title {
    border-bottom: 1px solid var(--SmartThemeBodyColor) !important;
    color: var(--SmartThemeBodyColor) !important;
}

.quick-reply-empty {
    color: var(--SmartThemeBodyColor) !important;
    opacity: 0.5;
}

.qr-color-text-input {
    background-color: transparent !important;
    border: none !important;
}

/* QR */
#qr--bar .qr--button {
    background: transparent !important;
    color: var(--SmartThemeUnderlineColor) !important;
    opacity: 1 !important;
    border: 1px solid var(--SmartThemeUnderlineColor) !important;
    border-radius: 6px !important;
}

/* Send Form */
#leftSendForm,
#rightSendForm {
    align-items: center;
    transition: all 0.3s ease-in-out;
}

#send_form, body.no-blur #send_form {
    border-radius: 0px;
    padding: 6px 10px 10px;
    border: none;
}

#send_textarea {
    font-size: calc(var(--mainFontSize) * 1.1) !important;
    line-height: 1.4;
    letter-spacing: 0.3px;
}

#send_form .mes_stop {
    padding: 0px 7px !important;
}

/* 初始界面 */
.welcomeRecent .recentChatList .recentChat {
    padding: 10px;
    margin: 5px 0px;
}

/* Spacing */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    padding: 5px 1px !important;
}

.text_pole, select,
.range-block-range-and-counter,
.range-block,
.range-block-title,
.WIEntryHeaderTitleMobile,
h1, h2, h3, h4,
#user-settings-block .flex-container {
    padding-bottom: 5px !important;
}

#form_sheld {
    margin: 0 !important;
}

#sheld {
    top: var(--bottomFormBlockSize);
    height: calc(100dvh - var(--bottomFormBlockSize));
    max-height: calc(100dvh - var(--bottomFormBlockSize));
}

#chat .mes + .mes {
    margin-top: 5px;
}

/* Font Opacity */
small, input.neo-range-slider[type="range"] {
    opacity: 0.85 !important;
}

/* Other */
#top-bar {
    box-shadow: none !important;
}

.toggle-description {
    color: var(--SmartThemeUnderlineColor);
}

.paginationjs-pages ul li.disabled a {
    opacity: 0.85 !important;
    border: 1px solid var(--SmartThemeUnderlineColor);
    color: var(--SmartThemeUnderlineColor);
}

/* Hide Scrollbar */
*::-webkit-scrollbar {
    display: none;
}

* {
    scrollbar-width: none;
    -ms-overflow-style: none;
}

/* Text Alignment */
.mes_block .mes_text {
    text-align: justify !important;
    padding-left: 0px !important;
    padding-right: 3px !important;
}

.mes_block {
    padding-left: 3px !important;
}

