custom_css:
/* ==========================================================
   样式作者：@Junezz
   版本：v1.55 (已修改：头像放大+印章移位)
   发布于：类脑 Discord 社区

   本样式仅供个人使用与参考，禁止任何形式的商用。
   可二改不可二传。请保留本注释和原作者署名。
   ========================================================== */


/* 字体 */

@import url("https://fontsapi.zeoseven.com/256/main/result.css");

body {
    font-family: "Huiwen-mincho";
    font-weight: normal;
}


#top-bar {
  box-shadow: none !important;
}

code, #send_textarea, input, select, button, .text_pole {
  font-family: inherit;
}

  :root {
    --mainFontSize: calc(var(--fontScale)* 16px); /* 主字体大小 */

    /* 1. 修改：放大头像尺寸 */
    --avatar-base-width: 90px; /* 原48px，已改大 */
    --avatar-base-height: 90px; /* 原48px，已改大 */

    --genericRadius: 8px; /* 通用圆角 */
    --warning: #ce3636;
    --crimson70a: #9fc1d663;
    --crimson-hover: color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 80%) !important;
    --standardIconSize: calc(var(--mainFontSize)* 1);
    --bottomFormIconSize: calc(var(--mainFontSize)* 1.2);
    --bottomFormBlockPadding: calc(var(--mainFontSize) / 1.3);
    --scrollBarWidth: 5px;
    --pagePadding: calc(var(--scrollBarWidth) + 3px);
    --boxShadowSubtle: 0 2px 6px rgba(0, 0, 0, 0.16); 

    /* ==== 所有颜色定义 ==== */
    --utilityColor: var(--black50a); /* 说明文字 */
    --navBackgroundColor: color-mix(in srgb, var(--SmartThemeUnderlineColor), white 70%); /* 顶栏和底栏背景颜色 */
    --buttonFill: color-mix(in srgb, var(--SmartThemeUnderlineColor), white 97%); /* 按钮背景颜色 */
    --menuBackgroundColor: var(--navBackgroundColor); /* 菜单背景颜色 */
    --accentColor: var(--SmartThemeBorderColor); /* 装饰色1 思维链，文字装饰，段落装饰，头像滑轮条等 */
    --accentColorOverlay: color-mix(in srgb, var(--SmartThemeBorderColor), transparent 76%); /* 装饰色2半透明 消息里头像和名字的背景颜色 */
    --scrollBarColor: var(--SmartThemeUnderlineColor); /* 滚动条颜色 */
  }

  /* 聊天框背景 */
  #chat {
    padding: var( --pagePadding) 3px var( --pagePadding) var( --pagePadding);
    overflow-y: scroll;
    border-radius: 0px 0px var(--genericRadius) var(--genericRadius);
    border: none;
  }

  #top-bar {
    background-color: transparent !important;
    height: var(--topBarBlockSize);
  }

  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4,
  .standoutHeader,
  .userSettingsInnerExpandable, .bg_example {
    border: transparent !important;
  }

  #left-nav-panel,
  #rm_api_block,
  #AdvancedFormatting,
  #WorldInfo,
  #user-settings-block,
  #Backgrounds,
  #rm_extensions_block,
  .drawer-content.openDrawer,
  #right-nav-panel {
    background-color: var(--menuBackgroundColor) !important;
    border-radius: 0px 0px var(--genericRadius) var(--genericRadius);
    padding: 8px;
    border: none;
  }

  .drawer-content {
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    /*box-shadow: 0 1px 0 0 var(--SmartThemeUnderlineColor);*/
    box-shadow: var(--boxShadowSubtle);
    padding-top: 8px;
  }

  /* Icon Sizes & Colors */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid,
  #rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div,
  #completion_prompt_manager .completion_prompt_manager_footer a,
  .fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid, .fab, .far, .fas {
    font-size: var(--standardIconSize);
    color: var(--SmartThemeUnderlineColor);
  }

  /* 预设icons */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
    color: var(--SmartThemeBodyColor)  !important;
  }

  /* 对话框icons */
  .mes_button {
    color: var(--SmartThemeBodyColor);
  }

  /* 底栏icon */
  #rightSendForm>div, #leftSendForm>div {
    font-size: var(--bottomFormIconSize);
    background-color: var(--navBackgroundColor) !important;
    color: var(--SmartThemeUnderlineColor);
  }

  /* 底栏icon 对齐 */
  #leftSendForm, #rightSendForm {
    align-items: center;
  }

  /* 标题样式 */

  /* 清除设置页相关模块标题的背景图，并精简上下 padding */
  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4, .standoutHeader{
    background-image: none !important;
    padding: 2px 0 !important;
  }

 /* 原始元素样式 */
  .standoutHeader:not(h4),
  #rm_PinAndTabs,
  #title_api,
  #rm_api_block .flex-container.alignItemsBaseline,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
  #WorldInfo .alignitemscenter:not(#world_popup *),
  #user-settings-block div[name="userSettingsRowOne"],
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter),
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p {
    position: relative; /* 必须要有 */
    padding-left: 12px;
    margin-top: calc(var(--mainFontSize) * 0.2) !important;
    margin-bottom: calc(var(--mainFontSize) * 0.2) !important;
  }

  /* 小竖线 */
  #rm_PinAndTabs::before,
  #title_api::before,
  #rm_api_block .flex-container.alignItemsBaseline::before,
  #AdvancedFormatting .flex-container.alignItemsBaseline:not(h3)::before,
  #WorldInfo .alignitemscenter:not(#world_popup *)::before,
  #user-settings-block div[name="userSettingsRowOne"]::before,
  #rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter)::before,
  #top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p::before {
    content: '';
    position: absolute;
    left: 0;
    top: 6px;
    bottom: 6px;
    width: 3px;
    background-color: var(--SmartThemeUnderlineColor);
    border-radius: 1px;
  }

  /* === 统一标题文字的行高（使阅读更舒适）=== */
  .standoutHeader strong,
  #right-nav-panel h2,
  .drawer-content > h3,
  .drawer-content .flex-container h3:first-of-type:not(#Backgrounds .textAlignCenter),
  .drawer-content .flex-container .flex-container h3:first-of-type,
  .popup h3:not(dialog *) {
    line-height: 1.6;
  }

  .flex-container {
    gap: 8px;
  }

  .redOverlayGlow {
    color: #9c2c2c;
  }

  /* AI聊天气泡样式 */
  body.bubblechat .mes[is_user="false"]{
    margin-top: 8px;
    margin-bottom: 16px;
  }

  /* menu颜色 */
  .ui-widget.ui-widget-content,
  #bulk_tag_popup,
  #dialogue_popup,
  .popup,
  #options,
  #extensionsMenu,
  .popup .popper-modal,
  .select2-dropdown,
  #select_chat_popup,
  #right-nav-panel,
  #left-nav-panel,
  #floatingPrompt,
  #cfgConfig,
  #logprobsViewer,
  #movingDivs > div {
    background-color: var(--menuBackgroundColor);
    box-shadow: var(--boxShadowSubtle);
    border: none;
  }

  .extensions_toolbar {
    background-color: var(--menuBackgroundColor);
  }

  .options-content, .list-group, .extensions_info .extension_block, #completion_prompt_manager_popup {
    border: none;
  }

  /* 背景添加页面 */
  .bg_example.no-border.no-shadow.add_bg_but.interactable {
    background-color: var(--SmartThemeUnderlineColor);
  } 

  /* 其他阴影 */
  .bg_example, #toast-container>div,
  .bg_example:hover, #toast-container>div:hover {
    box-shadow: var(--boxShadowSubtle);
  }

   /* 角色卡添加tag */
  body .ui-widget-content li:hover {
    background-color: var(--SmartThemeUnderlineColor);
  }

  .tag {
    background-color: var(--buttonFill);
  }

  /* 角色卡删除键 */
  .red_button:hover {
    background-color: var(--crimson-hover) !important;
  }

   /* 世界书 */
  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-results__option::before,
  .select2-results__option--selected.select2-results__option::before {
    background-color: var(--buttonFill);
  }

  .select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice,
  .select2-container--default .select2-selection--multiple .select2-selection__choice,
  .standoutHeader~.inline-drawer-content,
  .regex-script-label {
    border: none !important;
  }

  .select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display,
  span.select2.select2-container .select2-selection__choice__remove,
  .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    background-color: var(--accentColorOverlay);
  }

  .select2_multi_sameline+span.select2-container .select2-selection--multiple .select2-search__field {
    background-color: transparent !important;
  }

  .standoutHeader~.inline-drawer-content {
    background-color: var(--menuBackgroundColor);
  }

  .wi-card-entry {
    margin-top: 12px;
    padding: 8px;
    background-color: var(--accentColorOverlay);
    border: none;
  }

  .paginationjs-pages ul li a, .select_chat_block {
    border: none;
  }

  /* 聊天气泡 */
  body.bubblechat .mes {
    border: none !important;
    border-radius: var(--genericRadius);
    padding: 16px 16px;
    display: flex;
    flex-direction: column;
  }

  /* 头像 */
  .mesAvatarWrapper {
    display: flex;
    flex-direction: row; /* 从竖排改成横排 */
    align-items: center; /* 垂直居中 */
    gap: 8px; /* 头像和气泡之间的间距 */
    background-color: var(--accentColorOverlay);
    padding: 8px; /* 间距 */
    padding-right: 12px; /* 右侧间距 */
    border-top-left-radius: var(--genericRadius);
    border-top-right-radius: var(--genericRadius);
    overflow: hidden;
    width: 100%;
  }

    /* 头像处理 */
  .mes .avatar img{
   padding: 0px;
  }

  body.square-avatars .avatar, body.square-avatars .avatar img{
    border-radius: var(--genericRadius) !important;
  }

  .avatar img {
    border: none;
    box-shadow: none !important;
  }

  body.big-avatars .avatar {
    align-items: self-start !important;
  }

  /* 名字 */
  .mes_block .ch_name {
    background-color: var(--accentColorOverlay);
    border-bottom-left-radius: var(--genericRadius);
    border-bottom-right-radius: var(--genericRadius);
    padding: 8px; /* 间距 */
    margin-bottom: 12px;
  }

  /* 正文 */
  .mes_text {
    padding: 0px 4px !important;
    line-height: calc(var(--mainFontSize) + .7rem);
  }

  .mes_text p {
    margin-bottom: 12px;
  }

  .last_mes .mesAvatarWrapper {
    padding-bottom: 8px !important;
  }

  .mes_block {
    padding-left: 0px !important;
  }

  /* 正文edit按键 */
  .mes_edit_buttons {
    filter: drop-shadow(0px 0px 2px var(--buttonFill));
  }

  .mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
    color: var(--SmartThemeBodyColor);
  }

   /* 引用 */
  .mes_text blockquote, .mes_reasoning blockquote {
    background-color: var(--accentColorOverlay);
    border-left: var(--SmartThemeUnderlineColor) 3px solid;
    padding: 8px;
    margin-bottom: 8px;
  }

  /* extension */
  .extensions_info .extension_disabled {
    color: var(--SmartThemeEmColor);
  }

  /* 快捷回复 */
  .menu_button  {
    filter: none !important;
    background-color: var(--buttonFill) !important;
    border-radius: 4px !important;
    border:transparent !important;
  }

  .menu_button:hover {
    background-color: var(--crimson-hover) !important;
  }

  /* 输入助手 */
  .integrated-button-row {
    background-color: var(--menuBackgroundColor) !important;
  }

  /* 发送&top bar */
  #top-bar, #send_form {
    background-color: color-mix( in srgb, var(--navBackgroundColor) 50%, transparent 50%) !important;
    padding: 6px 8px;
    border: none;
  }

  body.no-blur #top-bar, body.no-blur #send_form {
    background-color: var(--navBackgroundColor) !important;
    padding: 6px 8px;
    border: none;
  }

  #send_form, body.no-blur #send_form {
    border-radius: var(--genericRadius) var(--genericRadius) 0px 0px;
  }

   /* 顶栏阴影 */
  body.no-blur #top-bar {
    box-shadow: none;
  }

   /* 发送栏阴影 */
   body.no-blur #send_form {
    box-shadow: var(--boxShadowSubtle);
  }

  .menu_button.menu_button_default {
    box-shadow: none !important;
  }

  /* Checkbox */
  input[type='checkbox'] {
    background-color: var(--buttonFill);
    border: transparent;
    box-shadow: none;
    border-radius: 2px;
  }

  input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--SmartThemeEmColor);
  }

  input[type="checkbox"]:disabled {
    background-color: color-mix(in srgb, var(--buttonFill) 20%, black 10%);
    opacity: 0.7;
  }

  /* 预设 */
  #completion_prompt_manager_list {
    background-color: var(--menuBackgroundColor) !important;
    padding: 0px 8px;
  }

  #completion_prompt_manager .caution {
    color: var(--warning);
  }

  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    background-color: var(--buttonFill);
    border: transparent;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
    background-color: var(--menuBackgroundColor) ;
    border: transparent;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }

  #completion_prompt_manager .completion_prompt_manager_header {
    color: var(--SmartThemeBodyColor);
  }

  .text_muted {
    color: var(--utilityColor);
  }

  /* 预设icon取消shadow */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  .mes_button, #completion_prompt_manager .completion_prompt_manager_header_advanced span{
    filter: none !important;
  }

  /* 预设inactive颜色 */
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--utilityColor);
  }

  /* tag颜色 */
  code {
    background-color: var(--menuBackgroundColor);
    color: var(--SmartThemeUnderlineColor);
    border: none;
  }

  /* Spacing Adjustment */
  .range-block-title {
    margin: 8px 0px;
    text-align: left;
  }

  hr {
    background-image: none;
    background-color: var(--SmartThemeUnderlineColor);
    margin: 12px 0px;
  }

  .range-block {
    margin-bottom: 8px;
  }
  .checkbox_label {
    margin: 2px 0px;
  }

  #result_info_text {
    color: var(--SmartThemeBodyColor);
  }

  .editable-slider-notification {
    top:10px;
  }

  /* 按键选项等颜色 */
  select, .range-block-counter input, .text_pole, textarea, .neo-range-input {
    filter: none !important;
    background-color: var(--buttonFill) !important;
    border: transparent !important;
    margin: 4px 0px !important;
  }

  /* 发送input */
  #send_textarea {
    background-color: transparent !important;
    border: none !important;
    font-size: calc(var(--mainFontSize) - 1px);
  }

  /* 圆形滑动条active */
  input.neo-range-slider[type="range"]::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none !important;
    background-color: var(--scrollBarColor) !important;
  }

  input.neo-range-slider[type="range"] {
    box-shadow: none !important;
    background-color: var(--accentColor) !important;
    opacity: 0.7;
  }

  /* 圆形滑动条disabled */
  input.neo-range-slider[type="range"]:disabled::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none !important;
    background-color: var(--scrollBarColor) !important;
  }

  input.neo-range-slider[type="range"]:disabled {
    box-shadow: none !important;
    background-color: var(--accentColor) !important;
    opacity: 0.3;
  }

   /* 预设圆形滑动条active */

  .neo-range-slider:hover, input[type="range"]:hover {
    filter: brightness(0.85);
  }

  .range-block-range input[type="range"]::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none;
    background-color: var(--scrollBarColor) !important;
  }

  .range-block-range input[type="range"] {
    box-shadow: none;
    background-color: var(--accentColor) !important;
  }

  .range-block-range input[type="range"]:disabled::-webkit-slider-thumb {
    width: 12px;
    height: 12px;
    border: none;
    box-shadow: none !important;
    background-color: var(--scrollBarColor) !important;
  }

  .range-block-range input[type="range"]:disabled {
    box-shadow: none !important;
    background-color: var(--accentColor) !important;
    opacity: 0.3;
  }

  /* 其他 */
  #userSettingsRowOne {
    margin: 8px 0px;
  }

  .mes_reasoning_header_title {
    padding-right: 16px;
  }

  #rm_print_characters_block {
    margin-top: 8px;
  }

  #ui_language_select {
    width: 6em !important;
  }

  /* 角色 */
  #rm_print_characters_pagination {
    background-color: var(--menuBackgroundColor);
    border-radius: var(--genericRadius);
    padding: 8px;
    margin: 8px;
  }

  .avatar-container.selected {
    border: 3px solid var(--accentColor);
    border-radius: 4px;
  }

  .avatar-container:hover {
    background-color: var(--white20a);
  }

  .add_avatar:hover {
    filter: drop-shadow(0px 0px 4px var(--accentColor));
  }

  .character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
    color: var(--SmartThemeUnderlineColor);
  }

  .character_select.is_fav .avatar {
    outline: 3px solid var(--accentColor);
  }

  /* 思维链 */
  .mes_reasoning_header {
    background-color: var(--accentColorOverlay);
    justify-content: center;
    padding: 8px;
    letter-spacing: 0.5px;
    margin-bottom: 12px;
    transition: all 0.25s ease;
    border-left: 3px solid var(--SmartThemeUnderlineColor);
    cursor: pointer;
  }

  .mes_reasoning_header:hover {
    background-color: var(--crimson-hover);
  }

  .mes_reasoning_details .mes_reasoning_arrow {
    height: var(--mainFontSize)*0.9;
    width: var(--mainFontSize)*0.9;
  }

  .mes_reasoning {
    border-left: var(--SmartThemeUnderlineColor) solid 2px;
    color: var(--SmartThemeBodyColor)
  }

  /* scrollbar */
  ::-webkit-scrollbar {
    width: var(--scrollBarWidth);
    height: var(--scrollBarWidth);
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb:vertical,
  ::-webkit-scrollbar-thumb:horizontal {
  background-color: var(--scrollBarColor);
  border: none;
  box-shadow: none;
  }

  /* 其他 */
  .drawer-content .alignItemsBaseline {
    align-items: center !important;
  }

  .flex1.flex-container.alignItemsBaseline {
    min-width: 214px;
  }

  .fa-solid.fa-circle-question.note-link-span {
    padding-top: 5px;
    padding-bottom: 5px;
  }

  .doubleRangeContainer > .doubleRangeInputContainer::after {
    background-color: var(--SmartThemeEmColor);
    box-shadow: none !important;
  } 

  /* pagination */
  div[is_user="false"].last_mes .mes_block {
    padding-bottom: 48px;
  }

  .swipes-counter {
    margin: 0px;
    min-width: max-content;
  }

  .flex-container.swipeRightBlock {
    display: flex;
    flex-direction: row-reverse; /* <-- this makes it horizontal */
    padding-bottom: 20px;
    margin-right: 16px;
  }

   /* qr */
  .popup #qr--modalEditor #qr--modal-message {
    color: var(--SmartThemeBodyColor) !important;
    background-color: var(--buttonFill) !important;
  }

  .popup:has(#qr--modalEditor) .popup-content #qr--modal-message::selection {
    color: var(--SmartThemeBodyColor) !important;
  }

  /* 对白样式 */
  .mes_text q {
    color: var(--SmartThemeQuoteColor); 
    background-color: var(--accentColorOverlay); /* 装饰性背景 */
    margin: 0px 3px;
    padding: 2px 4px;
    padding-left: 6px;
    border-radius: 2px;
    border-left: var(--accentColor) 1.5px solid; /* 装饰性竖线左 */
    border-right: var(--accentColor) 1.5px solid; /* 装饰性竖线右 */
    font-size: calc(var(--mainFontSize)* 0.98);
  }

  /* 角色收藏滚动条 */
  #HotSwapWrapper > div {
    overflow: auto hidden;
    flex-wrap: nowrap;
    justify-content: flex-start;
    gap: 8px;
    min-height: calc(var(--avatar-base-height)* 1.5);
  }

  #HotSwapWrapper > div::-webkit-scrollbar-thumb {
    background-color: var(--scrollBarColor);
  }

  #HotSwapWrapper {
    overflow: hidden;
  }

 /* 表格样式 */
 .mes_text table {
  width: 100%;
  border-collapse: collapse;
  margin: 8px 0;
  color: var(--SmartThemeQuoteColor);
  background-color: var(--accentColorOverlay);
  border-radius: var(--genericRadius);
  overflow: hidden;
 }

 .mes_text th,
 .mes_text td {
  border: none;
  border-right: 1px solid var(--accentColor);
  padding: 10px 14px;
  text-align: left;
  border-bottom: 1px solid var(--accentColor);
 }

 .mes_text th {
  font-weight: 700;
 }

 .mes_text tr:last-child td {
  border-bottom: none;
 }

 .mes_text tr:nth-child(even) {
  background-color: rgba(255, 255, 255, 0.025);
 }

 .mes_text td:last-child,
 .mes_text th:last-child {
  border-right: none;
 }


/* 基础代码块 */
.mes_text pre {
  background-color: var(--accentColorOverlay);
  padding: 12px 14px;
  border-radius: var(--genericRadius);
  font-family: inherit;    
  font-size: calc(var(--mainFontSize)* 0.95);
  letter-spacing: 0.3px;
  overflow-x: auto;
  white-space: pre-wrap;
  word-break: break-word;
  border-left: 3px solid var(--SmartThemeUnderlineColor);
  position: relative; 
}

 .mes_text pre code{
  line-height: 1.4;
}

/* 保留字体继承，不破坏复制按钮结构 */
.mes_text pre code {
  font-family: inherit;
  color: inherit;
  background: none;
  border: none
}

/* Formatting菜单 */
#InstructSettingsColumn .text_pole,
#InstructSettingsColumn select,
#InstructSettingsColumn .menu_button,
#InstructSettingsColumn input[type='checkbox'] {
  background-color: var(--white30a) !important;
}

/* 手机适配 */
@media (max-width: 600px) {
  #rm_extensions_block {
    padding-right: 24px;
  }
  #bg_menu_content {
    width: 100%;
  }
  /* 正则优化 */
  .regex-script-label {
    flex-wrap: wrap; /* override nowrap if needed */
    border-radius: var(--genericRadius) !important;
    background-color: var(--buttonFill) !important;
    gap: 4px !important;
    padding-top: 8px !important;
    padding-bottom: 4px !important;
    margin-right: 24px !important;
  }
  .regex-script-label > div:last-of-type {
    flex-basis: 100%;
    width: 100%;
  }
  .regex-script-label > div:first-of-type {
    flex-shrink: 1;
    min-width: 0;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    max-width: 90%; /* 可选，根据布局定 */
  }
  .regex-script-label > span {
    flex-shrink: 0;
  }
  .regex-script-label:has(.disable_regex:checked) {
    background-color: color-mix(in srgb, var(--buttonFill) 40%, var(--menuBackgroundColor) 60%) !important
  }
  /* 正则优化结束 */
}

/* Safari 适配 */
@supports (-webkit-touch-callout: none) {
  #chat {
    padding-right: var(--pagePadding); /* 手动留出滚动条空间 */
  }
  .drawer-content:not(#left-nav-panel):not(#right-nav-panel) {
    max-width: 100dvw;
  }
  #world_popup {
    width: 100%;
  }
}

/* iPad safari */
@supports (-webkit-touch-callout: none) {
  @media screen and (min-width: 768px) {
    #sheld {
      width: var(--sheldWidth);
      margin-left: auto;
      margin-right: auto;
    }
    #top-bar, #top-settings-holder .drawer .drawer-content {
      width: var(--sheldWidth);
    }
    #left-nav-panel,
    #right-nav-panel,
    #character_popup,
    #world_popup,
    .drawer-content {
      top: var(--topBarBlockSize);
    }
    #character_popup,
    #world_popup,
    .drawer-content {
      margin-top: 0px;
      top: var(--topBarBlockSize);
    }
  }
}

/* 插件适配 */

/* 文生图 */
#settings-panel input, #settings-panel select, #settings-panel textarea,
#settings-panel2 input, #settings-panel2 select {
  color: var(--SmartThemeBodyColor) !important;
  background-color: var(--buttonFill) !important;
}

/* qr隐藏插件 */
#quick-reply-rocket-button i {
  font-size: var(--bottomFormIconSize) !important;
  color: var(--SmartThemeUnderlineColor) !important;
}

#quick-reply-menu {
  background-color: var(--menuBackgroundColor) !important;
  box-shadow: var(--boxShadowSubtle);
  border:none !important;
}

.quick-reply-list-title {
  color: var(--SmartThemeBodyColor) !important;
  border-bottom: 1px solid var(--accentColor) !important;
}

.quick-reply-list {
  border: 1px solid var(--accentColor) !important;
}

#global-qr-items .quick-reply-item {
  background-color: var(--buttonFill) !important;
  color: var(--SmartThemeBodyColor) !important;
}

#global-qr-items .quick-reply-item:hover {
  background-color: var(--crimson-hover) !important;
}

/* 输入助手 */
.form-group label {
  color: var(--SmartThemeBodyColor) !important;
}

.form-group input, .form-group select {
  border: none !important;
  background-color: var(--buttonFill) !important;
  color: var(--SmartThemeBodyColor) !important;
}

.integrated-button-row, .shortcut-input, .shortcut-clear-btn, .button-preview {
  border: none !important;
}

/* 酒馆助手 */
.title-item-active {
  background-color: var(--buttonFill) !important;
}

.toggle-input:checked + .toggle-label {
  background-color: var(--accentColor) !important;
  border: none !important;
}

#audio-player-content .audio-container, .script-item, .box-container {
  border: none !important;
}

/* 世界书选择器 */
#rightSendForm > #global-combined-selector-button {
  width: var(--bottomFormBlockSize);
  height: var(--bottomFormBlockSize);
  background-color: var(--navBackgroundColor) !important;
  border: none !important;
  padding: 0px !important;
  margin-left: 0px !important;
  opacity: 0.7;
}

#rightSendForm > #global-combined-selector-button:hover {
  opacity: 1 !important;
  background-color: var(--navBackgroundColor) !important;
}

#rightSendForm > #global-combined-selector-button i {
  font-size: var(--bottomFormIconSize) !important;
  color: var(--SmartThemeUnderlineColor) !important;
}

/* === 主题基础调整 === */
/* --- 快速回复隐藏 --- */
#send_form:hover #qr--bar {
    height: 35px; /* 展开时qr的高度 */
    transition: all 0.3s ease;
}

/* 平时完全隐藏 */
#qr--bar {
    height: 0; 
}

/* 2. 修改：印章位置调整至头像右下角 */
.alignItemsBaseline:has(> .name_text):has(> .timestamp) {
  display: grid;
  background-color: white; /* 背景颜色 */
  border-radius: 50%; /* 圆角 */
  outline: 2px solid white; /* 外框颜色+宽度 */
  outline-offset: 2px; /* 外框与内框的间距 */
  /* 调整大小 */
  height: 3.9em;
  width: 3.9em;
  /* 调整位置 */
  left: 6.5em; /* 向左偏移，使其覆盖在头像右侧 */
  top: 2.2em;   /* 向下偏移，使其位于头像底部 */
  text-align: center;
  gap: 1px;
  position: absolute;
  z-index: 5;
  box-sizing: border-box;
  overflow: hidden;
  transform: rotate(-15deg); /* 可选：增加一点倾斜让它更像印章 */
}

/* 角色名字 */
.name_text {
  font-size: 1em; /* 字体大小 */
  padding-top: 15px; /* 与框顶部的间距 */
}

/* 时间戳 */
.timestamp {
  font-size: 0.5em; /* 字体大小 */
  padding-bottom: 35px; /* 与框底部的间距 */
}

.mes_block {
  overflow: visible !important;
}
