custom_css:
/* 本样式仅免费发布于Discord旅程社区。请勿二传或商用！二改请标注谢谢。感谢Junezz老师的简约卡片的正则参考。 */

/* 正文字体 */
@import url("https://fontsapi.zeoseven.com/301/main/result.css");
/* 思维链字体 */
@import url("https://fontsapi.zeoseven.com/629/main/result.css");

:root {
  /* 正文字体名称 */
  --mainFontFamily: "GenWanMin2 TC R";
  --monoFontFamily: "GenWanMin2 TC R";

  --气泡左右对齐: 10px;
  --正文左右位置: 15px;
  --思维链标题: "✎ ̼ ˗ˏˋ Memo ˎˊ˗ ✐ ᝰ .";
  --黑色阴影: rgba(0, 0, 0, 0.15);
  --思维链字体: "MaokenYingBiKaiShuJ";
  --思维链字体颜色: #333;

  --crimson70a: var(--SmartThemeShadowColor);
  --black30a: transparent;
  --black50a: transparent;
  --rlh-header-bg: var(--SmartThemeUnderlineColor) !important;
  --rlh-item-bg: var(--SmartThemeUnderlineColor) !important;
  --rlh-accent-color: var(--SmartThemeShadowColor) !important;
}

/* chat + mes */
#chat {
  border: none;
  background-color: transparent;
}

.mes {
  position: relative;
  flex-direction: column;
  padding-top: 70px; /* 三元素位置, 名字部分60 */
  padding-left: var(--气泡左右对齐);
  padding-right: var(--气泡左右对齐);
}

.mes_block {
  padding: 10px var(--正文左右位置) 20px;
  overflow: visible;
}

#chat .mes + .mes {
  margin-top: 50px;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0px;
}

#chat .mes.selected {
  background-color: var(--SmartThemeShadowColor);
}

/* 名字与时间戳 */
.ch_name > .flex-container:first-child {
  position: absolute;
  top: 10px;
  left: var(--气泡左右对齐);
}

.ch_name > .flex-container:first-child > .flex-container {
  flex-direction: column-reverse;
  align-items: flex-start;
}

.mes[is_user="false"] .ch_name > .flex-container:first-child > .flex-container {
  background: linear-gradient(to top, var(--SmartThemeShadowColor) 35%, var(--SmartThemeBotMesBlurTintColor) 65%);
  border: 2px solid var(--SmartThemeShadowColor);
  border-radius: 20px;
  padding: 5px 15px;
  box-shadow: 1px 1px 0px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 90%, black 10%);
}

.mes[is_user="true"] .ch_name > .flex-container:first-child > .flex-container {
  background: linear-gradient(to top, var(--SmartThemeShadowColor) 35%, var(--SmartThemeUserMesBlurTintColor) 65%);
  border: 2px solid var(--SmartThemeShadowColor);
  border-radius: 20px;
  padding: 5px 15px;
  box-shadow: 1px 1px 0px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 90%, black 10%);
}

.timestamp {
  color: var(--SmartThemeShadowColor);
}

.mes[is_user="false"] .name_text {
  color: var(--SmartThemeBotMesBlurTintColor);
}

.mes[is_user="true"] .name_text {
  color: var(--SmartThemeUserMesBlurTintColor);
}

.name_text, .timestamp, .mes .mes_timer, .mes .mesIDDisplay, .mes .tokenCounterDisplay {
  font-size: calc(var(--mainFontSize) * 0.9);
  font-weight: normal;
  opacity: 1;
}

/* 幽灵图标 */
.mes[is_system="true"] .ch_name > .flex-container:first-child > .flex-container {
  flex-direction: row;
  flex-wrap: wrap-reverse;
  align-items: center;
}

.mes[is_system="true"] .timestamp {
  flex-basis: 100%;
}

/* 消息按钮保持在右上角 */
.ch_name {
  justify-content: flex-end;
}

.mes_buttons, .extraMesButtons {
  align-items: center;
  overflow: visible;
  margin-right: calc(var(--正文左右位置) - var(--正文左右位置) * 2);
}

.mes_button:hover, .extraMesButtons>div:hover {
  transform: scale(1.05) rotate(5deg);
}

.mes_button, .extraMesButtons>div, .favorite-toggle-icon, .mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
  opacity: 1 !important;
  filter: none !important;
  background-color: var(--SmartThemeShadowColor);
  box-shadow:
    0 0 0 1px var(--SmartThemeUnderlineColor),
    1px 1px 0px 1px color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
  border-radius: 50px;
  padding: 5px !important;
  margin-left: 5px;
  aspect-ratio: 1 / 1; /* 保持圆形 */
}

.mes_button:not(.mes_reasoning_copy, .mes_reasoning_edit), .extraMesButtons>div, .favorite-toggle-icon {
  margin-top: -170px;
}

.extraMesButtons {
  margin-right: 0px;
}

.mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
  margin-top: -100px;
}

.mes_button.open_table_by_id.interactable {
  color:  var(--SmartThemeUnderlineColor);
  aspect-ratio: 2 / 1;
}

.mes_reasoning_details .mes_reasoning_summary {
  margin-right: var(--气泡左右对齐);
}

.mes_edit_buttons {
  filter: none;
}

/* timer, token, id */
.mesAvatarWrapper {
  display: flex;
  gap: 7px; /* 间距 */
}

.mes .mes_timer:empty, .mes .mesIDDisplay:empty, .mes .tokenCounterDisplay:empty {
  display: none;
}

.mes[is_user="false"] .mesAvatarWrapper {
  background-color: var(--SmartThemeShadowColor);
  outline: 2px dashed var(--SmartThemeBotMesBlurTintColor);
  outline-offset: -10px;
  border: 2px solid var(--SmartThemeShadowColor);
  box-shadow:
    inset 0 0 0 4px var(--SmartThemeBotMesBlurTintColor),
    1px 1px 0px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 90%, black 10%);
  border-radius: 20px;
  padding: 10px 15px;
}

.mes[is_user="true"] .mesAvatarWrapper {
  background-color: var(--SmartThemeShadowColor);
  outline: 2px dashed var(--SmartThemeUserMesBlurTintColor);
  outline-offset: -10px;
  border: 2px solid var(--SmartThemeShadowColor);
  box-shadow:
    inset 0 0 0 4px var(--SmartThemeUserMesBlurTintColor),
    1px 1px 0px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 90%, black 10%);
  border-radius: 20px;
  padding: 10px 15px;
}

.mes[is_user="false"] .mes_timer, .mes[is_user="false"] .tokenCounterDisplay, .mes[is_user="false"] .mesIDDisplay {
  color: var(--SmartThemeBotMesBlurTintColor);
}

.mes[is_user="true"] .mes_timer, .mes[is_user="true"] .tokenCounterDisplay, .mes[is_user="true"] .mesIDDisplay {
  color: var(--SmartThemeUserMesBlurTintColor);
}

/* 头像 */
.mes .avatar {
  position: absolute;
  top: 135px; /* 上面70+45 */
  left: 50%;
  transform: translateX(-50%);
}

.mes[is_user="false"] .avatar img {
  box-shadow:
    0 0 0 3px var(--SmartThemeBotMesBlurTintColor),
    0 0 0 6px var(--SmartThemeShadowColor),
    2px 2px 0px 6px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 90%, black 10%);
  transition: transform 0.3s ease;
  border: none;
}

.mes[is_user="true"] .avatar img {
  box-shadow:
    0 0 0 3px var(--SmartThemeUserMesBlurTintColor),
    0 0 0 6px var(--SmartThemeShadowColor),
    2px 2px 0px 6px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 90%, black 10%);
  transition: transform 0.3s ease;
  border: none;
}

.avatar img:hover {
  transform: scale(1.05) rotate(3deg); /* 悬停时轻微放大并旋转 */
}

/* .mes_block */
.mes[is_user="false"] .mes_block {
  margin-top: 50px; /* 在头像中间 */
  background-color: var(--SmartThemeBotMesBlurTintColor);
  border: 5px solid var(--SmartThemeShadowColor);
  border-radius: 20px;
  outline: 2px dashed var(--SmartThemeShadowColor);
  outline-offset: -10px;
  box-shadow: 2px 2px 0px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 90%, black 10%);
}

.mes[is_user="true"] .mes_block {
  margin-top: 50px; /* 在头像中间 */
  background-color: var(--SmartThemeUserMesBlurTintColor);
  border: 5px solid var(--SmartThemeShadowColor);
  border-radius: 20px;
  outline: 2px dashed var(--SmartThemeShadowColor);
  outline-offset: -10px;
  box-shadow: 2px 2px 0px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 90%, black 10%);
}

/* top bar + send form */
#send_form, body.no-blur #send_form, #top-bar, body.no-blur #top-bar {
  background-color: transparent !important;
  outline: none !important;
  border: none !important;
  box-shadow: none;
}

#top-settings-holder .drawer-icon, #send_form [class*="fa-"] {
  -webkit-text-stroke: 1.5px var(--SmartThemeShadowColor);
  font-size: 20px !important;
}

#form_sheld {
  position: absolute;
  bottom: 10px;
}

#send_form {
  width: 97%;
}

.last_mes {
  margin-bottom: 200px !important;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
  opacity: 1;
}

#rightSendForm, #leftSendForm {
  gap: 3px;
}

#send_form .mes_stop {
  padding: 0px 7px;
  opacity: 1;
}

#send_textarea, #send_textarea:focus {
  clip-path: none !important;
  border: none;
  background-color: var(--SmartThemeUnderlineColor);
  outline: 3px solid var(--SmartThemeShadowColor) !important;
  outline-offset: -5px;
  border-radius: 10px;
  padding: 10px 20px !important;
}

/* 菜单 */
#left-nav-panel,
#completion_prompt_manager_popup,
#rm_api_block,
#AdvancedFormatting,
#WorldInfo,
#user-settings-block,
#Backgrounds,
#rm_extensions_block,
.drawer-content.openDrawer,
#right-nav-panel,
.popup, #bulk_tag_popup, #dialogue_popup,
#options, #extensionsMenu, .popup .popper-modal,
.welcomePanel {
  border: 5px solid var(--SmartThemeShadowColor);
  padding: 10px;
  border-radius: 20px;
  outline: 2px dashed var(--SmartThemeShadowColor);
  outline-offset: -10px;
  box-shadow: 1px 1px 0px color-mix(in srgb, var(--SmartThemeBlurTintColor) 90%, black 10%);
}

/* 图标样式 */
.fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid:not(.fav_on, .world_set, .fa-power-off, .fa-chevron-right, .fa-chevron-left), .fab, .far, .fas {
  color: var(--SmartThemeUnderlineColor) !important;
  font-size: 17px !important;
  filter: none;
  opacity: 1;
}

/* 锁定按钮 */
#rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div {
  opacity: 1;
  -webkit-text-stroke: 1px var(--SmartThemeShadowColor);
}

/* 图标按钮样式 */
.menu_button {
  color: var(--SmartThemeUnderlineColor);
  filter: none;
  background-color: var(--SmartThemeShadowColor);
  box-shadow: 1px 1px 0px color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
}

.menu_button:hover {
  transform: scale(1.05) rotate(3deg);
}

.menu_button:not(.disabled):hover, .menu_button:not(.disabled).active {
  background-color: var(--SmartThemeShadowColor);
}

.inline-drawer-icon {
  -webkit-text-stroke: 1px var(--SmartThemeShadowColor);
}

input:not(input[type="range"], input[type='checkbox']), select, .text_pole, .textarea_compact, textarea:not(.select2-search__field),
#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item .qr--content .qr--set-itemMessage,
.select2-dropdown,
#extensionSideBar #extensionSideBarContainer .sideBarItem.selected,
.select_chat_block[highlight] {
  background-color: var(--SmartThemeUnderlineColor) !important;
  border: none !important;
  box-shadow:
    inset 0 0 0 3px color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%),
    0 0 0 2px var(--SmartThemeShadowColor),
    1px 1px 0 2px color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
  border-radius: 10px;
  outline: 1px solid var(--SmartThemeShadowColor) !important;
  outline-offset: -3px;
  padding: 5px 10px !important;
}

select:focus-visible, input:focus-visible, textarea:focus-visible {
  outline: 1px solid var(--SmartThemeShadowColor);
  outline-offset: -3px;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader, .inline-drawer-header {
  background-color: var(--SmartThemeUnderlineColor);
  color: var(--SmartThemeQuoteColor);
  border: 2px solid var(--SmartThemeShadowColor);
  outline: 2px dashed var(--SmartThemeQuoteColor);
  outline-offset: -6px;
  border-radius: 4px;
  padding: 7px 10px !important;
  box-shadow: 2px 2px 0 color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
  margin: 15px 0;
  background-image: none;
}

/* Spacing */
.text_pole, select, .range-block-range-and-counter, .range-block, .range-block-title, .WIEntryHeaderTitleMobile, h1, h2, h3 {
  margin: 3px !important;
}

#anchor_checkbox label, #power-user-option-checkboxes label, .checkbox_label, .auto_continue_settings_block {
  margin: 5px 0px;
}

/* 勾选框 */
input[type="checkbox"] {
  background-color: var(--SmartThemeUnderlineColor);
  border: 2px solid var(--SmartThemeShadowColor);
  box-shadow: 1px 1px 0px var(--SmartThemeUnderlineColor);
  outline: none;
  filter: none;
}

input[type="checkbox"]::before {
  box-shadow: inset 1em 1em var(--SmartThemeShadowColor);
}

/* 滑动条 */
input[type="range"], input[type="range"]:disabled {
  filter: none;
  box-shadow: 0 0 0 2px var(--SmartThemeShadowColor) !important;
  background-color: var(--SmartThemeUnderlineColor) !important;
  height: 3px !important;
  border-radius: 50px !important;
}

input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  border: none;
  cursor: pointer;
  background-color: transparent;
  box-shadow: none;

  /* 自定义 */
  width: 24px;
  height: 24px;
  background-color: var(--SmartThemeShadowColor);
  -webkit-mask-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/></svg>');
  mask-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/></svg>');
  -webkit-mask-size: contain;
  mask-size: contain;
  -webkit-mask-repeat: no-repeat;
  mask-repeat: no-repeat;
  -webkit-mask-position: center;
  mask-position: center;
}

/* 正文编辑 */
.mes[is_user="false"] .reasoning_edit_textarea, .mes[is_user="false"] .edit_textarea {
  background-color: var(--SmartThemeUserMesBlurTintColor) !important;
  border: none !important;
  margin: 5px 0px;
  border-radius: 4px !important;
  outline: none !important;
  box-shadow: 0 0 0 1px var(--SmartThemeShadowColor), 0 0 0 5px var(--SmartThemeUserMesBlurTintColor) !important;
}

.mes[is_user="true"] .reasoning_edit_textarea, .mes[is_user="true"] .edit_textarea {
  background-color: var(--SmartThemeBotMesBlurTintColor) !important;
  border: none !important;
  margin: 5px 0px;
  border-radius: 4px !important;
  outline: none !important;
  box-shadow: 0 0 0 1px var(--SmartThemeShadowColor), 0 0 0 5px var(--SmartThemeBotMesBlurTintColor) !important;
}

/* 正文 */
.mes_text {
  padding: 0;
  line-height: 1.6;
}

.mes[is_user="false"] .mes_text q {
  color: color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 90%, white 10%);
  background-color: var(--SmartThemeShadowColor);
  border: 1px dashed var(--SmartThemeBotMesBlurTintColor);
  box-shadow:
    0 0 0 2px var(--SmartThemeShadowColor),
    0 0 0 5px var(--SmartThemeBotMesBlurTintColor),
    0 0 0 6px var(--SmartThemeShadowColor);
  border-radius: 4px;
  padding: 0 3px;
  margin: 10px;
  display: inline-block;
}

.mes[is_user="true"] .mes_text q {
  color: color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 90%, white 10%);
  background-color: var(--SmartThemeShadowColor);
  border: 1px dashed var(--SmartThemeUserMesBlurTintColor);
  box-shadow:
    0 0 0 2px var(--SmartThemeShadowColor),
    0 0 0 5px var(--SmartThemeUserMesBlurTintColor),
    0 0 0 6px var(--SmartThemeShadowColor);
  border-radius: 4px;
  padding: 0 3px;
  margin: 10px;
  display: inline-block;
}

.mes_text em, code:not(pre code.hljs) {
  position: relative;
  display: inline-block;
  background-color: var(--SmartThemeShadowColor);
  color: var(--SmartThemeEmColor);
  padding: 0px 27px 0px 7px;
  margin: 3px;
  border-radius: 8px;
  border: none;
  line-height: 1.6;
  font-style: italic;
}

.mes[is_user="false"] .mes_text em::after, .mes[is_user="false"] code:not(pre code.hljs)::after {
  content: '';
  position: absolute;
  bottom: 0px;
  right: 0px;
  width: 20px;
  height: 20px;
  box-shadow: -1px -1px 5px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 95%, black 5%);
  background: linear-gradient(135deg,
    transparent 50%,
    var(--黑色阴影) 50%,
    var(--SmartThemeBotMesBlurTintColor) 52%
  );
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 8px;
  border-top-left-radius: 8px;
}

.mes[is_user="true"] .mes_text em::after, .mes[is_user="true"] code:not(pre code.hljs)::after {
  content: '';
  position: absolute;
  bottom: 0px;
  right: 0px;
  width: 20px;
  height: 20px;
  box-shadow: -1px -1px 5px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 95%, black 5%);
  background: linear-gradient(135deg,
    transparent 50%,
    var(--黑色阴影) 50%,
    var(--SmartThemeUserMesBlurTintColor) 52%
  );
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 8px;
  border-top-left-radius: 8px;
}

.mes_text pre code, .mes_reasoning {
  position: relative;
  display: block;
  background: var(--SmartThemeChatTintColor);
  border: none;
  box-shadow: 5px 5px 10px var(--黑色阴影);
  color: var(--思维链字体颜色);
  font-family: var(--思维链字体);
  line-height: 1.6;
  padding: 20px;
  margin: 20px 0;
  overflow: visible;
}

.mes_reasoning_details {
  margin-right: 0;
}

.mes_text pre code::before, .mes_reasoning::before {
  content: '';
  position: absolute;
  top: -12px;
  left: 50%;
  transform: translateX(-50%) rotate(-2deg);

  /* 胶纸的尺寸和外观 */
  width: 90px;
  height: 25px;
  background-color: color-mix(in srgb, var(--SmartThemeShadowColor), transparent 60%);
  border-left: 1px dotted var(--黑色阴影);
  border-right: 1px dotted var(--黑色阴影);
  box-shadow: 0px 1px 2px var(--黑色阴影);
}

/* 思维链 */
.mes_reasoning_header {
  margin: 0;
  padding-bottom: 0;
  background: none;
}

.mes_reasoning_header_title {
  font-size: 0;
}

.mes[is_user="false"] .mes_reasoning_header_title::before {
  font-size: calc(var(--mainFontSize) * 1.3);
  content: var(--思维链标题);
  color: var(--SmartThemeBotMesBlurTintColor);
  -webkit-text-stroke: 1px var(--SmartThemeShadowColor);
  font-weight: bold;
  text-shadow: 2px 2px 0px color-mix(in srgb, var(--SmartThemeBotMesBlurTintColor) 95%, black 5%);
  text-decoration: solid underline overline var(--SmartThemeShadowColor);
}

.mes[is_user="true"] .mes_reasoning_header_title::before {
  font-size: calc(var(--mainFontSize) * 1.3);
  content: var(--思维链标题);
  color: var(--SmartThemeUserMesBlurTintColor);
  -webkit-text-stroke: 1px var(--SmartThemeShadowColor);
  font-weight: bold;
  text-shadow: 2px 2px 0px color-mix(in srgb, var(--SmartThemeUserMesBlurTintColor) 95%, black 5%);
  text-decoration: solid underline overline var(--SmartThemeShadowColor);
}

.mes_reasoning_details .mes_reasoning_arrow {
  display: none;
}

.mes_text blockquote, .mes_reasoning blockquote {
  border-left: 10px solid var(--SmartThemeShadowColor);
  border-right: 10px solid var(--SmartThemeShadowColor);
  border-top: 2px solid var(--SmartThemeShadowColor);
  border-bottom: 2px solid var(--SmartThemeShadowColor);
  padding-top: 10px;
  padding-left: 0px;
  margin-bottom: 10px;
  text-align: center;
}

/* 预设小字 */
.toggle-description {
  color: var(--SmartThemeQuoteColor);
}

/* 预设提示词标题 */
#completion_prompt_manager .completion_prompt_manager_header_advanced span,
#completion_prompt_manager .completion_prompt_manager_header div {
  filter: none;
  color: var(--SmartThemeQuoteColor);
}

/* 预设条目 */
#completion_prompt_manager #completion_prompt_manager_list {
  gap: 5px;
  margin: 3px 0 !important;
}

/* 启用预设条目 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:not(#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled) {
  border: 1px solid var(--SmartThemeShadowColor);
  outline: 1px dashed var(--SmartThemeShadowColor);
  outline-offset: -4px;
}

/* 未启用预设条目 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
  border: none;
  color: var(--SmartThemeBodyColor);
}

/* icon */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .drag-handle,
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span,
.fa-toggle-on:before, .fa-toggle-off:before,
.fa-comments:before, .fa-circle-question:before, .fa-question-circle:before, .fa-circle-info:before, .fa-info-circle:before, .fa-expand-arrows-alt:before, .fa-maximize:before,
.fa-comment:before {
  filter: none !important;
  opacity: 1 !important;
  -webkit-text-stroke: 1px var(--SmartThemeShadowColor);
}

/* 世界书 */
.select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
.standoutHeader~.inline-drawer-content,
.wi-card-entry,
.select2-container.select2-container--focus .select2-selection--multiple, .select2-container.select2-container--focus .select2-selection--single {
  border: none;
}

.wi-card-entry {
  padding: 0 !important;
}

.select2-results .select2-results__option--selectable {
  opacity: 1 !important;
}

span.select2.select2-container .select2-selection__choice__remove,
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display {
  color: var(--SmartThemeBlurTintColor);
  border: none;
}

.select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
  background-color: var(--SmartThemeShadowColor);
  border: 1px dashed var(--SmartThemeBlurTintColor);
  box-shadow:
    0 0 0 2px var(--SmartThemeShadowColor),
    0 0 0 5px var(--SmartThemeBlurTintColor),
    0 0 0 6px var(--SmartThemeShadowColor),
    1px 1px 0 6px color-mix(in srgb, var(--SmartThemeBlurTintColor) 90%, black 10%);
  border-radius: 4px;
  padding: 0px 10px;
  margin: 10px;
}

/* 图片 */
.bg_example {
  border: 2px solid var(--SmartThemeShadowColor);
  border-radius: 10px;
}

/* QR */
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message {
  color: var(--SmartThemeBodyColor) !important;
}

/* 正则 */
.regex-script-label {
  background-color: var(--SmartThemeUnderlineColor);
  border: none !important;
  box-shadow: inset 0 0 0 3px color-mix(in srgb, var(--SmartThemeBlurTintColor), white 15%), 0 0 0 1px var(--SmartThemeShadowColor);
  margin-bottom: 5px;
  border-radius: 4px !important;
}

.regex-script-label:has(.disable_regex:checked) {
  background-color: transparent !important;
  border: 1px solid var(--SmartThemeShadowColor) !important;
  box-shadow: none;
}

@media (max-width: 600px) {
  .regex-script-label {
    flex-wrap: wrap;
    gap: 4px !important;
    padding-top: 8px !important;
    padding-bottom: 4px !important;
    margin-right: 100px !important;
  }
  .regex-script-label > div:last-of-type {
    flex-basis: 100%;
    width: 100%;
  }
}

/* 人设 */
.avatar-container.selected .ch_description, .avatar-container.selected .ch_name, .buttons_block .menu_button {
  color: var(--SmartThemeBlurTintColor);
  filter: none;
  opacity: 1;
  box-shadow: none;
}

.avatar-container.selected {
  background-color: var(--SmartThemeShadowColor);
  outline: 2px dashed var(--SmartThemeBlurTintColor);
  outline-offset: -10px;
  box-shadow:
    inset 0 0 0 4px var(--SmartThemeBlurTintColor),
    1px 1px 0 0px color-mix(in srgb, var(--SmartThemeBlurTintColor) 90%, black 10%);
  border-radius: 4px;
  padding: 15px;
}

.avatar img {
  box-shadow:
    0 0 0 2px var(--SmartThemeUnderlineColor),
    0 0 0 4px var(--SmartThemeShadowColor),
    1px 1px 0 4px color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
  border: none;
}

.character_name_block .ch_name, .avatar-container .ch_name, .ch_description {
  margin-left: 3px;
}

/* 角色卡 */
.avatars_inline {
  gap: 5px;
  flex-wrap: nowrap;
  overflow: visible;
}

#HotSwapWrapper {
  overflow-x: auto;
}

.rm_tag_filter .tag {
  opacity: 1;
  filter: none;
  border: 1px solid var(--SmartThemeBodyColor);
  box-shadow: 1px 1px 0 0px color-mix(in srgb, var(--SmartThemeBlurTintColor) 90%, black 10%);
}

.tag_name.fa-solid {
  color: var(--SmartThemeBodyColor) !important;
}

.character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
  color: var(--SmartThemeQuoteColor);
  text-decoration: wavy underline var(--SmartThemeQuoteColor);
  overflow: visible;
}

.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
  outline: none !important;
}

#rm_PinAndTabs {
  margin-top: 5px;
}

.rm_tag_controls {
  margin-bottom: 10px;
}

/* 底栏菜单+QR */
.options-content, .list-group {
  border: none;
}

#extensionsMenu>.extension_container>div, #extensionsMenu>div:not(.extension_container), .options-content a, .list-group-item {
  opacity: 1 !important;
}

.options-content i, .extensionsMenuExtensionButton {
  -webkit-text-stroke: 0.7px var(--SmartThemeShadowColor);
}

#qr--bar {
  opacity: 1 !important;
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button {
  color: var(--SmartThemeUnderlineColor) !important;
  border-radius: 4px !important;
}

/* Swipe */
.swipe_left {
  margin-bottom: -70px;
  left: var(--气泡左右对齐);
}

.swipeRightBlock {
  margin-bottom: -70px;
  right: var(--气泡左右对齐);
}

.swipe_left, .swipe_right, .swipe_right fa-solid fa-chevron-right interactable {
  background-color: var(--SmartThemeBotMesBlurTintColor);
  color: var(--SmartThemeShadowColor);
  border-radius: 50%;
  width: 30px;
  height: 30px;
  opacity: 1 !important;
  box-shadow:
    inset 0 0 0 1px var(--SmartThemeShadowColor),
    0 0 0 3px var(--SmartThemeBotMesBlurTintColor);
}

.swipes-counter {
  color: var(--SmartThemeShadowColor);
  font-size: calc(var(--mainNameSize) * 0.8);
  opacity: 1 !important;
  font-weight: bold;
  padding-top: 8px;
}

/* 表格 */
.tableDom td, .tableDom th, .history-cell-item {
  border: 1px solid var(--SmartThemeShadowColor) !important;
  background-color: var(--SmartThemeUnderlineColor) !important;
}

.insert-item {
  color: var(--SmartThemeShadowColor) !important;
}

.history-tab.active {
  background-color: transparent !important;
}

#tableContainer>div {
  margin-bottom: 15px;
}

tr .sheet-cell {
  line-height: 1.5;
}

/* 世界书正则 */
.rlh-action-btn-icon, .rlh-search-action-btn, #modern-screenshot-settings-panel .custom-icon-group a, .right_menu_button, .fa-circle-xmark:before, .fa-times-circle:before, .fa-xmark-circle:before, .renameChatButton, .exportRawChatButton, .exportChatButton, .fa-skull:before {
  -webkit-text-stroke: 0.7px var(--SmartThemeShadowColor);
}

.rlh-maximize-btn {
  background-color: var(--SmartThemeShadowColor) !important;
  border: none !important;
}

.memo-button.secondary {
  color: var(--SmartThemeUnderlineColor) !important;
}

/* Chat Top Bar */
#extensionTopBar, #extensionConnectionProfiles {
  border-radius: 10px !important;
  border: 3px solid var(--SmartThemeShadowColor) !important;
  margin-bottom: 2px;
}

#extensionSideBar #extensionSideBarContainer {
  gap: 10px !important;
}

/* 初始界面 */
.welcomePanel {
  background-color: var(--SmartThemeBlurTintColor);
}

.welcomeRecent .recentChatList .recentChat {
  background-color: var(--SmartThemeUnderlineColor);
  color: var(--SmartThemeShadowColor);
  border: 2px solid var(--SmartThemeShadowColor);
  outline: 2px dashed var(--SmartThemeQuoteColor);
  outline-offset: -6px;
  border-radius: 4px;
  padding: 10px !important;
  box-shadow: 2px 2px 0 color-mix(in srgb, var(--SmartThemeUnderlineColor) 95%, black 5%);
  margin: 5px 0;
}

/* 弹窗信息 */
#toast-container > .toast {
  background-color: white !important;
  border-radius: 4px;
  padding: 10px 15px !important;
  margin-bottom: 20px;
  opacity: 1 !important;
  box-shadow: none;
  font-weight: bold;
}
#toast-container > .toast .toast-progress {
  display: none;
}

/* 绿色 */
#toast-container > .toast-success {
  --toast-color: #A8D5BA;
  color: var(--toast-color) !important;
  border: 2px dashed var(--toast-color);
  box-shadow: 0 0 0 2px white, 0 0 0 6px var(--toast-color), 0 0 0 8px white, 1px 1px 0 8px color-mix(in srgb, var(--toast-color) 90%, black 10%);
}
#toast-container > .toast-success .toast-close-button {
  color: var(--toast-color);
  text-shadow: none;
}

/* 红色 */
#toast-container > .toast-error {
  --toast-color: #F4B8B8;
  color: var(--toast-color) !important;
  border: 2px dashed var(--toast-color);
  box-shadow: 0 0 0 2px white, 0 0 0 6px var(--toast-color), 0 0 0 8px white, 1px 1px 0 8px color-mix(in srgb, var(--toast-color) 90%, black 10%);
}
#toast-container > .toast-success .toast-close-button {
  color: var(--toast-color);
  text-shadow: none;
}

/* 黄色 */
#toast-container > .toast-warning {
  --toast-color: #FADDAF;
  color: var(--toast-color) !important;
  border: 2px dashed var(--toast-color);
  box-shadow: 0 0 0 2px white, 0 0 0 6px var(--toast-color), 0 0 0 8px white, 1px 1px 0 8px color-mix(in srgb, var(--toast-color) 90%, black 10%);
}
#toast-container > .toast-success .toast-close-button {
  color: var(--toast-color);
  text-shadow: none;
}

/* 蓝色 */
#toast-container > .toast-info {
  --toast-color: #A9D7E9;
  color: var(--toast-color) !important;
  border: 2px dashed var(--toast-color);
  box-shadow: 0 0 0 2px white, 0 0 0 6px var(--toast-color), 0 0 0 8px white, 1px 1px 0 8px color-mix(in srgb, var(--toast-color) 90%, black 10%);
}
#toast-container > .toast-success .toast-close-button {
  color: var(--toast-color);
  text-shadow: none;
}

/* 滚动条 */
::-webkit-scrollbar {
  width: 8px;
  background-color: transparent;
}

::-webkit-scrollbar-thumb {
  border: none !important;
  box-shadow: inset 0 0 0 2px var(--SmartThemeShadowColor) !important;
  background-color: var(--SmartThemeUnderlineColor) !important;
}

#HotSwapWrapper::-webkit-scrollbar, #tableContainer>div::-webkit-scrollbar, .history-tabs::-webkit-scrollbar {
  display: none;
}



/* Font Opacity */
small, input.neo-range-slider[type="range"] {
  opacity: 1 !important;
  color: var(--SmartThemeQuoteColor);
}

hr {
  border: 1px dashed var(--SmartThemeShadowColor);
  opacity: 1;
}

.fillLeft .scrollableInner {
  padding: 0;
}

.last_mes .mes_block {
  transform: none !important;
}

.menu_button small, .title-item-active {
  color: var(--SmartThemeUnderlineColor) !important;
}

