/* --- CSS è‡ªå®šä¹‰å˜é‡ --- */
:root {
    /* ä¸»é¢˜é¢œè‰² */
    --theme-color-1: #68C4B7; /* ä¸»é¢˜è‰²1 */
    --theme-color-2: #A0D9D0; /* ä¸»é¢˜è‰²2 */
    --theme-color-accent: #F0AFAF; /* å¼ºè°ƒè‰² */

    /* UI é¢œè‰² */
    --ui-color-main: #F5FAF9;     /* UIä¸»è‰² */
    --ui-color-sec: #E8F5F3;      /* UIè¾…è‰² */
    --ui-color-hover: #DDF0EE;    /* UIæ‚¬åœè‰² */

    /* æ–‡æœ¬é¢œè‰² */
    --text-color-main: #3D4F4C; /* ä¸»è¦æ–‡æœ¬é¢œè‰² */
    --text-color-sec: #798C88;  /* æ¬¡è¦æ–‡æœ¬é¢œè‰² */
    --text-color-accent-fg: var(--theme-color-1); /* å¼ºè°ƒæ–‡æœ¬é¢œè‰² */

    /* ç‰¹å®šåŒºåŸŸé¢œè‰² */
    --top-bar-color: var(--ui-color-main); /* é¡¶éƒ¨æ èƒŒæ™¯ */
    --top-bar-underline-color: var(--theme-color-1); /* é¡¶éƒ¨æ ä¸‹åˆ’çº¿ */
    --top-bar-icon-color: var(--text-color-main); /* é¡¶éƒ¨æ å›¾æ ‡ */
    --code-color: #D1E7E4; /* ä»£ç å—èƒŒæ™¯ */
    --header-bg-color: color-mix(in srgb, var(--theme-color-1) 12%, transparent); /* æ ‡é¢˜èƒŒæ™¯ */

    /* æ¸å˜è‰² */
    --gradient-main: linear-gradient(135deg, var(--theme-color-1) 25%, var(--theme-color-2) 75%); /* ä¸»è¦æ¸å˜ */
    --gradient-accent: linear-gradient(135deg, var(--theme-color-accent) 25%, color-mix(in srgb, var(--theme-color-accent) 70%, #ffffff 30%) 75%); /* å¼ºè°ƒæ¸å˜ */

    /* SmartTheme ç›¸å…³é¢œè‰²å˜é‡  */
    --SmartThemeQuoteColor: var(--theme-color-1);
    --SmartThemeUnderlineColor: var(--theme-color-2);
    --SmartThemeBlurTintColor: var(--ui-color-main);
    --SmartThemeBorderColor: var(--top-bar-color);
    --SmartThemeChatTintColor: var(--chat-background-color);
    --SmartThemeBodyColor: var(--text-color-main);
    --SmartThemeEmColor: var(--text-color-sec);
    --SmartThemeShadowColor: var(--code-color);

    /* å¸ƒå±€ä¸å°ºå¯¸ */
    --mes-width: 100%; /* æ¶ˆæ¯å®½åº¦ */
    --mes-block-width: 97%; /* æ¶ˆæ¯å—å®½åº¦ */
    --box-border-radius: 10px; /* ç›’å­åœ†è§’ */
    --top-distance: 5px; /* é¡¶éƒ¨é—´è· */
    --bottom-distance: 5px; /* åº•éƒ¨é—´è· */
    --chat-send-spacing: 8px; /* èŠå¤©å‘é€åŒºé—´è· */
    --leftRight-chat-spacing: 5px; /* èŠå¤©åŒºå·¦å³é—´è· */
    --scroll-width: 6px; /* æ»šåŠ¨æ¡å®½åº¦ */
    --icon-size: var(--mainFontSize); /* å›¾æ ‡å¤§å° */
    --border-radius-small: 8px; /* å°åœ†è§’ */
    --border-radius-medium: 12px; /* ä¸­ç­‰åœ†è§’ */
    --button-border-radius: 10px; /* æŒ‰é’®åœ†è§’ */
    --header-border-radius: 6px; /* æ ‡é¢˜æ ‡ç­¾åœ†è§’ */

    /* é—´è· */
    --spacing-long: 12px; /* é•¿é—´è· */
    --spacing-short: 6px; /* çŸ­é—´è· */
    --spacing-tiny: 3px; /* å¾®å°é—´è· */

    /* å¤´åƒå°ºå¯¸ */
    --avatar-photo-size: 40px; /* å¤´åƒå›¾ç‰‡å°ºå¯¸ */
    --avatar-padding: 4px; /* å¤´åƒå†…è¾¹è· */
    --avatar-bottom-padding: 15px; /* å¤´åƒåº•éƒ¨å†…è¾¹è· (å®ä¸½æ¥æ•ˆæœ) */

    /* ä¾¿åˆ©è´´ä¸è£…é¥°å›¾ç‰‡ */
    --sticky-note-bg-color: var(--ui-color-sec); /* ä¾¿åˆ©è´´èƒŒæ™¯ */
    --sticky-note-content-top-padding: var(--spacing-short); /* ä¾¿åˆ©è´´å†…å®¹é¡¶éƒ¨å†…è¾¹è· */
    --sticky-note-content-side-padding: 15px; /* ä¾¿åˆ©è´´å†…å®¹ä¾§è¾¹å†…è¾¹è· */
    --sticky-note-content-initial-bottom-padding: 10px; /* ä¾¿åˆ©è´´å†…å®¹åˆå§‹åº•éƒ¨å†…è¾¹è· */

    --torn-paper-edge-image: url('https://files.catbox.moe/siy8o0.png'); /* æ’•è¾¹å›¾ç‰‡ */
    --torn-paper-height: 20px; /* æ’•è¾¹å›¾ç‰‡é«˜åº¦ */

    --thin-sticky-note-button-image: url('https://files.catbox.moe/c2ihj1.png'); /* ç»†é•¿ä¾¿åˆ©è´´æŒ‰é’®å›¾ç‰‡ */

    /* ç”¨æˆ·å¤´åƒåç§»ä¸å°ºå¯¸ (èŠå¤©æ°”æ³¡å†…) */
    --user-avatar-offset-top: 10px; /* ç”¨æˆ·å¤´åƒé¡¶éƒ¨åç§» */
    --user-avatar-offset-right: 10px; /* ç”¨æˆ·å¤´åƒå³ä¾§åç§» */
    --user-avatar-polaroid-width: calc(var(--avatar-photo-size) + (var(--avatar-padding) * 2) + 5px); /* ç”¨æˆ·å¤´åƒå®ä¸½æ¥æ¡†å®½åº¦ */
    --user-avatar-total-width-allowance: calc(var(--user-avatar-polaroid-width) + var(--user-avatar-offset-right) + var(--sticky-note-content-side-padding)); /* ç”¨æˆ·å¤´åƒå ç”¨æ€»å®½åº¦ */
}

/* --- é€šç”¨å…ƒç´ æ ·å¼ --- */

/* é€šç”¨å¤´åƒ (éèŠå¤©ç•Œé¢) */
.avatar img,
body.big-avatars .avatar img {
    box-shadow: 0 0 0 2px var(--ui-color-main), 0 0 0 3px var(--theme-color-2); /* å¤´åƒå¤–å‘å…‰ */
    border: none;
    border-radius: 50% !important; /* åœ†å½¢å¤´åƒ */
    width: var(--avatar-photo-size) !important;
    height: var(--avatar-photo-size) !important;
    object-fit: cover !important;
}
/* å³ä¾§å¯¼èˆªè§’è‰²é€‰æ‹©å¤´åƒå¼ºåˆ¶åœ†å½¢ */
#right-nav-panel .character_select .avatar img {
    border-radius: 50% !important;
}

/* å°è¯•è°ƒæ•´è§’è‰²é€‰æ‹©é¡¹ä¸­çš„å¤´åƒå®¹å™¨å¯¹é½ */
#right-nav-panel .character_select .avatar {
    display: flex;
