}

/* æ ‡é¢˜æ ·å¼ */
.standoutHeader strong,
#right-nav-panel h2,
.drawer-content > h3,
.drawer-content .flex-container h3:first-of-type:not(#Backgrounds .textAlignCenter),
.drawer-content .flex-container .flex-container h3:first-of-type,
.popup h3:not(dialog *):not(#char_info_popup_header):not(.swal2-title),
.margin0.title_restorable.standoutHeader {
    position: relative;
    font-size: calc(var(--mainFontSize) * 1.2);
    color: var(--text-color-main);
    letter-spacing: 0.05em;
    white-space: nowrap;
    line-height: 1.5;
    font-weight: 600;
    background-color: var(--header-bg-color);
    background-image: none !important;
    padding: var(--spacing-tiny) var(--spacing-short) var(--spacing-tiny) calc(var(--mainFontSize) * 1.5 + var(--spacing-tiny));
