.popup h3:not(dialog *):not(#char_info_popup_header):not(.swal2-title)::before,
.margin0.title_restorable.standoutHeader::before {
    content: "âœ¿";
    position: absolute;
    left: var(--spacing-tiny);
    top: 50%;
    transform: translateY(-50%);
    color: var(--theme-color-1);
    font-size: calc(var(--mainFontSize) * 1.1);
    line-height: 1;
    text-shadow: 1px 1px 2px color-mix(in srgb, var(--theme-color-2) 30%, transparent);
}
/* ç‰¹å®šåŒºåŸŸæ ‡é¢˜è°ƒæ•´ */
.standoutHeader:not(h4),
#rm_PinAndTabs,
#top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p,
