}

/* è¾“å…¥æ¡†æ ·å¼ (æ–‡æœ¬ã€æ•°å­—ã€å¯†ç ã€é€šç”¨æ–‡æœ¬åŸŸ) */
input[type="text"],
input[type="number"],
input[type="password"],
input:not([type]),
